datos.CSS:
/*  --------------------------------------------------------------------------------
	VERSION: 26.0
	AUTOR: VELNEO
	ULTIMA MODIFICACION: 01 Octubre 2019

	INDICE
		- QCalendarWidget       CALENDARIO
		- QCheckBox             BOTON CHECK
		- QComboBox             COMBOBOX
		- QDateEdit             CAJA DE EDICIÓN DE CAMPO FECHA MODO DE EDICIÓN FECHA
		- QDateTime             CAJA DE VISUALIZACIÓN CAMPO FECHA Y HORA
		- QDateTimeEdit         CAJA DE EDICIÓN DE CAMPO FECHA Y HORA
		- QDialog               VENTANA EN CUADRO DE DIÁLOGO
		- QDockWidget           DOCK
		- QDoubleSpinBox        CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
		- QFrame                MARCO
		- QGroupBox             CAJA DE GRUPO
		- QHeaderView           CABECERA DE REJILLAS Y ÁRBOLES
		- QLabel                ETIQUETA DE TEXTO
		- QLineEdit             CAJA DE EDICIÓN DE TEXTO EN UNA LINEA
		- QMainWindow           VENTANA PRINCIPAL
		- QMenu                 MENU CONTEXTUAL
		- QMenuBar              BARRA DE MENU (Solo afecta a Windows)
		- QMessageBox           VENTANA DE MENSAJE
		- QNumberSpinBox        CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
		- QProgressBar          BARRA DE PROGRESO
		- QPushButton           BOTÓN
		- QRadioButton          BOTÓN DE RADIO
		- QScrollBar            BARRA DE SCROLL VERTICAL Y HORIZONTAL
		- QSlider               DESLIZADOR
		- QSpinBox              CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON UN BOTÓN
		- QSplitter             SPLITTER
		- QStatusBar            BARRA DE ESTADO
		- QTabWidget            SEPARADOR DE FORMULARIOS (PESTAÑAS)
		- QTableView            REJILLA
		- QTextEdit             CAJA DE EDICIÓN DE TEXTO MULTI-LINEA
		- QTimeEdit             CAJA DE EDICIÓN DE HORA
		- QTitanGrid            REJILLA AVANZADA
		- QToolBar              BARRA DE HERRAMIENTAS
		- QTooltip              TOOLTIP
		- QTreeView             QTREEVIEW - ARBOL VISOR Y MENU ARBOLADO
		- QWidget#qt_calendar   CALENDARIO
		- VBoundFieldEdit       CAJA DE EDICIÓN DE CAMPO PUNTERO A MAESTRO
		- VCFootView            PIE DE REJILLA
                      - VCOverlayShadow       OVERLAY (QMESSAGEBOX, QPROGRESSMODALDIALOG)
                      - VCOverlayShadowGui    OVERLAY (QDIALOG, QFILEDIALOG Y QPRINTDIALOG)
		- VCPVControlCasillero  CASILLERO
		- VDateLineEdit         CAJA DE EDICIÓN DE CAMPO FECHA MODO DE EDICIÓN ALFANUMÉRICO
		- VListBox              LISTBOX

	-------------------------------------------------------------------------------- */

/*  --------------------------------------------------------------------------------
	QCheckBox - BOTÓN CHECK
	QRadioButton - BOTÓN DE RADIO
	-------------------------------------------------------------------------------- */

QCheckBox, QRadioButton {
	color: $color-texto-edicion; }

QCheckBox:hover, QRadioButton:hover {
	color: $color-texto-hover; }

QCheckBox:focus, QRadioButton:focus {
	color: $color-texto-hover; }

QCheckBox:disabled, QRadioButton:disabled {
	color: $color-texto-disabled; }

/*  --------------------------------------------------------------------------------
	QComboBox - COMBOBOX
	QDateEdit - CAJA DE EDICIÓN DE CAMPO FECHA MODO DE EDICIÓN FECHA
	QDateTime - CAJA DE VISUALIZACIÓN CAMPO FECHA Y HORA
	QDateTimeEdit - CAJA DE EDICIÓN DE CAMPO FECHA Y HORA
	QDoubleSpinBox - CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
	QNumberSpinBox - CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
	QSpinBox - CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON UN BOTÓN
	QTimeEdit - CAJA DE EDICIÓN DE HORA
	VBoundFieldEdit - CAJA DE EDICION DE CAMPO PUNTERO A MAESTRO
	VDateLineEdit - CAJA DE EDICIÓN DE CAMPO FECHA MODO DE EDICIÓN ALFANUMÉRICO
	VListBox - LISTBOX
	-------------------------------------------------------------------------------- */

QComboBox, QDateEdit, VDateLineEdit, QDateTime, QDateTimeEdit, QDoubleSpinBox, QNumberSpinBox, QSpinBox, QTimeEdit, VBoundFieldEdit, vBoundFieldEditBrowser, VDateLineEdit,VListBox {
	background-color: $color-fondo-edicion;
	border: 0px solid $color-borde-normal;
	border-radius: $border-radius ;
	color: $color-texto-edicion;
	height: 30px;
	padding-left: 5px;
	padding-right: 0px;
	selection-background-color: $color-fondo-texto-seleccionado;
	selection-color: $color-texto-seleccionado; }

QComboBox:hover, QDateEdit:hover, VDateLineEdit:hover, QDateTime:hover, QDateTimeEdit:hover, QDoubleSpinBox:hover, QNumberSpinBox:hover, QSpinBox:hover, QTimeEdit:hover, VBoundFieldEditBrowser:hover,VBoundFieldEdit:hover, VListBox:hover {
	background-color: $color-fondo-texto-hover;
	border: 0px solid $color-borde-hover; }

QComboBox:focus, QDateEdit:focus, VDateLineEdit:focus, QDateTime:focus, QDateTimeEdit:focus, QDoubleSpinBox:focus, QNumberSpinBox:focus, QSpinBox:focus, QTimeEdit:focus, VBoundFieldEditBrowser:focus, VBoundFieldEdit:focus, VListBox:focus {
	background-color: $color-fondo-texto-focus;
	border: none; }

QComboBox:disabled, QDateEdit:disabled, VDateLineEdit:disabled, QDateTime:disabled, QDateTimeEdit:disabled, QDoubleSpinBox:disabled, QNumberSpinBox:disabled, QSpinBox:disabled, QTimeEdit:disabled, VBoundFieldEditBrowser:disabled,VBoundFieldEdit:disabled, VListBox:disabled {
	background-color: $color-fondo-disabled;
	color: $color-texto-disabled; }

QDateEdit::up-button, VDateLineEdit::up-button, QDateTime::up-button, QDateTimeEdit::up-button, QTimeEdit::up-button, VBoundFieldEditBrowser::up-button, VBoundFieldEdit::up-button {
	border: none;
	image: url(":/Resources/tg_moverarriba.png"); }

QDateEdit::down-button, VDateLineEdit::down-button, QDateTime::down-button, QDateTimeEdit::down-button, QTimeEdit::down-button, VBoundFieldEditBrowser::down-button, VBoundFieldEdit::down-button {
	border: none;
	image: url(":/Resources/tg_moverabajo.png"); }

QComboBox::drop-down, QDateEdit::drop-down, VDateLineEdit::drop-down, QDateTime::drop-down, QDateTimeEdit::drop-down, VBoundFieldEditBrowser::drop-down, VBoundFieldEdit::drop-down, QTimeEdit::drop-down {
	border: none;
	image: url(":/Resources/tg_moverabajo.png");
	width: 13px; }

/* EN PRINCIPIO NO AFECTAN */

QNumberSpinBox:up-button, QDoubleSpinBox:up-button, QSpinBox:up-button {
	border: none;
	image: url(":/Resources/tg_moverarriba.png"); }

QNumberSpinBox:down-button, QDoubleSpinBox:down-button, QSpinBox:down-button {
	border: none;
	image: url(":/Resources/tg_moverabajo.png"); }

VBoundFieldEditBrowser QToolButton {
	background-color: $color-fondo-boton-toolbar; }

VBoundFieldEditBrowser QToolButton:hover {
	background-color: $color-fondo-boton-toolbar-hover; }

/*  --------------------------------------------------------------------------------
	QDockWidget - DOCK
	-------------------------------------------------------------------------------- */

QDockWidget {
	color: transparent;
	font-size: 10px;
	min-width:10px;
	width: 210px; }

QDockWidget::title {
	background: $color-fondo-general;
	color: $color-texto-estatico;
	margin: 0px;
	padding: 0px;
	text-align: center; }

QDockWidget::close-button {
	image: none;
	position: absolute;
	subcontrol-origin: margin;
	subcontrol-position: top right;
	top: 0px; left: 0px; bottom: 0px;
	width: 0px; }

QDockWidget::float-button {
	image: none;
	position: absolute;
	subcontrol-origin: margin;
	subcontrol-position: top right;
	top: 0px; left: 0px; bottom: 0px;
	width: 0px; }

/*  --------------------------------------------------------------------------------
	QFrame - MARCO
	-------------------------------------------------------------------------------- */

QFrame {
	background: $color-fondo-general;
	border: 0px; }

/*  --------------------------------------------------------------------------------
	QGroupBox - CAJA DE GRUPO
	-------------------------------------------------------------------------------- */

QGroupBox {
	background: $color-fondo-caja-grupo;
	border: 0px solid $color-borde-normal;
	border-radius: $border-radius;
	font-size: 12px;
	font-weight: bold;
	margin-top: 20px;
	padding: 5px; }

QGroupBox::title {
	color: $color-texto-edicion;
	padding-top: -5px;
	padding-left: 5px;
	subcontrol-origin: margin; }

/*  --------------------------------------------------------------------------------
	QHeaderView - CABECERA DE REJILLAS Y ARBOLES
	-------------------------------------------------------------------------------- */

QHeaderView::section:horizontal {
	background: $color-fondo-general;
	border: none;
	border-bottom: 1px solid $color-borde-linea;
	border-right: 5px solid transparent;
	color: $color-texto-rejilla-cabecera;
	font-weight: bold;
	margin-top: 10px;
	min-height: 20px; }

/*  --------------------------------------------------------------------------------
	QLabel - ETIQUETA DE TEXTO
	-------------------------------------------------------------------------------- */

QLabel {
	background-color: transparent;
	color: $color-texto-estatico;
	font-size: 11px; }

/* TITULOS DE LOS FORMULARIOS Y MENUS */

QLabel#TXT_TIT {
	color: $color-texto-titulo;
	font-size: 24px; }

QLabel#EMP {
	font-size: 16px; }

QLabel#USR {
	font-size: 12px; }

/* MENSAJE ERROR */

QLabel#NAME_VAC, QLabel#EML_ERR {
	alignement: right;
	background: transparent;
	color: #E27249;
	font-size: 10px;
	text-transform: uppercase;
	padding-left: -1px;
	padding-top: 1px; }

/* MENSAJE DUPLICADO*/

QLabel#NAME_DUP, QLabel#EML_DUP {
	alignement: right;
	background: transparent;
	color: #6388C1;
	font-size: 10px;
	padding-top: 1px;
	text-transform: uppercase; }

/*  --------------------------------------------------------------------------------
	QLineEdit - CAJA DE EDICIÓN DE TEXTO EN UNA LINEA
	QTextEdit - CAJA DE EDICIÓN DE TEXTO MULTI-LINEA
	-------------------------------------------------------------------------------- */

QLineEdit, QTextEdit {
	background-color: $color-fondo-edicion;
	border: 0px solid $color-borde-normal;
	border-radius: $border-radius;
	color: $color-texto-edicion;
	height: 30px;
	padding-left: 2px;
	selection-background-color: $color-fondo-texto-seleccionado;
	selection-color: $color-texto-seleccionado; }

QLineEdit:hover, QTextEdit:hover {
	background-color: $color-fondo-texto-hover;
	border: 0px solid $color-borde-hover; }

QLineEdit:focus, QTextEdit:focus {
	background-color: $color-fondo-texto-focus;
	border: 0px solid $color-borde-focus; }

QLineEdit:disabled, QTextEdit:disabled {
	background-color: $color-fondo-disabled;
	color: $color-texto-disabled; }

/* CONTROL DE EDICIÓN DE TEXTOS A BUSCAR EN MENUS */

QLineEdit#TXT_BUS {
		qproperty-placeholderText: 'Texto a buscar'; }

/* FORMULARIO ALTA CONTACTO > TEXTO EDIT NOMBRE Y NOMBRE COMERCIAL */

QLineEdit#NAME_CON  {
		qproperty-placeholderText: 'Nombre contacto';
		qproperty-text: 'Nombre contacto'; }

QLineEdit#NOM_COM {
		margin-left: 10px; }

/*  --------------------------------------------------------------------------------
	QMainWindow - VENTANA PRINCIPAL
	-------------------------------------------------------------------------------- */

QMainWindow {
	background: $color-fondo-general;
	border: 0px; }

QMainWindow::separator {
	background: #FFF;
	border-left: 1px solid #CECECE;
	border-right: 1px solid #CECECE;
	height: 5px;
	width: 5px; }

QMainWindow::separator:hover {
	background: #CECECE; }

/*  --------------------------------------------------------------------------------
	QMenu - MENU CONTEXTUAL
	-------------------------------------------------------------------------------- */

QMenu {
		background-color: #FFF;
		border: 1px solid #CECECE;
		color: #333333;
		margin: 0px;
		padding: 5px; }

QMenu::item {
		border: 1px solid transparent;
		margin: 1px;
		padding: 5px 30px 5px 20px; }

QMenu::item:selected {
		background-color: #CECECE;
		color:#000; }

QMenu::separator {
		background-color: #CECECE;
		height: 1px;
		margin: 0px; }

/*  --------------------------------------------------------------------------------
	QMenuBar - BARRA DE MENU (Solo afecta a Windows)
	-------------------------------------------------------------------------------- */

QMenuBar {
		background-color: #FFF;
		border-bottom: 1px solid #CECECE;
		min-height:20px; }

QMenuBar::item:selected {
		background-color: #CECECE;
		color: #000; }

QMenuBar::item:hover {
		background: #333333;
		color: #FFF; }

/*  --------------------------------------------------------------------------------
	QMessageBox - VENTANA DE MENSAJE
	QDialog - VENTANA EN CUADRO DE DIÁLOGO
	-------------------------------------------------------------------------------- */

QMessageBox, QDialog {
		background-color: #FFF; }

QMessageBox QPushButton {
		min-width: 90px; }

/*  --------------------------------------------------------------------------------
	QProgressBar - BARRA DE PROGRESO
	-------------------------------------------------------------------------------- */

QProgressBar {
		background-color: #FFF;
		border: 1px solid #E0E0E0;
		border-radius: 2px;
		color: $color-texto-edicion;
		height: 30px;
		text-align: center; }

QProgressBar::chunk {
		background-color: #C5CAE9; }

/*  --------------------------------------------------------------------------------
	QPushButton - BOTÓN
	-------------------------------------------------------------------------------- */

QPushButton {
	background-color: $color-fondo-boton-normal;
	border: 0px solid $color-borde-normal;
	border-radius: $border-radius ;
	color: $color-texto-boton-normal;
	font-size: 12px;
	font-weight: bold;
	height: 30px;
	line-height: 16px;
	text-align: center;
	text-transform: $boton-text-transform;
	width: $ancho-boton;
	qproperty-iconSize: 18px; }

QPushButton:hover {
	background-color: $color-fondo-boton-resaltado-hover;
	border: 0px solid $color-borde-hover;
	color: $color-texto-boton-resaltado-hover; }

QPushButton:focus {
	background-color: $color-fondo-boton-resaltado-focus;
	border: 0px solid $color-borde-focus;
	color: $color-texto-boton-resaltado-focus; }

QPushButton:pressed {
	background-color: $color-fondo-boton-normal;
	border: 0px solid $color-borde-focus;
	color: $color-texto-boton-normal; }

QPushButton:disabled {
	background-color: $color-fondo-boton-disabled;
	border: 0px solid $color-borde-disabled;
	color: $color-texto-boton-disabled; }

QPushButton::menu-indicator {
	image: none;
	width: 0px; }

/* BOTÓN ACEPTAR */

QPushButton#BTN_ACE {
	background-color: $color-fondo-boton-resaltado-normal;
	color: $color-texto-boton-resaltado-normal; }

QPushButton:hover#BTN_ACE {
	background-color: $color-fondo-boton-resaltado-hover;
	color: $color-texto-boton-resaltado-hover; }

QPushButton:focus#BTN_ACE {
	background-color: $color-fondo-boton-resaltado-focus;
	color: $color-texto-boton-resaltado-focus; }

QPushButton:pressed#BTN_ACE {
	background-color: $color-fondo-boton-resaltado-focus;
	color: $color-texto-boton-resaltado-focus; }

QPushButton:disabled#BTN_ACE {
	background-color: $color-fondo-boton-resaltado-disabled;
	border: 0px solid $color-borde-boton-resaltado-disabled;
	color: $color-texto-boton-resaltado-disabled; }

/* BOTÓN AMPLIAR, BOTÓN BUSCAR, BOTÓN MENU y BOTÓN REDUCIR */

QPushButton#BTN_AMP, QPushButton#BTN_AYU, QPushButton#BTN_BUS, QPushButton#BTN_MEN, QPushButton#BTN_RED {
	border-radius: 0px;
	qproperty-iconSize: 18px; }

/*  --------------------------------------------------------------------------------
	QScrollBar - BARRA DE SCROLL VERTICAL Y HORIZONTAL
	-------------------------------------------------------------------------------- */

QScrollBar:vertical {
	background: $color-fondo-barra-scroll-normal;
	border-radius: 6px;
	padding-left: 2px;
	padding-right: 2px;
	width: 12px; }

QScrollBar:horizontal {
	background: $color-fondo-barra-scroll-normal;
	border-radius: 6px;
	padding-bottom: 2px;
	padding-top: 2px;
	height: 12px;  }

QScrollBar:vertical:hover {
	background: $color-fondo-barra-scroll-hover; }

QScrollBar:horizontal:hover {
	background: $color-fondo-barra-scroll-hover; }

QScrollBar::handle:vertical {
	background: $color-barra-scroll-normal;
	border: 2px solid transparent;
	border-radius: 4px;
	min-height: 90px; }

QScrollBar::handle:horizontal {
	background: $color-barra-scroll-normal;
	border: 2px solid transparent;
	border-radius: 4px;
	min-width: 90px; }

QScrollBar::handle:hover {
	background: $color-barra-scroll-hover;
	border: 2px solid transparent; }

QScrollBar::add-line:vertical {
	background: none;
	border: none; }

QScrollBar::sub-line:vertical {
	background: none;
	border: none; }

QScrollBar::add-line:horizontal {
	background: none;
	border: none; }

QScrollBar::sub-line:horizontal {
	background: none;
	border: none; }

/*  --------------------------------------------------------------------------------
	QScrollBar - Recuadro de intersección entre las scrollbar horizontal y vertical
	-------------------------------------------------------------------------------- */

QAbstractScrollArea::corner {
	background: transparent;
	border: transparent; }

/*  --------------------------------------------------------------------------------
	QSlider - DESLIZADOR
	-------------------------------------------------------------------------------- */

QSlider {
	background: transparent; }

/*  --------------------------------------------------------------------------------
	QSplitter - SPLITTER
	-------------------------------------------------------------------------------- */

QSplitter {
	background: transparent; }

/*  --------------------------------------------------------------------------------
	QStatusBar - BARRA DE ESTADO
	-------------------------------------------------------------------------------- */

QStatusBar {
	background-color: $color-fondo-general;
	border-top: 1px solid $color-borde-normal;
	qproperty-visible: false; }

/*  --------------------------------------------------------------------------------
	QTabWidget - SEPARADOR DE FORMULARIOS (Pestañas)
	-------------------------------------------------------------------------------- */

QTabWidget::pane {
	border-top: 1px solid $color-borde-linea;
	margin:0px;
	top: 10px; }

QTabWidget::tab-bar {
	left: 10px;
	min-width: 10px;
	right: 10px;
	top: 10px; }

QTabBar::tab {
	background-color: $color-fondo-general;
	color: $color-texto-estatico;
	font-weight: normal;
	margin-left: 5px;
	margin-right: 10px;
	padding-bottom: 3px;
	padding-left: 5px;
	padding-right: 5px;
	padding-top: 5px; }

QTabBar::tab:hover {
	border-top: 1px solid $color-fondo-focus; }

QTabBar::tab:focus {
	background-color: $color-fondo-boton-resaltado-focus;
	color: $color-texto-boton-resaltado-focus; }

QTabBar::tab:selected {
	border-bottom: 2px solid $color-borde-focus; }

QTabBar::close-button {
	image: url(":/Resources/td_borrartodospuntos.png");
	width: 32px;
	height: 32px;
	margin: 3px; }

QTabBar::scroller {
	padding-top: 10px;
	width: 64px;}

QTabBar QToolButton::left-arrow {
	image: url(":/Resources/tg_buscar_ant.png");
	width: 32px;}

QTabBar QToolButton::right-arrow {
	image: url(":/Resources/tg_buscar_sig.png");
	width: 32px;}

/*  --------------------------------------------------------------------------------
	QTableView - REJILLA
	VCFootView - PIE DE REJILLA
	-------------------------------------------------------------------------------- */

QTableView {
	alternate-background-color: transparent;
	background-color: white;
	border: none;
	border-bottom: 1px solid #BDBDBD;
	border-radius: $border-radius;
	color: $color-texto-rejilla-detalle;
	font-weight: normal;
	gridline-color: transparent;
	min-height: 30px;
	selection-background-color: $color-fondo-focus;
	selection-color: $color-texto-rejilla-seleccionado;
	qproperty-showGrid: false; }

QTableView QTableCornerButton::section {
	background-color: transparent;
	border: 0px; }

QTableView::item:hover {
	background-color: $color-fondo-focus;
	color: $color-texto-seleccionado; }

QTableView::item:focus {
	min-height: 30px;
	selection-background-color: $color-fondo-focus;
	selection-color: $color-texto-rejilla-seleccionado; }

/* PIE DE REJILLA */

VCFootView {
	background: transparent;
	border: none;
	border-right: 5px solid transparent;
	color: $color-texto-edicion;
	min-height: 30px; }

VCFootView::section:active {
	background: transparent;
	border: none;
	border-right: 5px solid transparent; }

VCFootView::section:!active {
	background: transparent;
	border: none;
	border-right: 5px solid transparent; }

/*  --------------------------------------------------------------------------------
	QTitanGrid - REJILLA AVANZADA
	-------------------------------------------------------------------------------- */

/* MARCO DE LA REJILLA */

Qtitan--Grid {            
	padding: 1px;
	margin: 1px;
	border: 1px solid $color-texto-seleccionado;  
	border-style: none;
	gridline-color: $color-fondo-texto;

/* COLOR DE FONDO Y FONDO PAR DE FILAS */
	background-color: $color-fondo-general;                        /* Fondo de la rejilla */
	alternate-background-color: $color-fondo-general;              /* Color de las líneas impares */

/* COLOR DE LA FILA SELECCIONADA */
	selection-color: $color-texto-rejilla-detalle;                /* Color del texto del registro seleccionado */
	selection-background-color: $color-fondo-texto;                /* Background del registro seleccionado */

/* AERA DE LOS AGRUPAMIENTOS */
	qproperty-headerHeight: 30;                                    /* Altura de la cabecera de agrupación */  
	qproperty-headerTextColor: $color-texto-rejilla-detalle;       /* Texto informativo de la cabecera de agrupamiento */
	qproperty-headerBrush: $color-fondo-general;                   /* Color de la franja de agrupamiento */
	qproperty-headerBorderColor: $color-fondo-general     ;        /* Color del borde */
	qproperty-headerBorderSize: 0;                                 /* Tamaño del borde */
	qproperty-headerBorderStyle: dot-dash;
	/* La fuente se puede establecer de dos formas. Con headerTextFont que requiere un formato concreto
	o indicando la familia y el tamaño en dos propiedades*/
	/*qproperty-headerTextFont: "Verdana,-1,20,5,0,0,0,0,0,0";*/ 
	qproperty-headerTextFontSize: 12;                              /* Tamaño de la fuente */
	qproperty-headerTextFontFamily: "Verdana";

/* AGRUPAMIENTOS */
	qproperty-groupColorLevel0: $color-texto-rejilla-agrupamiento; /* Color de fondo según el nivel de agrupamiento */
	qproperty-groupColorLevel1: $color-texto-seleccionado;
	qproperty-groupColorLevel2: $color-fondo-texto;
	qproperty-groupColorLevel3: $color-borde-linea;
	qproperty-groupColorLevel4: magenta; 
	qproperty-groupColorLevel5: white; 
	qproperty-groupColorLevel6: white; 
	qproperty-groupColorLevel7: white;

/* NIVEL DE DESPLIEGUE DE AGRUPAMIENTOS */
	qproperty-keepGroupLevelExpanded: 3;

/* PIES DE GRUPO */
	qproperty-summaryGroupItemTextColor: red;                      /* No está operativo en la versión 26 */
	qproperty-summaryGroupItemBackgroundColor: $color-fondo-general;
	qproperty-summaryGroupItemHeight: 30;
	qproperty-summaryGroupItemBorderColor: $color-borde-linea;
	qproperty-summaryGroupItemBorderSize: 1;
	qproperty-summaryGroupItemBorderStyle: native;

/* PIES DE REJILLA */
	qproperty-summaryItemTextColor: blue;                          /* No está operativo en la versión 26 */
	qproperty-summaryItemBackgroundColor: $color-fondo-general;
	qproperty-summaryItemHeight: 30;
	qproperty-summaryItemBorderColor: $color-texto-seleccionado;
	qproperty-summaryItemBorderSize: 1;
	qproperty-summaryItemBorderStyle: native;

/* ICONOS */
	qproperty-sortAscendingIcon: url(":/Resources/tg_moverarriba.png");
	qproperty-sortDescendingIcon: url(":/Resources/tg_moverabajo.png");
	qproperty-focusRowIndicatorIcon: url(":/Resources/tg_buscar_sig.png");
	qproperty-columnFilterToolIcon: url(":/Resources/mo_tuboficha.png");
	qproperty-columnFilterToolBoldIcon: url(":/Resources/mo_tubolista.png");
