QTableView {
    alternate-background-color: transparent;
    background-color: white;
    border: none;
    border-bottom: 1px solid #BDBDBD;
    border-radius: $border-radius;
    color: $color-texto-rejilla-detalle;
    font-weight: normal;
    gridline-color: transparent;
    min-height: 30px;
    selection-background-color: $color-fondo-texto;
    selection-color: $color-texto-seleccionado;
    qproperty-showGrid: false; }

QTableView QTableCornerButton::section {
    background-color: transparent;
    border: 0px; }

QTableView::item:focus {
    min-height: 30px;
    selection-background-color: $color-fondo-texto;
    selection-color: $color-texto-seleccionado; }

/* PIE DE REJILLA */

VCFootView {
    background: transparent;
    border: none;
    border-right: 5px solid transparent;
    color: $color-texto-edicion;
    min-height: 30px; }

VCFootView::section:active {
    background: transparent;
    border: none;
    border-right: 5px solid transparent; }

VCFootView::section:!active {
    background: transparent;
    border: none;
    border-right: 5px solid transparent; }

/*  --------------------------------------------------------------------------------
    QTitanGrid - REJILLA AVANZADA
    -------------------------------------------------------------------------------- */

/* MARCO DE LA REJILLA */

Qtitan--Grid {            
    padding: 1px;
    margin: 1px;
    border: 1px solid $color-texto-seleccionado;  
    border-style: none;
    gridline-color: $color-fondo-texto;

/* COLOR DE FONDO Y FONDO PAR DE FILAS */
    background-color: $color-fondo-general;                        /* Fondo de la rejilla */
    alternate-background-color: $color-fondo-general;              /* Color de las líneas impares */

/* COLOR DE LA FILA SELECCIONADA */
    selection-color: $color-texto-rejilla-detalle;                /* Color del texto del registro seleccionado */
    selection-background-color: $color-fondo-texto;                /* Background del registro seleccionado */

/* AERA DE LOS AGRUPAMIENTOS */
    qproperty-headerHeight: 30;                                    /* Altura de la cabecera de agrupación */  
    qproperty-headerTextColor: $color-texto-rejilla-detalle;       /* Texto informativo de la cabecera de agrupamiento */
    qproperty-headerBrush: $color-fondo-general;                   /* Color de la franja de agrupamiento */
    qproperty-headerBorderColor: $color-fondo-general     ;        /* Color del borde */
    qproperty-headerBorderSize: 0;                                 /* Tamaño del borde */
    qproperty-headerBorderStyle: dot-dash;
    /* La fuente se puede establecer de dos formas. Con headerTextFont que requiere un formato concreto
    o indicando la familia y el tamaño en dos propiedades*/
    /*qproperty-headerTextFont: "Verdana,-1,20,5,0,0,0,0,0,0";*/ 
    qproperty-headerTextFontSize: 12;                              /* Tamaño de la fuente */
    qproperty-headerTextFontFamily: "Verdana";

/* AGRUPAMIENTOS */
    qproperty-groupColorLevel0: $color-texto-rejilla-agrupamiento; /* Color de fondo según el nivel de agrupamiento */
    qproperty-groupColorLevel1: $color-texto-seleccionado;
    qproperty-groupColorLevel2: $color-fondo-texto;
    qproperty-groupColorLevel3: $color-borde-linea;
    qproperty-groupColorLevel4: magenta; 
    qproperty-groupColorLevel5: white; 
    qproperty-groupColorLevel6: white; 
    qproperty-groupColorLevel7: white;

/* NIVEL DE DESPLIEGUE DE AGRUPAMIENTOS */
    qproperty-keepGroupLevelExpanded: 3;

/* PIES DE GRUPO */
    qproperty-summaryGroupItemTextColor: red;                      /* No está operativo en la versión 26 */
    qproperty-summaryGroupItemBackgroundColor: $color-fondo-general;
    qproperty-summaryGroupItemHeight: 30;
    qproperty-summaryGroupItemBorderColor: $color-borde-linea;
    qproperty-summaryGroupItemBorderSize: 1;
    qproperty-summaryGroupItemBorderStyle: native;

/* PIES DE REJILLA */
    qproperty-summaryItemTextColor: blue;                          /* No está operativo en la versión 26 */
    qproperty-summaryItemBackgroundColor: $color-fondo-general;
    qproperty-summaryItemHeight: 30;
    qproperty-summaryItemBorderColor: $color-texto-seleccionado;
    qproperty-summaryItemBorderSize: 1;
    qproperty-summaryItemBorderStyle: native;

/* ICONOS */
    qproperty-sortAscendingIcon: url($sysCacheClientPath/ARR.png);
    qproperty-sortDescendingIcon: url($sysCacheClientPath/ABA.png);
    qproperty-focusRowIndicatorIcon: url($sysCacheClientPath/DER.png);
    qproperty-columnFilterToolIcon: url($sysCacheClientPath/FLT_ON.png);
    qproperty-columnFilterToolBoldIcon: url($sysCacheClientPath/FLT_OFF.png);
    qproperty-filterCheckBoxOnIcon: url($sysCacheClientPath/ARR.png);
    qproperty-filterCheckBoxOffIcon: url($sysCacheClientPath/ARR.png);
    qproperty-editorPopupOkIcon: url($sysCacheClientPath/ARR.png);
    qproperty-editorPopupCancelIcon: url($sysCacheClientPath/CRR.png);
    qproperty-editorPopupCrossIcon: url($sysCacheClientPath/ARR.png);    

/* LÍNEA DE COLUMNAS INMOVILIZADAS */
    qproperty-fixedSeparatorWidth: 3;
    qproperty-fixedSeparatorColor: $color-texto-seleccionado;
    qproperty-fixedSeparatorStyle: dot-dash; }

/* CABECERA Y BANDA */
Qtitan--Grid::section {
    color: $color-texto-rejilla-cabecera;
    font-weight: bold;
    border: none;
    background-color: $color-fondo-general; }

/* CABECERA CON FOCO */
Qtitan--Grid::section:focus {
    background-color: $color-fondo-general;
    border: 1px solid $color-texto-seleccionado; }

/* CELDAS */
Qtitan--Grid::item {
   /*background-color: red;*/                               /*Se aplica sobre los posibles colores asignados genéricamente en la rejilla al fondo o fondo par*/ 
   }

/* CELDA CON FOCO */
Qtitan--Grid::item:focus {
    background-color: $color-fondo-texto;                   /*Se aplica sobre los posibles colores asignados genéricamente en la rejilla al fondo o fondo par*/
    border: 1px solid $color-fondo-general; }

/* MARCO DEL FOCO Y CABECERA SELECCIONADA DE AGRUPACIÓN */
Qtitan--Grid:focus {
   outline: 1px dotted $color-texto-seleccionado; }


/* FILTRO RÁPIDO */
Qtitan--Grid > Qtitan--GridColumnFilterPopup, Qtitan--Grid > Qtitan--GridColumnFilterPopup *
{
    background-color: $color-borde-transparente;
    color: $color-texto-rejilla-cabecera ;
    font: 12pt "Verdana"; }

/* SELECCIÓN DE CAMPOS */
Qtitan--Grid > Qtitan--GridColumnsQuickCustomization, Qtitan--Grid > Qtitan--GridColumnsQuickCustomization *
{
    background-color: $color-borde-transparente;
    color: $color-texto-rejilla-cabecera;
    font: 12pt "Verdana"; }

/*  --------------------------------------------------------------------------------
    QToolBar - BARRA DE HERRAMIENTAS
    -------------------------------------------------------------------------------- */

QToolBar {
    background-color: transparent;
    border: 0px;
    padding: 0px;
    spacing: 10px;
    qproperty-iconSize: 18px; }

QToolButton {
    background-color: $color-fondo-boton-toolbar;
    border: 1px solid $color-borde-normal;
    border-radius: $border-radius;
    color: $color-texto-estatico;
    margin-right: 0px;
    min-height: 18px;
    min-width: 18px;
    padding: 5px;
    qproperty-iconSize: 18px; }

QToolButton:hover {
    border: 1px solid $color-borde-hover; }

QToolButton:focus {
    border: 2px solid $color-borde-focus; }

QToolButton:pressed {
    border: 2px solid $color-borde-focus; }

QToolButton:disabled {
    background-color: $color-fondo-boton-normal;
    border: 1px solid $color-borde-disabled;
    color: $color-texto-boton-disabled; }

QToolButton::menu-indicator {
    background-color: transparent;
    color: transparent; }

/*  --------------------------------------------------------------------------------
    QTooltip - TOOLTIP
    -------------------------------------------------------------------------------- */

QToolTip {
    align: center;
    background-color: $color-fondo-texto;
    border: 1px solid $color-borde-normal;
    border-radius: $border-radius;
    color: $color-texto-edicion;
    vertical-align:center; }

/*  --------------------------------------------------------------------------------
    QTreeView - ARBOL VISOR Y MENU ARBOLADO
    -------------------------------------------------------------------------------- */

QTreeView {
    outline: 0;
    background-color: $color-fondo-general;
    selection-background-color: transparent;
    selection-color: $color-texto-seleccionado;
    show-decoration-selected: 1; }

QTreeView::item {
    border-right: 0;
    color: $color-texto-estatico;
    margin-bottom:2px;
    margin-right:10px;
    margin-top: 1px;
    padding-bottom:2px;
    padding-top:1px; }

QTreeView::item:hover {
    border-color: transparent;
    color: $color-texto-hover; }

QTreeView::item:selected {
    color: $color-texto-seleccionado; }

/*  --------------------------------------------------------------------------------
    QCalendarWidget - CALENDARIO
    QWidget#qt_calendar - CALENDARIO
    -------------------------------------------------------------------------------- */

QWidget#qt_calendar_navigationbar {
    background-color: $color-fondo-general;
    qproperty-gridVisible: false; }

QWidget#qt_calendar_nextmonth {
    background: $color-fondo-general;
    border: 1px solid $color-fondo-general;
    qproperty-icon: url($sysCacheClientPath/DER.png); }

QWidget#qt_calendar_prevmonth {
    background: $color-fondo-general;
    border: 1px solid $color-fondo-general;
    qproperty-icon: url($sysCacheClientPath/IZQ.png); }

QWidget#qt_calendar_monthbutton, QWidget#qt_calendar_yearbutton {
    background: $color-fondo-general;
    border: 1px solid $color-fondo-general; }

QCalendarWidget  {
    min-width: 300px;
    min-height: 260px;}

QCalendarWidget QSpinBox {
    width: 35px;
    font-size:12px;
    color: $color-texto-edicion;
    background-color: $color-fondo-general; }

QCalendarWidget QSpinBox::up-button {
    subcontrol-origin: border;  subcontrol-position: top right;  width:30px; }

QCalendarWidget QSpinBox::down-button {
    subcontrol-origin: border; subcontrol-position: bottom right;  width:30px; }

