    VListBox - LISTBOX
--------------------------------------------------------------------------------*/

QDateEdit,
VDateLineEdit,
QDateTime,
QDateTimeEdit,
QDoubleSpinBox,
QNumberSpinBox,
QSpinBox,
QTimeEdit,
VBoundFieldEdit,
vBoundFieldEditBrowser,
VListBox {
	background-color: $color-fondo-edicion;
	border: $size-borde solid $color-borde-normal;
	color: $color-texto-edicion;
	font-size: $alto-fuente-m;
	height: $alto-referencia;
	padding-left: $padding-combo-izquierda;
	padding-right: $padding-combo-derecha;
	selection-background-color: $color-fondo-texto-seleccionado;
	selection-color: $color-texto-seleccionado;
	width: $ancho-referencia;
}

QDateEdit:hover,
VDateLineEdit:hover,
QDateTime:hover,
QDateTimeEdit:hover,
QDoubleSpinBox:hover,
QNumberSpinBox:hover,
QSpinBox:hover,
QTimeEdit:hover,
VBoundFieldEditBrowser:hover,
VBoundFieldEdit:hover,
VListBox:hover {
	border: $size-borde solid $color-borde-hover;
}

QDateEdit:focus,
VDateLineEdit:focus,
QDateTime:focus,
QDateTimeEdit:focus,
QDoubleSpinBox:focus,
QNumberSpinBox:focus,
QSpinBox:focus,
QTimeEdit:focus,
VBoundFieldEditBrowser:focus,
VBoundFieldEdit:focus,
VListBox:focus {
	border: $size-borde-seleccionado solid $color-borde-focus;
}

QDateEdit:disabled,
VDateLineEdit:disabled,
QDateTime:disabled,
QDateTimeEdit:disabled,
QDoubleSpinBox:disabled,
QNumberSpinBox:disabled,
QSpinBox:disabled,
QTimeEdit:disabled,
VBoundFieldEditBrowser:disabled,
VBoundFieldEdit:disabled,
VListBox:disabled {
