QDateEdit::up-button,
VDateLineEdit::up-button,
QDateTime::up-button,
QDateTimeEdit::up-button,
QTimeEdit::up-button,
VBoundFieldEditBrowser::up-button,
VBoundFieldEdit::up-button {
