	image: url($sysCacheClientPath/ABA.png);
}*/

/*QHeaderView[toggleVertical=true]::section:vertical:checked {
	image: url($sysCacheClientPath/ABA.png);
}*/

QHeaderView::down-arrow {
	image: url($sysCacheClientPath/ABA.png);
}

QHeaderView::up-arrow {
	image: url($sysCacheClientPath/ARR.png);
}

QHeaderView::section:horizontal:uncheked {
	padding-right: 2px;
}

QHeaderView::section:horizontal:checked {
	padding-right: 20px;
}

/* Posible incidencia en detalle que siempre aplica 2px a izquierda y derecha del texto */

/*--------------------------------------------------------------------------------
    QLabel - ETIQUETA DE TEXTO
--------------------------------------------------------------------------------*/

QLabel {
	background-color: $color-fondo-general;
	color: $color-texto-estatico;
}

/* TITULOS DE LOS FORMULARIOS Y MENUS */

QLabel#TXT_TIT {
	color: $color-texto-edicion;
	font-size: $alto-fuente-xl;
}

QLabel#EMP {
	color: $color-texto-estatico;
	font-size: $alto-fuente-m;
	text-transform: uppercase;
}

QLabel#USR {
	color: $color-texto-estatico;
	font-size: $alto-fuente-m;
}

/*--------------------------------------------------------------------------------
    QLineEdit - CAJA DE EDICIÓN DE TEXTO EN UNA LINEA
--------------------------------------------------------------------------------*/

QLineEdit {
	background-color: $color-fondo-edicion;
	border: $size-borde solid $color-borde-normal;
	color: $color-texto-edicion;
	font-size: $alto-fuente-m;
	height: $alto-referencia;
	padding-left: $padding-qline-izquierda;
	selection-background-color: $color-fondo-texto-seleccionado;
	selection-color: $color-texto-seleccionado;
}

QLineEdit:hover {
	border: $size-borde solid $color-borde-hover;
}

QLineEdit:focus {
	border: $size-borde-seleccionado solid $color-borde-focus;
