QLineEdit#NOM_COM {
        margin-left: 10px; }

/*  --------------------------------------------------------------------------------
    QMainWindow - VENTANA PRINCIPAL
    -------------------------------------------------------------------------------- */

QMainWindow {
    background: $color-fondo-general;
    border: 0px; }

QMainWindow::separator {
    background: #FFF;
    border-left: 1px solid #CECECE;
    border-right: 1px solid #CECECE;
    height: 5px;
    width: 5px; }

QMainWindow::separator:hover {
    background: #CECECE; }

/*  --------------------------------------------------------------------------------
    QMenu - MENU CONTEXTUAL
    -------------------------------------------------------------------------------- */

QMenu {
        background-color: #FFF;
        border: 1px solid #CECECE;
        color: #333333;
        margin: 0px;
        padding: 5px; }

QMenu::item {
        border: 1px solid transparent;
        margin: 1px;
        padding: 5px 30px 5px 20px; }

QMenu::item:selected {
        background-color: #CECECE;
        color:#000; }

QMenu::separator {
        background-color: #CECECE;
        height: 1px;
        margin: 0px; }

/*  --------------------------------------------------------------------------------
    QMenuBar - BARRA DE MENU (Solo afecta a Windows)
    -------------------------------------------------------------------------------- */

QMenuBar {
        background-color: #FFF;
        border-bottom: 1px solid #CECECE;
        min-height:20px; }

QMenuBar::item:selected {
        background-color: #CECECE;
        color: #000; }

QMenuBar::item:hover {
        background: #333333;
        color: #FFF; }

/*  --------------------------------------------------------------------------------
    QMessageBox - VENTANA DE MENSAJE
    QDialog - VENTANA EN CUADRO DE DIÁLOGO
    -------------------------------------------------------------------------------- */

QMessageBox, QDialog {
        background-color: #FFF; }

QMessageBox QPushButton {
        min-width: 90px; }

/*  --------------------------------------------------------------------------------
    QProgressBar - BARRA DE PROGRESO
    -------------------------------------------------------------------------------- */

QProgressBar {
        background-color: #FFF;
        border: 1px solid #E0E0E0;
        border-radius: 2px;
        color: $color-texto-edicion;
        height: 30px;
        text-align: center; }

QProgressBar::chunk {
        background-color: #C5CAE9; }

/*  --------------------------------------------------------------------------------
    QPushButton - BOTÓN
    -------------------------------------------------------------------------------- */

QPushButton {
    background-color: $color-fondo-boton-normal;
    border: 0px solid $color-borde-normal;
    border-radius: $border-radius ;
    color: $color-texto-boton-normal;
    font-size: 12px;
    font-weight: bold;
    height: 30px;
    line-height: 16px;
    text-align: center;
    text-transform: $boton-text-transform;
    width: $ancho-boton;
    qproperty-iconSize: 18px; }

QPushButton:hover {
    background-color: $color-fondo-boton-resaltado-hover;
    border: 0px solid $color-borde-hover; }

QPushButton:focus {
    background-color: $color-fondo-boton-resaltado-focus;
    border: 0px solid $color-borde-focus;
    color: $color-texto-boton-normal; }

QPushButton:pressed {
    background-color: $color-fondo-boton-normal;
    border: 0px solid $color-borde-focus;
    color: $color-texto-boton-normal; }

QPushButton:disabled {
    background-color: $color-fondo-boton-disabled;
    border: 1px solid $color-texto-boton-disabled;
    color: $color-texto-boton-disabled; }

QPushButton::menu-indicator {
    image: none;
    width: 0px; }

/* BOTÓN ACEPTAR */

QPushButton#BTN_ACE {
    background-color: $color-fondo-boton-resaltado-normal;
    color: $color-texto-boton-resaltado-normal; }

QPushButton:hover#BTN_ACE {
    background-color: $color-fondo-boton-resaltado-hover;
    color: $color-texto-boton-resaltado-hover; }

QPushButton:focus#BTN_ACE {
    background-color: $color-fondo-boton-resaltado-focus;
    color: $color-texto-boton-resaltado-focus; }

QPushButton:pressed#BTN_ACE {
    background-color: $color-fondo-boton-resaltado-focus;
    color: $color-texto-boton-resaltado-focus; }

QPushButton:disabled#BTN_ACE {
    background-color: $color-fondo-boton-resaltado-disabled;
    border: 1px solid $color-borde-boton-resaltado-disabled;
    color: $color-texto-boton-resaltado-disabled; }

/* BOTÓN AMPLIAR, BOTÓN BUSCAR, BOTÓN MENU y BOTÓN REDUCIR */

QPushButton#BTN_AMP, QPushButton#BTN_AYU, QPushButton#BTN_BUS, QPushButton#BTN_MEN, QPushButton#BTN_RED {
    border-radius: 0px;
    qproperty-iconSize: 18px; }

/*  --------------------------------------------------------------------------------
    QScrollBar - BARRA DE SCROLL VERTICAL Y HORIZONTAL
    -------------------------------------------------------------------------------- */

QScrollBar:vertical {
    background: $color-fondo-barra-scroll-normal;
    border-radius: 6px;
    padding-left: 2px;
    padding-right: 2px;
    width: 12px; }

QScrollBar:horizontal {
    background: $color-fondo-barra-scroll-normal;
    border-radius: 6px;
    padding-bottom: 2px;
    padding-top: 2px;
    height: 12px;  }

QScrollBar:vertical:hover {
    background: $color-fondo-barra-scroll-hover; }

QScrollBar:horizontal:hover {
    background: $color-fondo-barra-scroll-hover; }

QScrollBar::handle:vertical {
    background: $color-barra-scroll-normal;
    border: 2px solid transparent;
    border-radius: 4px;
    min-height: 90px; }

QScrollBar::handle:horizontal {
    background: $color-barra-scroll-normal;
    border: 2px solid transparent;
    border-radius: 4px;
    min-width: 90px; }

QScrollBar::handle:hover {
    background: $color-barra-scroll-hover;
    border: 2px solid transparent; }

QScrollBar::add-line:vertical {
    background: none;
    border: none; }

QScrollBar::sub-line:vertical {
    background: none;
    border: none; }

QScrollBar::add-line:horizontal {
    background: none;
    border: none; }

QScrollBar::sub-line:horizontal {
    background: none;
    border: none; }

/*  --------------------------------------------------------------------------------
    QScrollBar - Recuadro de intersección entre las scrollbar horizontal y vertical
    -------------------------------------------------------------------------------- */

QAbstractScrollArea::corner {
    background: transparent;
    border: transparent; }

/*  --------------------------------------------------------------------------------
    QSlider - DESLIZADOR
    -------------------------------------------------------------------------------- */

QSlider {
    background: transparent; }

/*  --------------------------------------------------------------------------------
    QSplitter - SPLITTER
    -------------------------------------------------------------------------------- */

QSplitter {
    background: transparent; }

/*  --------------------------------------------------------------------------------
    QStatusBar - BARRA DE ESTADO
    -------------------------------------------------------------------------------- */

QStatusBar {
    background-color: $color-fondo-general;
    border-top: 1px solid $color-borde-normal;
    qproperty-visible: false; }

/*  --------------------------------------------------------------------------------
    QTabWidget - SEPARADOR DE FORMULARIOS (Pestañas)
    -------------------------------------------------------------------------------- */

QTabWidget::pane {
    border-top: 1px solid $color-borde-linea;
    margin:0px;
    top: 10px; }

QTabWidget::tab-bar {
    left: 10px;
    min-width: 10px;
    right: 10px;
    top: 10px; }

QTabBar::tab {
    background-color: $color-fondo-general;
    color: $color-texto-estatico;
    font-weight: normal;
    margin-left: 5px;
    margin-right: 10px;
    padding-bottom: 3px;
    padding-left: 5px;
    padding-right: 5px;
    padding-top: 5px; }

QTabBar::tab:hover {
    border-top: 1px solid $color-fondo-focus; }

QTabBar::tab:selected {
    color: $color-texto-seleccionado;
    border-bottom: 2px solid $color-borde-focus; }

QTabBar::close-button {
    image: url($sysCacheClientPath/CRR.png);
    margin: 3px; }

QTabBar::scroller {
    padding-top: 10px;
    width: 64px;}

QTabBar QToolButton::left-arrow {
    image: url($sysCacheClientPath/IZQ.png);
    width: 32px;}

QTabBar QToolButton::right-arrow {
    image: url($sysCacheClientPath/DER.png);
    width: 32px;}

/*  --------------------------------------------------------------------------------
    QTableView - REJILLA
    VCFootView - PIE DE REJILLA
    -------------------------------------------------------------------------------- */

/* CABECERA DE REJILLA */

QTableView {
    alternate-background-color: transparent;
    background-color: white;
    border: none;
    border-bottom: 1px solid #BDBDBD;
    border-radius: $border-radius;
    color: $color-texto-rejilla-detalle;
    font-weight: normal;
    gridline-color: transparent;
    min-height: 30px;
    selection-background-color: $color-fondo-texto;
    selection-color: $color-texto-seleccionado;
    qproperty-showGrid: false; }

QTableView QTableCornerButton::section {
    background-color: transparent;
    border: 0px; }

QTableView::item:focus {
    min-height: 30px;
    selection-background-color: $color-fondo-texto;
    selection-color: $color-texto-seleccionado; }

/* PIE DE REJILLA */

VCFootView {
    background: transparent;
    border: none;
    border-right: 5px solid transparent;
    color: $color-texto-edicion;
    min-height: 30px; }

VCFootView::section:active {
    background: transparent;
    border: none;
    border-right: 5px solid transparent; }

VCFootView::section:!active {
    background: transparent;
    border: none;
    border-right: 5px solid transparent; }

/*  --------------------------------------------------------------------------------
    QTitanGrid - REJILLA AVANZADA
    -------------------------------------------------------------------------------- */

/* MARCO DE LA REJILLA */

Qtitan--Grid {            
    padding: 1px;
    margin: 1px;
    border: 1px solid $color-texto-seleccionado;  
    border-style: none;
    gridline-color: $color-fondo-texto;

/* COLOR DE FONDO Y FONDO PAR DE FILAS */
    background-color: $color-fondo-general;                        /* Fondo de la rejilla */
    alternate-background-color: $color-fondo-general;              /* Color de las líneas impares */

/* COLOR DE LA FILA SELECCIONADA */
    selection-color: $color-texto-rejilla-detalle;                /* Color del texto del registro seleccionado */
    selection-background-color: $color-fondo-texto;                /* Background del registro seleccionado */

/* AERA DE LOS AGRUPAMIENTOS */
    qproperty-headerHeight: 30;                                    /* Altura de la cabecera de agrupación */  
    qproperty-headerTextColor: $color-texto-rejilla-detalle;       /* Texto informativo de la cabecera de agrupamiento */
    qproperty-headerBrush: $color-fondo-general;                   /* Color de la franja de agrupamiento */
    qproperty-headerBorderColor: $color-fondo-general     ;        /* Color del borde */
    qproperty-headerBorderSize: 0;                                 /* Tamaño del borde */
    qproperty-headerBorderStyle: dot-dash;
    /* La fuente se puede establecer de dos formas. Con headerTextFont que requiere un formato concreto
    o indicando la familia y el tamaño en dos propiedades*/
    /*qproperty-headerTextFont: "Verdana,-1,20,5,0,0,0,0,0,0";*/ 
    qproperty-headerTextFontSize: 12;                              /* Tamaño de la fuente */
    qproperty-headerTextFontFamily: "Verdana";

/* AGRUPAMIENTOS */
    qproperty-groupColorLevel0: $color-texto-rejilla-agrupamiento; /* Color de fondo según el nivel de agrupamiento */
    qproperty-groupColorLevel1: $color-texto-seleccionado;
    qproperty-groupColorLevel2: $color-fondo-texto;
    qproperty-groupColorLevel3: $color-borde-linea;
    qproperty-groupColorLevel4: magenta; 
    qproperty-groupColorLevel5: white; 
    qproperty-groupColorLevel6: white; 
    qproperty-groupColorLevel7: white;

/* NIVEL DE DESPLIEGUE DE AGRUPAMIENTOS */
    qproperty-keepGroupLevelExpanded: 3;

/* PIES DE GRUPO */
    qproperty-summaryGroupItemTextColor: red;                      /* No está operativo en la versión 26 */
    qproperty-summaryGroupItemBackgroundColor: $color-fondo-general;
    qproperty-summaryGroupItemHeight: 30;
    qproperty-summaryGroupItemBorderColor: $color-borde-linea;
    qproperty-summaryGroupItemBorderSize: 1;
    qproperty-summaryGroupItemBorderStyle: native;

/* PIES DE REJILLA */
    qproperty-summaryItemTextColor: blue;                          /* No está operativo en la versión 26 */
    qproperty-summaryItemBackgroundColor: $color-fondo-general;
    qproperty-summaryItemHeight: 30;
    qproperty-summaryItemBorderColor: $color-texto-seleccionado;
    qproperty-summaryItemBorderSize: 1;
    qproperty-summaryItemBorderStyle: native;

/* ICONOS */
    qproperty-sortAscendingIcon: url($sysCacheClientPath/ARR.png);
    qproperty-sortDescendingIcon: url($sysCacheClientPath/ABA.png);
    qproperty-focusRowIndicatorIcon: url($sysCacheClientPath/DER.png);
    qproperty-columnFilterToolIcon: url($sysCacheClientPath/FLT_ON.png);
    qproperty-columnFilterToolBoldIcon: url($sysCacheClientPath/FLT_OFF.png);
    qproperty-filterCheckBoxOnIcon: url($sysCacheClientPath/ARR.png);
    qproperty-filterCheckBoxOffIcon: url($sysCacheClientPath/ARR.png);
    qproperty-editorPopupOkIcon: url($sysCacheClientPath/ARR.png);
    qproperty-editorPopupCancelIcon: url($sysCacheClientPath/CRR.png);
    qproperty-editorPopupCrossIcon: url($sysCacheClientPath/ARR.png);    

/* LÍNEA DE COLUMNAS INMOVILIZADAS */
    qproperty-fixedSeparatorWidth: 3;
    qproperty-fixedSeparatorColor: $color-texto-seleccionado;
    qproperty-fixedSeparatorStyle: dot-dash; }

/* CABECERA Y BANDA */
Qtitan--Grid::section {
    color: $color-texto-rejilla-cabecera;
    font-weight: bold;
    border: none;
    background-color: $color-fondo-general; }

/* CABECERA CON FOCO */
Qtitan--Grid::section:focus {
    background-color: $color-fondo-general;
    border: 1px solid $color-texto-seleccionado; }

/* CELDAS */
Qtitan--Grid::item {
   /*background-color: red;*/                               /*Se aplica sobre los posibles colores asignados genéricamente en la rejilla al fondo o fondo par*/ 
   }

/* CELDA CON FOCO */
Qtitan--Grid::item:focus {
    background-color: $color-fondo-texto;                   /*Se aplica sobre los posibles colores asignados genéricamente en la rejilla al fondo o fondo par*/
    border: 1px solid $color-fondo-general; }

/* MARCO DEL FOCO Y CABECERA SELECCIONADA DE AGRUPACIÓN */
Qtitan--Grid:focus {
   outline: 1px dotted $color-texto-seleccionado; }


/* FILTRO RÁPIDO */
Qtitan--Grid > Qtitan--GridColumnFilterPopup, Qtitan--Grid > Qtitan--GridColumnFilterPopup *
{
    background-color: $color-borde-transparente;
    color: $color-texto-rejilla-cabecera ;
    font: 12pt "Verdana"; }

/* SELECCIÓN DE CAMPOS */
Qtitan--Grid > Qtitan--GridColumnsQuickCustomization, Qtitan--Grid > Qtitan--GridColumnsQuickCustomization *
{
    background-color: $color-borde-transparente;
    color: $color-texto-rejilla-cabecera;
    font: 12pt "Verdana"; }

/*  --------------------------------------------------------------------------------
    QToolBar - BARRA DE HERRAMIENTAS
    -------------------------------------------------------------------------------- */

QToolBar {
    background-color: transparent;
    border: 0px;
    padding: 0px;
    spacing: 10px;
    qproperty-iconSize: 18px; }

QToolButton {
    background-color: $color-fondo-boton-toolbar;
    border: 1px solid $color-borde-normal;
    border-radius: $border-radius;
    color: $color-texto-estatico;
    margin-right: 0px;
    min-height: 18px;
    min-width: 18px;
    padding: 5px;
    qproperty-iconSize: 18px; }

QToolButton:hover {
    border: 1px solid $color-borde-hover; }

QToolButton:focus {
    border: 2px solid $color-borde-focus; }

QToolButton:pressed {
    border: 2px solid $color-borde-focus; }

QToolButton:disabled {
    background-color: $color-fondo-boton-normal;
    border: 1px solid $color-borde-disabled;
    color: $color-texto-boton-disabled; }

QToolButton::menu-indicator {
    background-color: transparent;
    color: transparent; }

/*  --------------------------------------------------------------------------------
    QTooltip - TOOLTIP
    -------------------------------------------------------------------------------- */

QToolTip {
    align: center;
    background-color: $color-fondo-texto;
    border: 1px solid $color-borde-normal;
    border-radius: $border-radius;
    color: $color-texto-edicion;
    vertical-align:center; }

/*  --------------------------------------------------------------------------------
    QTreeView - ARBOL VISOR Y MENU ARBOLADO
    -------------------------------------------------------------------------------- */

QTreeView {
    outline: 0;
    background-color: $color-fondo-general;
    selection-background-color: transparent;
font-size: 20px;
    selection-color: $color-texto-seleccionado;
    show-decoration-selected: 1; }

QTreeView::item {
    border-right: 0;
    color: $color-texto-estatico;
    margin-bottom:2px;
    margin-right:10px;
