/*  --------------------------------------------------------------------------------
    QTITAN_GRID - REJILLA AVANZADA
    -------------------------------------------------------------------------------- */ 

/* MARCO DE LA REJILLA */

Qtitan--Grid {            
    padding: 1px;
    margin: 1px;
    border: 1px solid #3F51B5;  
    border-style: native;
    gridline-color: #DFDFDF;

/* COLOR DE FONDO Y FONDO PAR DE FILAS */
    background-color: white;                    /* Fondo de la rejilla */
    alternate-background-color: white;          /* Color de las líneas impares */

/* COLOR DE LA FILA SELECCIONADA */
    selection-color: #FFF;                      /* Color del texto del registro seleccionado */
    selection-background-color: #3F51B5;        /* Background del registro seleccionado */

/* AERA DE LOS AGRUPAMIENTOS */
    qproperty-headerHeight: 30;                 /* Altura de la cabecera de agrupación */  
    qproperty-headerTextColor: #FFF;            /* Texto informativo de la cabecera de agrupamiento */
    qproperty-headerBrush: #3F51B5;             /* Color de la franja de agrupamiento */
    qproperty-headerBorderColor: #3F51B5;       /* Color del borde */
    qproperty-headerBorderSize: 0;              /* Tamaño del borde */
    qproperty-headerBorderStyle: dot-dash;
    /* La fuente se puede establecer de dos formas. Con headerTextFont que requiere un formato concreto o indicando la familia y el tamaño en dos propiedades*/
    /*qproperty-headerTextFont: "Verdana,-1,20,5,0,0,0,0,0,0";*/ 
    qproperty-headerTextFontSize: 12;           /* Tamaño de la fuente */
    qproperty-headerTextFontFamily: "Verdana";

/* AGRUPAMIENTOS */
    qproperty-groupColorLevel0: #CAD3E2;        /* Color de fondo según el nivel de agrupamiento */
    qproperty-groupColorLevel1: #E2C8E9;
    qproperty-groupColorLevel2: #DFDFDF;
    qproperty-groupColorLevel3: #CAD3E2;
    qproperty-groupColorLevel4: magenta; 
    qproperty-groupColorLevel5: white; 
    qproperty-groupColorLevel6: white; 
    qproperty-groupColorLevel7: white;

/* NIVEL DE DESPLIEGUE DE AGRUPAMIENTOS */
    qproperty-keepGroupLevelExpanded: 3;

/* PIES DE GRUPO */
    qproperty-summaryGroupItemTextColor: red;           /* No está operativo en la versión 26 */
    qproperty-summaryGroupItemBackgroundColor: #CAD3E2;
    qproperty-summaryGroupItemHeight: 30;
    qproperty-summaryGroupItemBorderColor: #CAD3E2;
    qproperty-summaryGroupItemBorderSize: 1;
    qproperty-summaryGroupItemBorderStyle: native;

/* PIES DE REJILLA */
    qproperty-summaryItemTextColor: blue;               /* No está operativo en la versión 26 */
    qproperty-summaryItemBackgroundColor: #CAD3E2;
    qproperty-summaryItemHeight: 30;
    qproperty-summaryItemBorderColor: #CAD3E2;
    qproperty-summaryItemBorderSize: 1;
    qproperty-summaryItemBorderStyle: native;

/* ICONOS */
    qproperty-sortAscendingIcon: url(/Users/mariocondefornos/Velneo/cacherun/c3.velneo.com_8940/ARR.png);
    qproperty-sortDescendingIcon: url(/Users/mariocondefornos/Velneo/cacherun/c3.velneo.com_8940/ABA.png);
    qproperty-focusRowIndicatorIcon: url(/Users/mariocondefornos/Velneo/cacherun/c3.velneo.com_8940/DER.png);
    qproperty-columnFilterToolIcon: url(/Users/mariocondefornos/Velneo/cacherun/c3.velneo.com_8940/FLT_ON.png);
    qproperty-columnFilterToolBoldIcon: url(/Users/mariocondefornos/Velneo/cacherun/c3.velneo.com_8940/FLT_OFF.png);
    qproperty-filterCheckBoxOnIcon: url(/Users/mariocondefornos/Velneo/cacherun/c3.velneo.com_8940/ARR.png);
    qproperty-filterCheckBoxOffIcon: url(/Users/mariocondefornos/Velneo/cacherun/c3.velneo.com_8940/ARR.png);
    qproperty-editorPopupOkIcon: url(/Users/mariocondefornos/Velneo/cacherun/c3.velneo.com_8940/ARR.png);
    qproperty-editorPopupCancelIcon: url(/Users/mariocondefornos/Velneo/cacherun/c3.velneo.com_8940/CRR.png);
    qproperty-editorPopupCrossIcon: url(/Users/mariocondefornos/Velneo/cacherun/c3.velneo.com_8940/ARR.png);    

