/*--------------------------------------------------------------------------------
    QTitanGrid - REJILLA AVANZADA
--------------------------------------------------------------------------------*/

/* Marco de la rejilla */       
Qtitan--Grid {            
    padding: $space-controls;
    margin: $space-controls;
    gridline-color: $color-fondo-texto;

    /* Color de fondo y fondo par de filas */
    background-color: $color-fondo-general;                            /* Fondo de la rejilla */
    alternate-background-color: $color-fondo-general;                  /* Color de las líneas impares */

    /* Color de la fila seleccionada */
    selection-color: $color-texto-rejilla-cabecera;                    /* Color del texto del registro seleccionado */
    selection-background-color: $color-fondo-texto;                    /* Background del registro seleccionado */

    /* Área de los agrupamientos */
    qproperty-headerTextColor: $color-texto-seleccionado;              /* Texto informativo de la cabecera de agrupamiento */
    qproperty-headerBrush: $color-fondo-general;                       /* Color de la franja de agrupamiento */
    qproperty-headerBorderColor: $color-fondo-general;                 /* Color del borde */
    qproperty-headerBorderSize:  $size-borde;                          /* Tamaño del borde */
    qproperty-headerTextFontSize: $alto-fuente-s;                      /* Tamaño de la fuente */

    /* Agrupamientos */
    qproperty-groupColorLevel0: $color-texto-rejilla-agrupamiento;     /* Color de fondo según el nivel de agrupamiento */
    qproperty-groupColorLevel1: $color-texto-seleccionado;
    qproperty-groupColorLevel2: $color-fondo-texto;
    qproperty-groupColorLevel3: $color-borde-linea;
    qproperty-groupColorLevel4: magenta; 
    qproperty-groupColorLevel5: white; 
    qproperty-groupColorLevel6: white; 
    qproperty-groupColorLevel7: white;

    /* Nivel de despliegue de agrupamientos */
    qproperty-keepGroupLevelExpanded: 3;

    /* Pies de grupo */
    qproperty-summaryGroupItemTextColor: $color-texto-rejilla-pie;     /* No está operativo en la versión 26 */
    qproperty-summaryGroupItemBackgroundColor: $color-fondo-general;
    qproperty-summaryGroupItemHeight: $alto-referencia;
    qproperty-summaryGroupItemBorderColor: $color-borde-linea;
    qproperty-summaryGroupItemBorderSize: $size-borde;

    /* Pies de rejilla */
    qproperty-summaryItemTextColor: $color-texto-rejilla-pie;          /* No está operativo en la versión 26 */
    qproperty-summaryItemBackgroundColor: $color-fondo-general;
    qproperty-summaryItemHeight: $alto-referencia;
    qproperty-summaryItemBorderColor: $color-borde-linea;
    qproperty-summaryItemBorderSize: $size-borde;

    /* Línea de columnas inmovilizadas */
    qproperty-fixedSeparatorWidth: 3;
    qproperty-fixedSeparatorColor: $color-texto-seleccionado; }

/* Cabecera y banda */
Qtitan--Grid::section {
    color: $color-texto-rejilla-cabecera;
    border: $size-borde solid $color-fondo-texto;
    background-color: $color-fondo-general; }

/* Cabecera con foco */
Qtitan--Grid::section:focus {
    background-color: $color-fondo-general;
    border: $size-borde solid $color-texto-seleccionado; }

/* Celdas */
Qtitan--Grid::item {
    /*background-color: $color-fondo-general;*/                        /* Se aplica sobre los posibles colores asignados genéricamente en la rejilla al fondo o fondo par */ }

/* Celda con foco */
Qtitan--Grid::item:focus {
    background-color: $color-fondo-texto;                              /* Se aplica sobre los posibles colores asignados genéricamente en la rejilla al fondo o fondo par */
    border: $size-borde solid $color-fondo-general; }

/* Marco del foco y cabecera seleccionada de agrupación */
Qtitan--Grid:focus {
    outline: $size-borde dotted $color-texto-seleccionado; }

/* Filtro rápido */
Qtitan--Grid > Qtitan--GridColumnFilterPopup, Qtitan--Grid > Qtitan--GridColumnFilterPopup * {
    background-color: $color-borde-transparente;
    color: $color-texto-rejilla-cabecera ; }

/* Selección de campos */
Qtitan--Grid > Qtitan--GridColumnsQuickCustomization, Qtitan--Grid > Qtitan--GridColumnsQuickCustomization * {
    background-color: $color-borde-transparente;
    color: $color-texto-rejilla-cabecera; }

