/*--------------------------------------------------------------------------------
    QComboBox - COMBOBOX
--------------------------------------------------------------------------------*/

QComboBox {
	background-color: $color-fondo-edicion;
	border: $size-borde solid $color-borde-normal;
	color: $color-texto-edicion;
	font-size: $alto-fuente-m;
	height: $alto-referencia;
	padding-left: $padding-combo-izquierda;
	padding-right: $padding-combo-derecha;
	selection-background-color: $color-elemento-seleccionado-combobox;
	selection-color: $color-texto-seleccionado-combobox;
	width: $ancho-referencia;
}

QComboBox:hover {
	border: $size-borde solid $color-borde-hover;
}

QComboBox:focus {
	border: $size-borde-seleccionado solid $color-borde-focus;
}

QComboBox:disabled {
	background-color: $color-fondo-disabled;
	color: $color-texto-disabled;
}

QComboBox::drop-down {
	border: none;
	image: url($sysCacheClientPath/ABA.png);
	width: $ancho-icono-combobox;
}

QComboBox QAbstractItemView {
	border: $size-borde solid $color-borde-linea;
}

/*--------------------------------------------------------------------------------
    QDockWidget - DOCK
--------------------------------------------------------------------------------*/

QDockWidget {
	color: transparent;
	font-size: 10px;
	min-width: 10px;
	width: 210px;
}

QDockWidget::title {
	background: $color-fondo-general;
	color: $color-texto-estatico;
	margin: 0px;
	padding: 0px;
	text-align: center;
}

QDockWidget::close-button {
	image: none;
	position: absolute;
	subcontrol-origin: margin;
	subcontrol-position: top right;
	top: 0px;
	left: 0px;
	bottom: 0px;
	width: 0px;
}

QDockWidget::float-button {
	image: none;
	position: absolute;
	subcontrol-origin: margin;
	subcontrol-position: top right;
	top: 0px;
	left: 0px;
	bottom: 0px;
	width: 0px;
}

/*--------------------------------------------------------------------------------
    QFrame - MARCO
--------------------------------------------------------------------------------*/

QFrame {
	background: $color-fondo-general;
	border: 0px;
}

/*--------------------------------------------------------------------------------
    QGroupBox - CAJA DE GRUPO
--------------------------------------------------------------------------------*/

QGroupBox {
	border: $size-borde solid $color-borde-normal;
	border-radius: 5px;
	font-size: 12px;
	font-weight: bold;
	margin-top: 20px;
	padding: 5px;
}

QGroupBox::title {
	color: $color-texto-edicion;
	padding-top: -5px;
	padding-left: 5px;
	subcontrol-origin: margin;
}

/*--------------------------------------------------------------------------------
    QHeaderView - CABECERA DE REJILLAS Y ARBOLES
--------------------------------------------------------------------------------*/

QHeaderView::section {
	background: $color-fondo-general;
	border: none;
	border-bottom: $size-borde solid $color-borde-linea;
	color: $color-texto-rejilla-cabecera;
	font-size: $alto-fuente-s;
	font-weight: bold;
	/* margin-right: $space-controls; */
	/* Posible incidencia en detalle con márgenes */
	min-height: $alto-referencia;
	padding-left: 2px;
	/* Posible incidencia en detalle que siempre aplica 2px a izquierda y derecha del texto */
