QPushButton#BTN_AVA_CTL {
           min-width: 0px;
}

/*--------------------------------------------------------------------------------
    QScrollBar - BARRA DE SCROLL VERTICAL Y HORIZONTAL
--------------------------------------------------------------------------------*/

/* SCROLL VERTICAL Y HORIZONTAL */

QScrollBar:vertical {
	background: $color-fondo-barra-scroll-normal;
	border: $size-borde solid $color-fondo-barra-scroll-normal;
	border-radius: 0px;
	padding-left: $size-borde-seleccionado;
	padding-right: $size-borde-seleccionado;
	margin: 0px 0px 0px 0px;
	width: $ancho-minimo-scroll;
}

QScrollBar:horizontal {
	background: $color-fondo-barra-scroll-normal;
	border: $size-borde solid $color-fondo-barra-scroll-normal;
	border-radius: 0px;
	padding-bottom: $size-borde-seleccionado;
	padding-top: $size-borde-seleccionado;
	margin: 0px 0px 0px 0px;
	height: $ancho-minimo-scroll;
}

/* MANEJADOR DE LA BARRA DE SCROLL */

QScrollBar::handle:vertical {
	background: $color-barra-scroll-normal;
	border: $size-borde solid $color-barra-scroll-normal;
	border-radius: 0px;
	min-height: $ancho-referencia;
}

QScrollBar::handle:horizontal {
	background: $color-barra-scroll-normal;
	border: $size-borde solid $color-barra-scroll-normal;
	border-radius: 0px;
	min-width: $ancho-minimo;
}

QScrollBar::hover {
	background: $color-fondo-barra-scroll-normal;
	border: $size-borde solid $color-barra-scroll-normal;
}

QScrollBar::handle:hover {
	background: $color-barra-scroll-hover;
	border: $size-borde solid $color-barra-scroll-hover;
}

/* BOTONES EN LOS EXTREMOS DE LA BARRA DE SCROLL */

QScrollBar::add-line:vertical {
	background: $color-fondo-barra-scroll-normal;
	border: $size-borde solid $color-fondo-barra-scroll-normal;
	height: $ancho-borde-sin;
	subcontrol-origin: margin;
	subcontrol-position: bottom;
	width: $ancho-borde-sin;
}

QScrollBar::add-line:horizontal {
	background: $color-fondo-barra-scroll-normal;
	border: $size-borde solid $color-fondo-barra-scroll-normal;
	height: $ancho-minimo;
	subcontrol-origin: margin;
	subcontrol-position: right;
	width: $ancho-minimo;
}

QScrollBar::sub-line:vertical {
	background: $color-fondo-barra-scroll-normal;
	border: $size-borde solid $color-fondo-barra-scroll-normal;
	height: $ancho-borde-sin;
	subcontrol-position: top;
	subcontrol-origin: margin;
	width: $ancho-borde-sin;
}

QScrollBar::sub-line:horizontal {
	background: $color-fondo-barra-scroll-normal;
	border: $size-borde solid $color-fondo-barra-scroll-normal;
	height: $ancho-minimo;
	subcontrol-origin: margin;
	subcontrol-position: left;
	width: $ancho-minimo;
}

/*--------------------------------------------------------------------------------
    QScrollBar - Recuadro de intersección entre las scrollbar horizontal y vertical
--------------------------------------------------------------------------------*/

QAbstractScrollArea::corner {
	background: transparent;
	border: transparent;
}

/*--------------------------------------------------------------------------------
    QSlider - DESLIZADOR
--------------------------------------------------------------------------------*/

QSlider {
	background: transparent;
}

/*--------------------------------------------------------------------------------
    QSplitter - SPLITTER
--------------------------------------------------------------------------------*/

QSplitter {
	background: $color-fondo-general;
}

/*--------------------------------------------------------------------------------
    QStatusBar - BARRA DE ESTADO
--------------------------------------------------------------------------------*/

QStatusBar {
	background-color: $color-fondo-general;
	border-top: 1px solid $color-borde-normal;
	qproperty-visible: false;
}

/*--------------------------------------------------------------------------------
    QTabWidget - SEPARADOR DE FORMULARIOS (Pestañas)
--------------------------------------------------------------------------------*/

QTabWidget::pane {
	border-top: 1px solid $color-borde-linea;
	margin: 0px;
	top: 10px;
}

QTabWidget::tab-bar {
	left: 10px;
	min-width: 10px;
	right: 10px;
	top: 10px;
}

QTabBar {
	font-weight: normal;
	font-size: $alto-fuente-s;
	text-transform: uppercase;
}

QTabBar::tab {
	background-color: $color-fondo-general;
	color: $color-texto-tab;
	/*margin-left: 5px;*/
	margin-right: 10px;
	padding-bottom: 3px;
	padding-left: 5px;
	padding-right: 5px;
	padding-top: 5px;
}

QTabBar::tab:hover {
	border-bottom: $size-borde-seleccionado solid $color-borde-tab-hover;
}

QTabBar::tab:selected {
	color: $color-texto-tab-seleccionado;
	border-bottom: $size-borde-seleccionado solid $color-borde-tab-seleccionada;
}

QTabBar::close-button {
	image: url($sysCacheClientPath/CRR.png);
	margin: 3px;
}

QTabBar::scroller {
	padding-top: 10px;
	width: 64px;
}

QTabBar QToolButton::left-arrow {
	image: url($sysCacheClientPath/IZQ.png);
	width: 32px;
}

QTabBar QToolButton::right-arrow {
	image: url($sysCacheClientPath/DER.png);
	width: 32px;
}

/*--------------------------------------------------------------------------------
    QTableView - REJILLA
    VCFootView - PIE DE REJILLA
--------------------------------------------------------------------------------*/

/* REJILLA */

QTableView {
	alternate-background-color: $color-fondo-general;
	background-color: $color-fondo-general;
	border: none;
	border-bottom: $size-borde solid $color-borde-defecto;
	color: $color-texto-rejilla-detalle;
	font-weight: normal;
	gridline-color: $color-fondo-general;
	margin-right: $space-controls;
	min-height: $alto-referencia;
	/* Posible incidencia, parece que no funciona */
	padding-left: $space-controls;
	/* Posible incidencia, parece que no funciona */
	padding-right: $space-controls;
	/* Posible incidencia, parece que no funciona */
	qproperty-showGrid: $grid-rejilla;
	selection-background-color: $color-fondo-rejilla-fila-focus;
	selection-color: $color-texto-rejilla-fila-focus;
}

QTableView QTableCornerButton::section {
	background-color: $color-fondo-general;
	border: none;
	border-bottom: $size-borde solid $color-borde-linea;
}

/* PIE DE REJILLA */

VCFootView {
	background: $color-fondo-general;
	border: none;
	color: $color-texto-rejilla-pie;
	font-size: $alto-fuente-s;
	font-weight: bold;
	margin-right: $space-controls;
	min-height: $alto-referencia;
	padding-left: $space-controls;
	padding-right: $space-controls;
}

/*--------------------------------------------------------------------------------
    QTitanGrid - REJILLA AVANZADA
--------------------------------------------------------------------------------*/

/* Marco de la rejilla */
Qtitan--Grid {
	border-bottom: $size-borde solid $color-borde-defecto;
	color: $color-texto-rejilla-detalle;
	gridline-color: $color-sistema-gris-medio;
	margin: $space-controls;
	padding: $space-controls;

	/* Color de fondo y fondo par de filas */
	background-color: $color-fondo-general;
	/* Fondo de la rejilla */
	/*alternate-background-color: $color-fondo-disabled;*/
	/* Color de las líneas impares */

	/* Color de la fila seleccionada */
	/* Background del registro seleccionado */
	selection-background-color: $color-sistema-gris-claro;
	/* Color del texto del registro seleccionado */
	selection-color: $color-sistema-gris-oscuro;

	/* Área de los agrupamientos */
	qproperty-headerTextColor: $color-texto-seleccionado;
	/* Texto informativo de la cabecera de agrupamiento */
	qproperty-headerBrush: $color-fondo-general;
	/* Color de la franja de agrupamiento */
	qproperty-headerBorderColor: $color-fondo-general;
	/* Color del borde */
	qproperty-headerBorderSize: $size-borde;
	/* Tamaño del borde */
	qproperty-headerTextFontSize: $alto-fuente-s;
	/* Tamaño de la fuente */

	/* Agrupamientos */
	/* Color de fondo según el nivel de agrupamiento */
	qproperty-groupColorLevel0: $color-texto-rejilla-agrupamiento;
	qproperty-groupColorLevel1: $color-borde-linea;
	qproperty-groupColorLevel2: $color-texto-rejilla-agrupamiento;
	qproperty-groupColorLevel3: $color-borde-linea;
	qproperty-groupColorLevel4: $color-texto-rejilla-agrupamiento;
	qproperty-groupColorLevel5: $color-borde-linea;
	qproperty-groupColorLevel6: $color-texto-rejilla-agrupamiento;
	qproperty-groupColorLevel7: $color-borde-linea;

	/* Nivel de despliegue de agrupamientos */
	qproperty-keepGroupLevelExpanded: 3;

	/* Pies de grupo */
	qproperty-summaryGroupItemTextColor: $color-texto-rejilla-pie;
	/* No está operativo en la versión 26 */
	qproperty-summaryGroupItemBackgroundColor: $color-sistema-producto-oscuro;
	qproperty-summaryGroupItemHeight: $alto-referencia;
	qproperty-summaryGroupItemBorderColor: $color-sistema-producto-oscuro;
	qproperty-summaryGroupItemBorderSize: $ancho-borde-sin;

	/* Pies de rejilla */
	qproperty-summaryItemTextColor: $color-texto-rejilla-pie;
	/* No está operativo en la versión 26 */
	qproperty-summaryItemBackgroundColor: $color-sistema-producto-oscuro;
	qproperty-summaryItemHeight: $alto-referencia;
	qproperty-summaryItemBorderColor: $color-sistema-producto-oscuro;
	qproperty-summaryItemBorderSize: $ancho-borde-sin;

	/* Línea de columnas inmovilizadas */
	qproperty-fixedSeparatorWidth: 3;
	qproperty-fixedSeparatorColor: $color-texto-seleccionado;
}

/* Cabecera y banda */
Qtitan--Grid::section {
	color: $color-texto-rejilla-cabecera;
	background-color: $color-fondo-general;
	border-bottom: $size-borde solid $color-borde-defecto;
}

/* Cabecera con foco */
Qtitan--Grid::section:focus {
	background-color: $color-fondo-general;
}

/* Celdas */
Qtitan--Grid::item {
	/* Se aplica sobre los posibles colores asignados genéricamente en la rejilla al fondo o fondo par */
	/*background-color: $color-fondo-general;*/
	border-bottom: $size-borde solid $color-borde-disabled;
	color: $color-texto-rejilla-detalle;
}

/* Celda con foco */
Qtitan--Grid::item:focus {
	/* Se aplica sobre los posibles colores asignados genéricamente en la rejilla al fondo o fondo par */
	background-color: $color-sistema-blanco;
	border: $size-borde solid $color-borde-disabled;
	color: $color-texto-rejilla-fila-focus;
}

/* Marco del foco y cabecera seleccionada de agrupación */
Qtitan--Grid:focus {
	outline: $size-borde dotted $color-texto-seleccionado;
}

/* Filtro rápido */
Qtitan--Grid>Qtitan--GridColumnFilterPopup,
Qtitan--Grid>Qtitan--GridColumnFilterPopup * {
	background-color: $color-borde-transparente;
	color: $color-texto-rejilla-cabecera;
}

/* Selección de campos */
Qtitan--Grid>Qtitan--GridColumnsQuickCustomization,
Qtitan--Grid>Qtitan--GridColumnsQuickCustomization * {
	background-color: $color-borde-transparente;
	color: $color-texto-rejilla-cabecera;
}

/*--------------------------------------------------------------------------------
    QToolBar - BARRA DE HERRAMIENTAS
--------------------------------------------------------------------------------*/

QToolBar {
	background-color: transparent;
	border: 0px;
	padding: 0px;
	spacing: 10px;
	qproperty-iconSize: $size-icono;
}

QToolButton {
	background-color: $color-boton-icono-fondo;
	border: $size-borde solid $color-boton-icono-borde;
	color: $color-texto-estatico;
	margin: $margen-sin;
	min-height: $alto-referencia;
	min-width: $alto-referencia;
	padding: 0px;
	qproperty-iconSize: $size-icono;
}

QToolButton:hover {
	background-color: $color-boton-icono-fondo-hover;
	border: $size-borde solid $color-boton-icono-borde-hover;
}

QToolButton:focus {
	background-color: $color-boton-icono-fondo-focus;
	border: $size-borde-seleccionado solid $color-boton-icono-borde-focus;
}

QToolButton:pressed {
	background-color: $color-boton-icono-fondo-pressed;
	border: $size-borde-seleccionado solid $color-boton-icono-borde-pressed;
}

QToolButton:disabled {
	/* background-color: $color-fondo-boton-normal;
    border: $size-borde solid $color-borde-disabled;
    color: $color-texto-boton-disabled; */
}

QToolButton::menu-indicator {
	background-color: transparent;
	color: transparent;
}

QToolBar QToolButton {
	background-color: $color-boton-toolbar-fondo;
	border: $size-borde solid $color-boton-toolbar-borde;
	color: $color-texto-estatico;
	margin: $margen-sin;
	min-height: 25;
	min-width: 25;
	padding: 0px;
	qproperty-iconSize: $size-icono;
}

QToolBar QToolButton:hover {
	background-color: $color-boton-toolbar-fondo-hover;
	border: $size-borde solid $color-boton-toolbar-borde-hover;
}

QToolBar QToolButton:focus {
	background-color: $color-boton-toolbar-fondo-focus;
	border: $size-borde-seleccionado solid $color-boton-toolbar-borde-focus;
}

QToolBar QToolButton:pressed {
	background-color: $color-boton-toolbar-fondo-pressed;
	border: $size-borde-seleccionado solid $color-boton-toolbar-borde-pressed;
}

/*--------------------------------------------------------------------------------
    QTooltip - TOOLTIP
--------------------------------------------------------------------------------*/

QToolTip {
	align: center;
	background-color: $color-sistema-fondo;
	border: $size-borde solid $color-borde-tooltip;
	color: $color-texto-edicion;
	vertical-align: center;
}

/*--------------------------------------------------------------------------------
    QTreeView - ARBOL VISOR Y MENU ARBOLADO
--------------------------------------------------------------------------------*/

QTreeView {
	outline: 0;
	background-color: $color-fondo-general;
	font-size: $alto-fuente-m;
	selection-background-color: transparent;
	selection-color: $color-texto-treewidget-seleccionado;
	show-decoration-selected: 1;
}

QTreeView::item {
	border-right: 0;
	color: $color-texto-treewidget;
	margin-bottom: 2px;
	margin-right: 10px;
	margin-top: 1px;
	padding-bottom: 2px;
	padding-top: 1px;
}

QTreeView::item:hover {
	border-color: transparent;
	color: $color-texto-treewidget-hover;
}

QTreeView::item:selected {
	color: $color-texto-treewidget-seleccionado;
}

QTreeView::branch:closed:has-children:!has-siblings,
QTreeView::branch:closed:has-children:has-siblings {
	image: url($sysCacheClientPath/DER.png);
}

QTreeView::branch:open:has-children:!has-siblings,
QTreeView::branch:open:has-children:has-siblings {
	image: url($sysCacheClientPath/ABA.png);
}

/*--------------------------------------------------------------------------------
    QCalendarWidget - CALENDARIO
    QWidget#qt_calendar - CALENDARIO
--------------------------------------------------------------------------------*/

QWidget#qt_calendar_navigationbar {
	background-color: $color-fondo-general;
	qproperty-gridVisible: false;
}

QWidget#qt_calendar_nextmonth {
	background: $color-fondo-general;
	border: $size-borde solid $color-fondo-general;
	qproperty-icon: url($sysCacheClientPath/DER.png);
}

QWidget#qt_calendar_prevmonth {
	background: $color-fondo-general;
	border: $size-borde solid $color-fondo-general;
	qproperty-icon: url($sysCacheClientPath/IZQ.png);
}

QWidget#qt_calendar_monthbutton,
QWidget#qt_calendar_yearbutton {
	background: $color-fondo-general;
	border: $size-borde solid $color-fondo-general;
	color: $color-texto-defecto;
}

QCalendarWidget {
	min-width: 300px;
	min-height: 260px;
}

QCalendarWidget QSpinBox {
	width: 35px;
	font-size: 12px;
	color: $color-texto-edicion;
	background-color: $color-fondo-general;
}

QCalendarWidget QSpinBox::up-button {
	subcontrol-origin: border;
	subcontrol-position: top right;
	width: 30px;
}

QCalendarWidget QSpinBox::down-button {
	subcontrol-origin: border;
	subcontrol-position: bottom right;
	width: 30px;
}

/*  --------------------------------------------------------------------------------
    VCOverlayShadow - OVERLAY 
    -------------------------------------------------------------------------------- */

VCOverlayShadow {
background-color: rgba(255, 255, 255, 5%); }

VCOverlayShadowGui {
background-color: rgba(255, 255, 255, 5%); }

datos.CSS:
/*  --------------------------------------------------------------------------------
    VERSION: 25.0
    AUTOR: VELNEO
    ULTIMA MODIFICACION: 18 Abril 2019

    INDICE
        - QCalendarWidget       CALENDARIO
        - QCheckBox             BOTON CHECK
        - QComboBox             COMBOBOX
        - QDateEdit             CAJA DE EDICIÓN DE CAMPO FECHA MODO DE EDICIÓN FECHA
        - QDateTime             CAJA DE VISUALIZACIÓN CAMPO FECHA Y HORA
        - QDateTimeEdit         CAJA DE EDICIÓN DE CAMPO FECHA Y HORA
        - QDialog               VENTANA EN CUADRO DE DIÁLOGO
        - QDockWidget           DOCK
        - QDoubleSpinBox        CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
        - QFrame                MARCO
        - QGroupBox             CAJA DE GRUPO
        - QHeaderView           CABECERA DE REJILLAS Y ÁRBOLES
        - QLabel                ETIQUETA DE TEXTO
        - QLineEdit             CAJA DE EDICIÓN DE TEXTO EN UNA LINEA
        - QMainWindow           VENTANA PRINCIPAL
        - QMenu                 MENU CONTEXTUAL
        - QMenuBar              BARRA DE MENU (Solo afecta a Windows)
        - QMessageBox           VENTANA DE MENSAJE
        - QNumberSpinBox        CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
        - QProgressBar          BARRA DE PROGRESO
        - QPushButton           BOTÓN
        - QRadioButton          BOTÓN DE RADIO
        - QScrollBar            BARRA DE SCROLL VERTICAL Y HORIZONTAL
        - QSlider               DESLIZADOR
        - QSpinBox              CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON UN BOTÓN
        - QSplitter             SPLITTER
        - QStatusBar            BARRA DE ESTADO
        - QTabWidget            SEPARADOR DE FORMULARIOS (PESTAÑAS)
        - QTableView            REJILLA
        - QTextEdit             CAJA DE EDICIÓN DE TEXTO MULTI-LINEA
        - QTimeEdit             CAJA DE EDICIÓN DE HORA
        - QTitanGrid            REJILLA AVANZADA
        - QToolBar              BARRA DE HERRAMIENTAS
        - QTooltip              TOOLTIP
        - QTreeView             QTREEVIEW - ARBOL VISOR Y MENU ARBOLADO
        - QWidget#qt_calendar   CALENDARIO
        - VBoundFieldEdit       CAJA DE EDICIÓN DE CAMPO PUNTERO A MAESTRO
        - VDateLineEdit         CAJA DE EDICIÓN DE CAMPO FECHA MODO DE EDICIÓN ALFANUMÉRICO
        - VCFootView            PIE DE REJILLA
        - VListBox              LISTBOX
    -------------------------------------------------------------------------------- */

/*  --------------------------------------------------------------------------------
    QCheckBox - BOTÓN CHECK
    QRadioButton - BOTÓN DE RADIO
    -------------------------------------------------------------------------------- */

QCheckBox, QRadioButton {
    color: #212121; }

QCheckBox:hover, QRadioButton:hover {
    border: 1px solid #757575; }

QCheckBox:focus, QRadioButton:focus {
    border: 2px solid #3F51B5; }

QCheckBox:disabled, QRadioButton:disabled {
    color: #BDBDBD; }

/*  --------------------------------------------------------------------------------
    QComboBox - COMBOBOX
    QDateEdit - CAJA DE EDICIÓN DE CAMPO FECHA MODO DE EDICIÓN FECHA
    QDateTime - CAJA DE VISUALIZACIÓN CAMPO FECHA Y HORA
    QDateTimeEdit - CAJA DE EDICIÓN DE CAMPO FECHA Y HORA
    QDoubleSpinBox - CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
    QNumberSpinBox - CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
    QSpinBox - CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON UN BOTÓN
    QTimeEdit - CAJA DE EDICIÓN DE HORA
    VBoundFieldEdit - CAJA DE EDICION DE CAMPO PUNTERO A MAESTRO
    VDateLineEdit - CAJA DE EDICIÓN DE CAMPO FECHA MODO DE EDICIÓN ALFANUMÉRICO
    VListBox - LISTBOX
    -------------------------------------------------------------------------------- */

QComboBox, QDateEdit, QDateTime, VDateLineEdit, QDateTimeEdit, QDoubleSpinBox, QNumberSpinBox, QSpinBox, QTimeEdit, VBoundFieldEdit, vBoundFieldEditBrowser, VListBox {
    background-color: #FFFFFF;
    border: 1px solid #E0E0E0;
    color: #212121;
    height: 30px;
    padding-left: 5px;
    padding-right: 0px;
    selection-background-color: #3F51B5;
    selection-color: #FFFFFF; }

QComboBox:hover, QDateEdit:hover, VDateLineEdit:hover, QDateTime:hover, QDateTimeEdit:hover, QDoubleSpinBox:hover, QNumberSpinBox:hover, QSpinBox:hover, QTimeEdit:hover, VBoundFieldEditBrowser:hover,VBoundFieldEdit:hover, VListBox:hover {
    border: 1px solid #757575; }

QComboBox:focus, QDateEdit:focus, VDateLineEdit:focus, QDateTime:focus, QDateTimeEdit:focus, QDoubleSpinBox:focus, QNumberSpinBox:focus, QSpinBox:focus, QTimeEdit:focus, VBoundFieldEditBrowser:focus, VBoundFieldEdit:focus, VListBox:focus {
    border: 2px solid #3F51B5; }

QComboBox:disabled, QDateEdit:disabled, VDateLineEdit:disabled, QDateTime:disabled, QDateTimeEdit:disabled, QDoubleSpinBox:disabled, QNumberSpinBox:disabled, QSpinBox:disabled, QTimeEdit:disabled, VBoundFieldEditBrowser:disabled,VBoundFieldEdit:disabled, VListBox:disabled {
    background-color: #BDBDBD; }

QDateEdit::up-button, QDateTime::up-button, VDateLineEdit::up-button, QDateTimeEdit::up-button, QTimeEdit::up-button, VBoundFieldEditBrowser::up-button, VBoundFieldEdit::up-button {
    border: none;
    image: url($sysCacheClientPath/ARR.png); }

QDateEdit::down-button, QDateTime::down-button, VDateLineEdit::down-button, QDateTimeEdit::down-button, QTimeEdit::down-button, VBoundFieldEditBrowser::down-button, VBoundFieldEdit::down-button {
    border: none;
    image: url($sysCacheClientPath/ABA.png); }

QComboBox::drop-down, QDateEdit::drop-down, VDateLineEdit::drop-down, QDateTime::drop-down, QDateTimeEdit::drop-down, VBoundFieldEditBrowser::drop-down, VBoundFieldEdit::drop-down, QTimeEdit::drop-down {
    border: none;
    image: url($sysCacheClientPath/ABA.png);
    width: 13px; }

/* EN PRINCIPIO NO AFECTAN */

QNumberSpinBox:up-button, QDoubleSpinBox:up-button, QSpinBox:up-button {
    border: none;
    image: url($sysCacheClientPath/ARR.png); }

QNumberSpinBox:down-button, QDoubleSpinBox:down-button, QSpinBox:down-button {
    border: none;
    image: url($sysCacheClientPath/ABA.png); }

/*  --------------------------------------------------------------------------------
    QDockWidget - DOCK
    -------------------------------------------------------------------------------- */

QDockWidget {
    color: #727272;
    font-size: 10px;
    min-width:10px;
    width: 210px; }

QDockWidget::title {
    background: #CECECE;
    margin: 0px;
    padding: 0px;
    text-align: center; }

QDockWidget::close-button {
    image: none;
    position: absolute;
    subcontrol-origin: margin;
    subcontrol-position: top right;
    top: 0px; left: 0px; bottom: 0px;
    width: 0px; }

QDockWidget::float-button {
    image: none;
    position: absolute;
    subcontrol-origin: margin;
    subcontrol-position: top right;
    top: 0px; left: 0px; bottom: 0px;
    width: 0px; }

/*  --------------------------------------------------------------------------------
    QFrame - MARCO
    -------------------------------------------------------------------------------- */

QFrame {
    background: #FFF;
    border: 0px; }

/*  --------------------------------------------------------------------------------
    QGroupBox - CAJA DE GRUPO
    -------------------------------------------------------------------------------- */

QGroupBox {
    border: 1px solid #E0E0E0;
    border-radius: 5px;
    font-size: 12px;
    font-weight: bold;
    margin-top: 20px;
    padding: 5px; }

QGroupBox::title {
    color: #212121;
    padding-top: -5px;
    padding-left: 5px;
    subcontrol-origin: margin; }

/*  --------------------------------------------------------------------------------
    QHeaderView - CABECERA DE REJILLAS Y ARBOLES
    -------------------------------------------------------------------------------- */

QHeaderView::section:horizontal {
    background: #FFF;
    border: none;
    border-bottom: 1px solid #BDBDBD;
    border-right: 2px solid #FFF;
    color: #212121;
    font-weight: normal;
    margin-top: 10px;
    min-height: 20px; }

/*  --------------------------------------------------------------------------------
    QLabel - ETIQUETA DE TEXTO
    -------------------------------------------------------------------------------- */

QLabel {
    background-color: transparent;
    color: #757575;
    font-size: 11px; }

/* TITULOS DE LOS FORMULARIOS Y MENUS */

QLabel#TXT_TIT {
    color: #757575;
    font-size: 24px; }

/*  --------------------------------------------------------------------------------
    QLineEdit - CAJA DE EDICIÓN DE TEXTO EN UNA LINEA
    QTextEdit - CAJA DE EDICIÓN DE TEXTO MULTI-LINEA
    -------------------------------------------------------------------------------- */

QLineEdit, QTextEdit {
    background-color: #FFFFFF;
    border: 1px solid #E0E0E0;
    color: #212121;
    height: 30px;
    padding-left: 2px;
    selection-background-color: #3F51B5;
    selection-color: #FFFFFF; }

QLineEdit:hover, QTextEdit:hover {
    border: 1px solid #757575; }

QLineEdit:focus, QTextEdit:focus {
    border: 2px solid #3F51B5; }

QLineEdit:disabled, QTextEdit:disabled {
    background-color: #BDBDBD; }

/* CONTROL DE EDICIÓN DE TEXTOS A BUSCAR EN MENUS */

QLineEdit#TXT_BUS {
    qproperty-placeholderText: 'Texto a buscar'; }

/*  --------------------------------------------------------------------------------
    QMainWindow - VENTANA PRINCIPAL
    -------------------------------------------------------------------------------- */

QMainWindow {
    background: #FFF;
    border: 0px; }

QMainWindow::separator {
    background: #FFF;
    border-left: 1px solid #CECECE;
    border-right: 1px solid #CECECE;
    height: 5px;
    width: 5px; }

QMainWindow::separator:hover {
    background: #CECECE; }

/*  --------------------------------------------------------------------------------
    QMenu - MENU CONTEXTUAL
    -------------------------------------------------------------------------------- */

QMenu {
    background-color: #FFF;
    border: 1px solid #CECECE;
    color: #333333;
    margin: 0px;
    padding: 5px; }

QMenu::item {
    border: 1px solid transparent;
    margin: 1px;
    padding: 5px 30px 5px 20px; }

QMenu::item:selected {
    background-color: #CECECE;
    color:#000; }

QMenu::separator {
    background-color: #CECECE;
    height: 1px;
    margin: 0px; }

/*  --------------------------------------------------------------------------------
    QMenuBar - BARRA DE MENU (Solo afecta a Windows)
    -------------------------------------------------------------------------------- */

QMenuBar {
    background-color: #FFF;
    border-bottom: 1px solid #CECECE;
    min-height:20px; }

QMenuBar::item:selected {
    background-color: #CECECE;
    color: #000; }

QMenuBar::item:hover {
    background: #333333;
    color: #FFF; }

/*  --------------------------------------------------------------------------------
    QMessageBox - VENTANA DE MENSAJE
    QDialog - VENTANA EN CUADRO DE DIÁLOGO
    -------------------------------------------------------------------------------- */

QMessageBox, QDialog {
    background-color: #FFF; }

QMessageBox QPushButton {
    min-width: 90px; }

/*  --------------------------------------------------------------------------------
    QProgressBar - BARRA DE PROGRESO
    -------------------------------------------------------------------------------- */

QProgressBar {
    background-color: #FFF;
    border: 1px solid #E0E0E0;
    border-radius: 2px;
    color: #212121;
    height: 30px;
    text-align: center; }

QProgressBar::chunk {
    background-color:#C5CAE9; }

/*  --------------------------------------------------------------------------------
    QPushButton - BOTÓN
    -------------------------------------------------------------------------------- */

QPushButton {
    background-color: #FFFFFF;
    border: 1px solid #E0E0E0;
    color: #212121;
    font-size: 12px;
    font-weight: bold;
    height: 30px;
    line-height: 16px;
    text-align: center;
    text-transform: uppercase;
    width: 90px;
    qproperty-iconSize: 18px; }

QPushButton:hover {
    border: 1px solid #3F51B5; }

QPushButton:focus {
    border: 2px solid #3F51B5; }

QPushButton:pressed {
    border: 2px solid #3F51B5; }

QPushButton:disabled {
    background-color: #9E9E9E;
    border: 1px solid #9E9E9E;
    color:  #FFF; }

QPushButton::menu-indicator {
    image: none;
    width: 0px; }

/* BOTÓN ACEPTAR */

QPushButton#BTN_ACE {
    background-color: #3F51B5;
    color: #FFF; }

QPushButton:hover#BTN_ACE {
    background-color: #FFFFFF;
    color: #3F51B5; }

QPushButton:focus#BTN_ACE {
    background-color: #FFFFFF;
    color: #3F51B5; }

QPushButton:pressed#BTN_ACE {
    background-color: #3F51B5;
    color: #FFF; }

QPushButton:disabled#BTN_ACE {
    background-color: #9E9E9E;
    border: 1px solid #9E9E9E;
    color:  #FFF; }

/* BOTÓN AMPLIAR, BOTÓN BUSCAR, BOTÓN MENU y BOTÓN REDUCIR */

QPushButton#BTN_AMP, QPushButton#BTN_BUS, QPushButton#BTN_MEN, QPushButton#BTN_RED {
    background-color: transparent;
    border: 1px solid #E0E0E0;
    border-radius: 0px;
    qproperty-iconSize: 18px; }

QPushButton:hover#BTN_AMP, QPushButton:hover#BTN_BUS, QPushButton:hover#BTN_MEN, QPushButton:hover#BTN_RED {
    border: 1px solid #3F51B5; }

QPushButton:focus#BTN_AMP, QPushButton:focus#BTN_BUS, QPushButton:focus#BTN_MEN, QPushButton:focus#BTN_RED {
    border: 2px solid #3F51B5; }

QPushButton:pressed#BTN_AMP, QPushButton:pressed#BTN_BUS, QPushButton:pressed#BTN_MEN, QPushButton:pressed#BTN_RED {
    border: 2px solid #3F51B5; }

QPushButton:disabled#BTN_AMP, QPushButton:disabled#BTN_BUS, QPushButton:disabled#BTN_MEN, QPushButton:disabled#BTN_RED {
    border: 1px solid #9E9E9E; }

/*  --------------------------------------------------------------------------------
    QScrollBar - BARRA DE SCROLL VERTICAL Y HORIZONTAL
    -------------------------------------------------------------------------------- */

QScrollBar:vertical {
    background: #FFF;
    padding-left: 2px;
    padding-right: 2px;
    width: 12px; }

QScrollBar:horizontal {
    background: #FFF;
    padding-bottom: 2px;
    padding-top: 2px;
    height: 12px;  }

QScrollBar::handle:vertical {
    background: #E0E0E0;
    border: 3px solid #E0E0E0;
    border-radius: 4px;
    min-height: 90px; }

QScrollBar::handle:horizontal {
    background: #E0E0E0;
    border: 3px solid #E0E0E0;
    border-radius: 4px;
    min-width: 90px; }

QScrollBar::handle:hover {
    background: #BDBDBD;
    border: 3px solid #BDBDBD; }

QScrollBar::add-line:vertical {
    border: none;
    background: none; }

QScrollBar::sub-line:vertical {
    border: none;
    background: none; }

QScrollBar::add-line:horizontal {
    border: none;
    background: none; }

QScrollBar::sub-line:horizontal {
    border: none;
    background: none; }

/*  --------------------------------------------------------------------------------
    QScrollBar - Recuadro de intersección entre las scrollbar horizontal y vertical
    -------------------------------------------------------------------------------- */

QAbstractScrollArea::corner {
    background: transparent;
    border: transparent; }

/*  --------------------------------------------------------------------------------
    QSlider - DESLIZADOR
    -------------------------------------------------------------------------------- */

QSlider {
    background: #FFF; }

/*  --------------------------------------------------------------------------------
    QSplitter - SPLITTER
    -------------------------------------------------------------------------------- */

QSplitter {
    background: transparent; }

/*  --------------------------------------------------------------------------------
    QStatusBar - BARRA DE ESTADO
    -------------------------------------------------------------------------------- */

QStatusBar {
    background-color: #FFF;
    border-top: 1px solid #CECECE;
    qproperty-visible: false; }

/*  --------------------------------------------------------------------------------
    QTabWidget - SEPARADOR DE FORMULARIOS (Pestañas)
    -------------------------------------------------------------------------------- */

QTabWidget::pane {
    border-top: 1px solid #CECECE;
    margin:0px;
    top: 10px; }

QTabWidget::tab-bar {
    left: 10px;
    min-width: 10px;
    right: 10px;
    top: 10px; }

QTabBar::tab {
    background-color: #FFF;
    border-bottom: 1px solid #FFF;
    border-left: 1px solid #EEEEEE;
    border-top: 1px solid #EEEEEE;
    border-right: 1px solid #EEEEEE;
    color: #212121;
    margin-left: 5px;
    padding-bottom: 3px;
    padding-left: 5px;
    padding-right: 0px;
    padding-top: 5px; }

QTabBar::tab:!selected {
    background-color: #EEEEEE;
