/*  --------------------------------------------------------------------------------
    QScrollBar - BARRA DE SCROLL VERTICAL Y HORIZONTAL
    -------------------------------------------------------------------------------- */

QScrollBar:vertical {
    background: #FFF;
    padding-left: 2px;
    padding-right: 2px;
    width: 12px; }

QScrollBar:horizontal {
    background: #FFF;
    padding-bottom: 2px;
    padding-top: 2px;
    height: 12px;  }

QScrollBar::handle:vertical {
    background: #E0E0E0;
    border: 3px solid #E0E0E0;
    border-radius: 4px;
    min-height: 90px; }

QScrollBar::handle:horizontal {
    background: #E0E0E0;
    border: 3px solid #E0E0E0;
    border-radius: 4px;
    min-width: 90px; }

QScrollBar::handle:hover {
    background: #BDBDBD;
    border: 3px solid #BDBDBD; }

QScrollBar::add-line:vertical {
    border: none;
    background: none; }

QScrollBar::sub-line:vertical {
    border: none;
    background: none; }

QScrollBar::add-line:horizontal {
    border: none;
    background: none; }

QScrollBar::sub-line:horizontal {
    border: none;
    background: none; }

/*  --------------------------------------------------------------------------------
    QScrollBar - Recuadro de intersección entre las scrollbar horizontal y vertical
    -------------------------------------------------------------------------------- */

QAbstractScrollArea::corner {
    background: transparent;
    border: transparent; }

/*  --------------------------------------------------------------------------------
    QSlider - DESLIZADOR
    -------------------------------------------------------------------------------- */

QSlider {
    background: #FFF; }

/*  --------------------------------------------------------------------------------
    QSplitter - SPLITTER
    -------------------------------------------------------------------------------- */

QSplitter {
    background: transparent; }

/*  --------------------------------------------------------------------------------
    QStatusBar - BARRA DE ESTADO
    -------------------------------------------------------------------------------- */

QStatusBar {
    background-color: #FFF;
    border-top: 1px solid #CECECE;
    qproperty-visible: false; }

/*  --------------------------------------------------------------------------------
    QTabWidget - SEPARADOR DE FORMULARIOS (Pestañas)
    -------------------------------------------------------------------------------- */

QTabWidget::pane {
    border-top: 1px solid #CECECE;
    margin:0px;
    top: 10px; }

QTabWidget::tab-bar {
    left: 10px;
    min-width: 10px;
    right: 10px;
    top: 10px; }

QTabBar::tab {
    background-color: #FFF;
