    border: 0px; }

QTableView::item:focus {
    selection-background-color: #3F51B5;
    selection-color: #FFF; }

/* PIE DE REJILLA */

VCFootView {
    background: #FFF;
    border: none;
    border-right: 2px solid #FFF;
    color: #212121;
    min-height: 20px; }

VCFootView::section:active {
    background: #FFF;
    border: none;
    border-right: 2px solid #FFF; }

VCFootView::section:!active {
    background: #FFF;
    border: none;
    border-right: 2px solid #FFF; }

/*  --------------------------------------------------------------------------------
    QTITAN_GRID - REJILLA AVANZADA
    -------------------------------------------------------------------------------- */

/* MARCO DE LA REJILLA */

Qtitan--Grid {
    border: 2px solid #3F51B5;              /* Parece que no se aplica */
