    selection-color: #FFF; }

/* PIE DE REJILLA */

VCFootView {
    background: #FFF;
    border: none;
    border-right: 2px solid #FFF;
    color: #212121;
    min-height: 20px; }

VCFootView::section:active {
    background: #FFF;
    border: none;
    border-right: 2px solid #FFF; }

VCFootView::section:!active {
    background: #FFF;
    border: none;
    border-right: 2px solid #FFF; }

/*  --------------------------------------------------------------------------------
