    border: 1px solid #E0E0E0;
    border-radius: 2px;
    color: #212121;
    height: 30px;
    text-align: center; }

QProgressBar::chunk {
    background-color:#C5CAE9; }

/*  --------------------------------------------------------------------------------
    QPushButton - BOTÓN
    -------------------------------------------------------------------------------- */

QPushButton {
    background-color: #FFFFFF;
    border: 1px solid #E0E0E0;
    border-radius: 5px;
    color: #212121;
    font-size: 12px;
    height: 30px;
    line-height: 16px;
    text-align: center;
    qproperty-iconSize: 18px;
    width: 120px; }

QPushButton:hover {
    border: 1px solid #3F51B5; }

QPushButton:focus {
    border: 2px solid #3F51B5; }

QPushButton:pressed {
    border: 2px solid #3F51B5; }

QPushButton:disabled {
    background-color: #9E9E9E;
    border: 1px solid #9E9E9E;
    color:  #FFF; }

QPushButton::menu-indicator {
    image: none;
    width: 0px; }

/* BOTÓN ACEPTAR */

QPushButton#BTN_ACE {
    background-color: #3F51B5;
    color: #FFF; }

QPushButton:hover#BTN_ACE {
    background-color: #FFFFFF;
    color: #3F51B5; }

QPushButton:focus#BTN_ACE {
    background-color: #FFFFFF;
    color: #3F51B5; }

QPushButton:pressed#BTN_ACE {
    background-color: #3F51B5;
    color: #FFF; }

QPushButton:disabled#BTN_ACE {
    background-color: #9E9E9E;
    border: 1px solid #9E9E9E;
    color:  #FFF; }

/* BOTÓN AMPLIAR, BOTÓN BUSCAR, BOTÓN MENU y BOTÓN REDUCIR */

QPushButton#BTN_AMP, QPushButton#BTN_BUS, QPushButton#BTN_MEN, QPushButton#BTN_RED {
    background-color: transparent;
    border: 1px solid #E0E0E0;
    border-radius: 5px;
    qproperty-iconSize: 18px; }

QPushButton:hover#BTN_AMP, QPushButton:hover#BTN_BUS, QPushButton:hover#BTN_MEN, QPushButton:hover#BTN_RED {
    border: 1px solid #3F51B5; }

QPushButton:focus#BTN_AMP, QPushButton:focus#BTN_BUS, QPushButton:focus#BTN_MEN, QPushButton:focus#BTN_RED {
    border: 2px solid #3F51B5; }

QPushButton:pressed#BTN_AMP, QPushButton:pressed#BTN_BUS, QPushButton:pressed#BTN_MEN, QPushButton:pressed#BTN_RED {
    border: 2px solid #3F51B5; }

QPushButton:disabled#BTN_AMP, QPushButton:disabled#BTN_BUS, QPushButton:disabled#BTN_MEN, QPushButton:disabled#BTN_RED {
    border: 1px solid #9E9E9E; }

/* BOTÓN CON TAMAÑO FIJO */

QPushButton#BTN_ACE, QPushButton#BTN_CNC, QPushButton#BTN_SUP, QPushButton#BTN_OPC {
    width: 120px; }

/*  --------------------------------------------------------------------------------
    QScrollBar - BARRA DE SCROLL VERTICAL Y HORIZONTAL
    -------------------------------------------------------------------------------- */

QScrollBar:vertical {
    background: #FFF;
    padding-left: 2px;
    padding-right: 2px;
    width: 12px; }

QScrollBar:horizontal {
    background: #FFF;
    padding-bottom: 2px;
    padding-top: 2px;
    height: 12px;  }

QScrollBar::handle:vertical {
    background: #E0E0E0;
    border: 3px solid #E0E0E0;
    border-radius: 4px;
    min-height: 90px; }

QScrollBar::handle:horizontal {
    background: #E0E0E0;
    border: 3px solid #E0E0E0;
    border-radius: 4px;
    min-width: 90px; }

QScrollBar::handle:hover {
    background: #BDBDBD;
    border: 3px solid #BDBDBD; }

QScrollBar::add-line:vertical {
    border: none;
    background: none; }

QScrollBar::sub-line:vertical {
    border: none;
    background: none; }

QScrollBar::add-line:horizontal {
    border: none;
    background: none; }

QScrollBar::sub-line:horizontal {
    border: none;
    background: none; }

/*  --------------------------------------------------------------------------------
    QScrollBar - Recuadro de intersección entre las scrollbar horizontal y vertical
    -------------------------------------------------------------------------------- */

QAbstractScrollArea::corner {
    background: transparent;
    border: transparent; }

/*  --------------------------------------------------------------------------------
    QSlider - DESLIZADOR
    -------------------------------------------------------------------------------- */

QSlider {
    background: #FFF; }

/*  --------------------------------------------------------------------------------
    QSplitter - SPLITTER
    -------------------------------------------------------------------------------- */

QSplitter {
    background: transparent; }

/*  --------------------------------------------------------------------------------
    QStatusBar - BARRA DE ESTADO
    -------------------------------------------------------------------------------- */

QStatusBar {
    background-color: #FFF;
    border-top: 1px solid #CECECE;
    qproperty-visible: false; }

/*  --------------------------------------------------------------------------------
    QTabWidget - SEPARADOR DE FORMULARIOS (Pestañas)
    -------------------------------------------------------------------------------- */

QTabWidget::pane {
    border-top: 1px solid #CECECE;
    margin:0px;
    top: 10px; }

QTabWidget::tab-bar {
    left: 10px;
    min-width: 10px;
    right: 10px;
    top: 10px; }

QTabBar::tab {
    background-color: #FFF;
    border-bottom: 1px solid #FFF;
    border-left: 1px solid #EEEEEE;
    border-top: 1px solid #EEEEEE;
    border-right: 1px solid #EEEEEE;
    color: #212121;
    margin-left: 5px;
    padding-bottom: 3px;
    padding-left: 5px;
    padding-right: 0px;
    padding-top: 5px; }

QTabBar::tab:!selected {
    background-color: #EEEEEE;
    border-bottom: 1px solid #EEEEEE;
    color: #424242; }

QTabBar::close-button {
    image: url($sysCacheClientPath/CRR.png);
    margin: 3px; }

QTabBar::scroller {
    padding-top: 10px;
    width:15px;}

QTabBar QToolButton::left-arrow {
    image: url($sysCacheClientPath/IZQ.png);
    width:15px;}

QTabBar QToolButton::right-arrow {
    image: url($sysCacheClientPath/DER.png);
    width:15px;}

/*  --------------------------------------------------------------------------------
    QTableView - REJILLA
    VCFootView - PIE DE REJILLA
    -------------------------------------------------------------------------------- */

/* CABECERA DE REJILLA */

QTableView {
    alternate-background-color: transparent;
    background-color: #FFF;
    border: none;
    border-bottom: 1px solid #BDBDBD;
    font-weight: normal;
    gridline-color: transparent;
    selection-background-color: #3F51B5;
    selection-color: #FFF;
    qproperty-showGrid: false; }

QTableView QTableCornerButton::section {
    background-color: #FFF;
    border: 0px; }

QTableView::item:focus {
    selection-background-color: #3F51B5;
    selection-color: #FFF; }

/* PIE DE REJILLA */

VCFootView {
    background: #FFF;
    border: none;
    border-right: 2px solid #FFF;
    color: #212121;
    min-height: 20px; }

VCFootView::section:active {
    background: #FFF;
    border: none;
    border-right: 2px solid #FFF; }

VCFootView::section:!active {
    background: #FFF;
    border: none;
    border-right: 2px solid #FFF; }

/*  --------------------------------------------------------------------------------
    QTITAN_GRID - REJILLA AVANZADA
    -------------------------------------------------------------------------------- */

/* MARCO DE LA REJILLA */

Qtitan--Grid {
    border: 2px solid darkBlue;             /* Parece que no se aplica */
    padding: 2px;
    margin: 3px;
    border-style: native; }

/* PALETA DE COLORES DE LOS AGRUPAMIENTOS */

Qtitan--Grid {
    color: black;                           /* Triángulo para desplegar agrupaciones */
    selection-background-color: #3F51B5;    /* Background del registro seleccionado */
    selection-color: white;                 /* Color del texto del registro seleccionado */
    alternate-background-color: white;      /* Parece que no funciona */
    background-color: white; }              /* Fondo de la rejilla */

/* COLOR DE LA LÍNEA DE LA REJILLA */

Qtitan--Grid {
    gridline-color: #EEEEEE; }              /* Líneas de la rejilla */

/* CABECERAS */

Qtitan--Grid::section {
    background-color: #FFFFFF;              /* Cabecera y bloque para multi-selección */
    color: red;                             /* Parece que no se aplica */
