/* CABECERA CON FOCO */

Qtitan--Grid::section:focus {
    background-color: #FFFFFF;              /* Columna seleccionada al pulsar en un campo */
    border: 1px solid #BDBDBD;              /* Líneas de la columna seleccionada al pulsar en un campo */
    color: #FFFFFF; }                       /* Parece que no se aplica */

/*  --------------------------------------------------------------------------------
    QToolBar - BARRA DE HERRAMIENTAS
    -------------------------------------------------------------------------------- */

QToolBar {
    background-color: transparent;
    border: 0px;
    padding: 3px;
    spacing: 10px;
    qproperty-iconSize: 18px; }

QToolButton {
    background-color: #FFF;
    border: 1px solid #E0E0E0;
    border-radius: 5px;
    color: #727272;
    margin-right: 1px;
    min-height: 18px;
    min-width: 18px;
    padding: 5px;
    qproperty-iconSize: 18px; }

QToolButton:hover {
    border: 1px solid #3F51B5;
    border-radius: 5px; }

QToolButton:focus {
    border: 2px solid #3F51B5;
    border-radius: 5px; }

QToolButton:disabled {
    background-color: #CECECE;
    border: 1px solid #727272;
    color: #727272; }

QToolButton:pressed {
    border: 2px solid #3F51B5;
    border-radius: 5px; }

QToolButton::menu-indicator {
    background-color: transparent;
    color: transparent; }

/*  --------------------------------------------------------------------------------
    QTooltip - TOOLTIP
    -------------------------------------------------------------------------------- */

QToolTip {
    align: center;
    background-color: #FFFFFF;
    border: 1px solid #727272;
    color: #333333;
    vertical-align:center; }

/*  --------------------------------------------------------------------------------
    QTreeView - ARBOL VISOR Y MENU ARBOLADO
    -------------------------------------------------------------------------------- */

QTreeView {
    outline: 0;
    background-color: #FFF;
    selection-background-color: transparent;
    show-decoration-selected: 1; }

QTreeView::item {
    border-right: 0;
