    margin-bottom:2px;
    margin-right:10px;
    margin-top: 1px;
    padding-bottom:2px;
    padding-top:1px; }

QTreeView::item:hover {
    border-color: transparent;
    color: #212121; }

QTreeView::item:selected {
    background-color: #3F51B5;
    color: #FFF; }

/* MEN_APP - MENU GENERAL ARBOLADO */

QTreeView::item:selected#MEN_APP {
    background-color: transparent;
    border-color: transparent;
    color: #212121; }

/*  --------------------------------------------------------------------------------
    QCalendarWidget - CALENDARIO
