    background: #CECECE;
    border: 1px solid #CECECE;
    qproperty-icon: url($sysCacheClientPath/IZQ.png); }

QWidget#qt_calendar_monthbutton, QWidget#qt_calendar_yearbutton {
    background: #CECECE;
    border: 1px solid #CECECE; }

QCalendarWidget  {
    min-width: 300px;
    min-height: 260px;}

QCalendarWidget QSpinBox {
    width: 35px;
    font-size:12px;
    color: #212121;
