    padding-left: 5px;
    padding-right: 5px;
    padding-top: 5px; }

QTabBar::tab:hover {
    border-top: 1px solid $color-fondo-focus; }

QTabBar::tab:selected {
    color: $color-texto-seleccionado;
    border-bottom: 2px solid $color-borde-focus; }

QTabBar::close-button {
    image: url($sysCacheClientPath/CRR.png);
    margin: 3px; }

QTabBar::scroller {
    padding-top: 10px;
    width: 64px;}

QTabBar QToolButton::left-arrow {
    image: url($sysCacheClientPath/IZQ.png);
    width: 32px;}

QTabBar QToolButton::right-arrow {
    image: url($sysCacheClientPath/DER.png);
    width: 32px;}

/*  --------------------------------------------------------------------------------
    QTableView - REJILLA
    VCFootView - PIE DE REJILLA
    -------------------------------------------------------------------------------- */

/* CABECERA DE REJILLA */

QTableView {
    alternate-background-color: transparent;
    background-color: white;
    border: none;
    border-bottom: 1px solid #BDBDBD;
    color: $color-texto-rejilla-detalle;
    font-weight: normal;
    gridline-color: transparent;
    min-height: 20px;
    selection-background-color: $color-fondo-texto;
