QTextEdit {
	background-color: $color-fondo-edicion;
	border: $size-borde solid $color-borde-normal;
	color: $color-texto-edicion;
	font-size: $alto-fuente-m;
	padding: $padding-qtextedit;
	selection-background-color: $color-fondo-texto-seleccionado;
	selection-color: $color-texto-seleccionado;
}

QTextEdit:hover {
	border: $size-borde solid $color-borde-hover;
}

QTextEdit:focus {
	border: $size-borde-seleccionado solid $color-borde-focus;
}

QTextEdit:disabled {
	background-color: $color-fondo-disabled;
	color: $color-texto-defecto;
}

/*--------------------------------------------------------------------------------
    QMainWindow - VENTANA PRINCIPAL
--------------------------------------------------------------------------------*/

QMainWindow {
	background: $color-fondo-general;
	border: 0px;
}

QMainWindow::separator {
	background: $color-fondo-general;
	border-left: 0px solid $color-borde-defecto;
	border-right: $size-borde solid $color-borde-defecto;
