    /*  --------------------------------------------------------------------------------
        QTITAN_GRID - REJILLA AVANZADA
        -------------------------------------------------------------------------------- */

    /* MARCO DE LA REJILLA */

    Qtitan--Grid {
        border: 2px solid #3F51B5;              /* Parece que no se aplica */
