datos.CSS:
/*  --------------------------------------------------------------------------------
    VERSION: 25.0
    AUTOR: VELNEO
    ULTIMA MODIFICACION: 18 Abril 2019

    INDICE
        - QCalendarWidget       CALENDARIO
        - QCheckBox             BOTON CHECK
        - QComboBox             COMBOBOX
        - QDateEdit             CAJA DE EDICIÓN DE CAMPO FECHA MODO DE EDICIÓN FECHA
        - QDateTime             CAJA DE VISUALIZACIÓN CAMPO FECHA Y HORA
        - QDateTimeEdit         CAJA DE EDICIÓN DE CAMPO FECHA Y HORA
        - QDialog               VENTANA EN CUADRO DE DIÁLOGO
        - QDockWidget           DOCK
        - QDoubleSpinBox        CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
        - QFrame                MARCO
        - QGroupBox             CAJA DE GRUPO
        - QHeaderView           CABECERA DE REJILLAS Y ÁRBOLES
        - QLabel                ETIQUETA DE TEXTO
        - QLineEdit             CAJA DE EDICIÓN DE TEXTO EN UNA LINEA
        - QMainWindow           VENTANA PRINCIPAL
        - QMenu                 MENU CONTEXTUAL
        - QMenuBar              BARRA DE MENU (Solo afecta a Windows)
        - QMessageBox           VENTANA DE MENSAJE
        - QNumberSpinBox        CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
        - QProgressBar          BARRA DE PROGRESO
        - QPushButton           BOTÓN
        - QRadioButton          BOTÓN DE RADIO
        - QScrollBar            BARRA DE SCROLL VERTICAL Y HORIZONTAL
        - QSlider               DESLIZADOR
        - QSpinBox              CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON UN BOTÓN
        - QSplitter             SPLITTER
        - QStatusBar            BARRA DE ESTADO
        - QTabWidget            SEPARADOR DE FORMULARIOS (PESTAÑAS)
        - QTableView            REJILLA
        - QTextEdit             CAJA DE EDICIÓN DE TEXTO MULTI-LINEA
        - QTimeEdit             CAJA DE EDICIÓN DE HORA
        - QTitanGrid            REJILLA AVANZADA
        - QToolBar              BARRA DE HERRAMIENTAS
        - QTooltip              TOOLTIP
        - QTreeView             QTREEVIEW - ARBOL VISOR Y MENU ARBOLADO
        - QWidget#qt_calendar   CALENDARIO
        - VBoundFieldEdit       CAJA DE EDICIÓN DE CAMPO PUNTERO A MAESTRO
        - VDateLineEdit         CAJA DE EDICIÓN DE CAMPO FECHA MODO DE EDICIÓN ALFANUMÉRICO
        - VCFootView            PIE DE REJILLA
        - VListBox              LISTBOX
    -------------------------------------------------------------------------------- */

/*  --------------------------------------------------------------------------------
    QCheckBox - BOTÓN CHECK
    QRadioButton - BOTÓN DE RADIO
    -------------------------------------------------------------------------------- */

    QCheckBox, QRadioButton {
        color: #F07676; }

    QCheckBox:focus, QRadioButton:focus {
        border: 2px solid #F07676; }

    QCheckBox:disabled, QRadioButton:disabled {
        color: #BDBDBD; }

    /*  --------------------------------------------------------------------------------
        QComboBox - COMBOBOX
        QDateEdit - CAJA DE EDICIÓN DE CAMPO FECHA MODO DE EDICIÓN FECHA
        QDateTime - CAJA DE VISUALIZACIÓN CAMPO FECHA Y HORA
        QDateTimeEdit - CAJA DE EDICIÓN DE CAMPO FECHA Y HORA
        QDoubleSpinBox - CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
        QNumberSpinBox - CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON BOTONES ARRIBA Y ABAJO
        QSpinBox - CAJA DE EDICIÓN DE CAMPO NUMÉRICO CON UN BOTÓN
        QTimeEdit - CAJA DE EDICIÓN DE HORA
        VBoundFieldEdit - CAJA DE EDICION DE CAMPO PUNTERO A MAESTRO
        VDateLineEdit - CAJA DE EDICIÓN DE CAMPO FECHA MODO DE EDICIÓN ALFANUMÉRICO
        VListBox - LISTBOX
        -------------------------------------------------------------------------------- */

    QComboBox, QDateEdit, VDateLineEdit, QDateTime, QDateTimeEdit, QDoubleSpinBox, QNumberSpinBox, QSpinBox, QTimeEdit, VBoundFieldEdit, vBoundFieldEditBrowser, VListBox {
        background-color: #E7DDDD;
        border: 1px solid #E7DDDD;
        color: #474747;
        height: 20px;
        padding-left: 1px;
        padding-right: 0px;
        selection-background-color: #FFF;
        border-radius:3%;
        selection-color: #474747; }

    QComboBox:hover, QDateEdit:hover, VDateLineEdit:hover, QDateTime:hover, QDateTimeEdit:hover, QDoubleSpinBox:hover, QNumberSpinBox:hover, QSpinBox:hover, QTimeEdit:hover, VBoundFieldEditBrowser:hover,VBoundFieldEdit:hover, VListBox:hover {
        border: 1px solid #F07676; }

    QComboBox:focus, QDateEdit:focus, VDateLineEdit:focus, QDateTime:focus, QDateTimeEdit:focus, QDoubleSpinBox:focus, QNumberSpinBox:focus, QSpinBox:focus, QTimeEdit:focus, VBoundFieldEditBrowser:focus, VBoundFieldEdit:focus, VListBox:focus {
        border: 2px solid #F07676; }

    QComboBox:disabled, QDateEdit:disabled, VDateLineEdit:disabled, QDateTime:disabled, QDateTimeEdit:disabled, QDoubleSpinBox:disabled, QNumberSpinBox:disabled, QSpinBox:disabled, QTimeEdit:disabled, VBoundFieldEditBrowser:disabled,VBoundFieldEdit:disabled, VListBox:disabled {
        background-color: #BDBDBD; }

    QDateEdit::up-button, QDateTime::up-button, VDateLineEdit::up-button, QDateTimeEdit::up-button, QTimeEdit::up-button, VBoundFieldEditBrowser::up-button, VBoundFieldEdit::up-button {
        border: none;
        image: url($sysCacheClientPath/ARR.png); }

    QDateEdit::down-button, QDateTime::down-button, VDateLineEdit::down-button, QDateTimeEdit::down-button, QTimeEdit::down-button, VBoundFieldEditBrowser::down-button, VBoundFieldEdit::down-button {
        border: none;
        image: url($sysCacheClientPath/ABA.png); }

    QComboBox::drop-down, QDateEdit::drop-down, VDateLineEdit::drop-down, QDateTime::drop-down, QDateTimeEdit::drop-down, VBoundFieldEditBrowser::drop-down, VBoundFieldEdit::drop-down, QTimeEdit::drop-down {
        border: none;
        image: url($sysCacheClientPath/ABA.png);
        width: 10px; }

    /* EN PRINCIPIO NO AFECTAN */

    QNumberSpinBox:up-button, QDoubleSpinBox:up-button, QSpinBox:up-button {
        border: none;
        image: url($sysCacheClientPath/ARR.png); }

    QNumberSpinBox:down-button, QDoubleSpinBox:down-button, QSpinBox:down-button {
        border: none;
        image: url($sysCacheClientPath/ABA.png); }

    QComboBox QAbstractItemView{
        border: 2px solid #F07676;
        color: #474747;
        background-color: #E7DDDD;
        selection-background-color: #F06767;
        border-top-left-radius: 4px;
        border-top-right-radius: 4px;
        border-bottom-left-radius: 4px;
        border-bottom-right-radius: 4px;
        box-shadow: transparent;
    }

    /*--------------------------------------------------------------------------------
        QDockWidget - DOCK
        -------------------------------------------------------------------------------- */

    QDockWidget {
        color: #727272;
        font-size: 10px;
        min-width:10px;
        width: 210px;
    }

    QDockWidget#MEN_APP{
        background: #FFF;
    }

    QDockWidget::title {
        background: #CECECE;
        margin: 0px;
        padding: 0px;
        text-align: center; }

    QDockWidget::close-button {
        image: none;
        position: absolute;
        subcontrol-origin: margin;
        subcontrol-position: top right;
        top: 0px; left: 0px; bottom: 0px;
        width: 0px; }

    QDockWidget::float-button {
        image: none;
        position: absolute;
        subcontrol-origin: margin;
        subcontrol-position: top right;
        top: 0px; left: 0px; bottom: 0px;
        width: 0px; }

    /*  --------------------------------------------------------------------------------
        QFrame - MARCO
        -------------------------------------------------------------------------------- */

    QFrame {
        background: #FFF;
        border: 0px; }

    /*  --------------------------------------------------------------------------------
        QGroupBox - CAJA DE GRUPO
        -------------------------------------------------------------------------------- */

    QGroupBox {
        border: 1px solid #E0E0E0;
        border-radius: 5px;
        font-size: 12px;
        font-weight: bold;
        margin-top: 20px;
        padding: 5px; }

    QGroupBox::title {
        color: #212121;
        padding-top: -5px;
        padding-left: 5px;
        subcontrol-origin: margin; }

    /*  --------------------------------------------------------------------------------
        QHeaderView - CABECERA DE REJILLAS Y ARBOLES
        -------------------------------------------------------------------------------- */

    QHeaderView::section:horizontal {
        background: #FFF;
        border: none;
        border-bottom: 1px solid #BDBDBD;
        border-right: 2px solid #FFF;
        color: #212121;
        font-weight: normal;
        margin-top: 10px;
        min-height: 20px; }

    /*  --------------------------------------------------------------------------------
        QLabel - ETIQUETA DE TEXTO
        -------------------------------------------------------------------------------- */

    QLabel {
        background-color: transparent;
        color: #474747;
        font-style: oblique;
        font-size: 12px; }

    /* TITULOS DE LOS FORMULARIOS Y MENUS */

    QLabel#TXT_TIT {
        color: #F07676;
        font-weight: bold;
        font-style: normal;
        font-size: 14px; }

     QLabel#TXT_TIT_MES{
         color: #F07676;
         font-weight: bold;
         font-style: normal;
         font-size: 18px;}

     QLabel#TXT_TIT_FAM{
         color: #F07676;
         font-weight: bold;
         font-style: normal;
         font-size: 18px;}

      QLabel#TXT_TIT_DIA{
         color: #F07676;
         font-weight: bold;
         font-style: normal;
         font-size: 18px;}

      QLabel#TXT_TIT_WEEK{
         color: #F07676;
         font-weight: bold;
         font-style: normal;
         font-size: 18px;}

    /*  --------------------------------------------------------------------------------
        QLineEdit - CAJA DE EDICIÓN DE TEXTO EN UNA LINEA
        QTextEdit - CAJA DE EDICIÓN DE TEXTO MULTI-LINEA
        -------------------------------------------------------------------------------- */

    QLineEdit, QTextEdit {
        background-color: #E7DDDD;
        border: 1px solid #E7DDDD;
        border-radius: 3%;
        color: #212121;
        height: 30px;
        padding-left: 2px;
        padding-right: 2px;
        selection-background-color: #FFF;
        selection-color: #474747; 
    }

    QLineEdit:hover, QTextEdit:hover {
        border: 1px solid #F07676; }

    QLineEdit:focus, QTextEdit:focus {
        border: 2px solid #F07676; }

    QLineEdit:disabled, QTextEdit:disabled {
        background-color: #BDBDBD; }

    /* CONTROL DE EDICIÓN DE TEXTOS A BUSCAR EN MENUS */

    QLineEdit#TXT_BUS {
        qproperty-placeholderText: 'Texto a buscar'; }

    /*  --------------------------------------------------------------------------------
        QMainWindow - VENTANA PRINCIPAL
        -------------------------------------------------------------------------------- */

    QMainWindow {
        background: #FFF;
        border: 0px; }

    QMainWindow::separator {
        background: #F2F3F3;
        height: 1px;
        width: 1px; }

    QMainWindow::separator:hover {
        background: #F07676; }

    /*  --------------------------------------------------------------------------------
        QMenu - MENU CONTEXTUAL
        -------------------------------------------------------------------------------- */

    QMenu {
        background-color: #FFF;
        border: 1px solid #CECECE;
        color: #474747;
        margin: 0px;
        padding: 5px; }

    QMenu::item {
        border: 1px solid transparent;
        margin: 1px;
        padding: 5px 30px 5px 20px; }

    QMenu::item:selected {
        background-color: #CECECE;
        color:#000; }

    QMenu::separator {
        background-color: #CECECE;
        height: 1px;
        margin: 0px; }

    /*  --------------------------------------------------------------------------------
        QMenuBar - BARRA DE MENU (Solo afecta a Windows)
        -------------------------------------------------------------------------------- */

    QMenuBar {
        background-color: #FFF;
        border-bottom: 1px solid #CECECE;
        min-height:10px; }

    QMenuBar::item:selected {
        background-color: #CECECE;
        color: #000; }

    QMenuBar::item:hover {
        background: #333333;
        color: #FFF; }

    /*  --------------------------------------------------------------------------------
        QMessageBox - VENTANA DE MENSAJE
        QDialog - VENTANA EN CUADRO DE DIÁLOGO
        -------------------------------------------------------------------------------- */

    QMessageBox, QDialog {
        background-color: #FFF; }

    QMessageBox QPushButton {
        min-width: 90px; }

    /*  --------------------------------------------------------------------------------
        QProgressBar - BARRA DE PROGRESO
        -------------------------------------------------------------------------------- */

    QProgressBar {
        background-color: #FFF;
        border: 1px solid #E0E0E0;
        border-radius: 2px;
        color: #474747;
        height: 30px;
        text-align: center; }

    QProgressBar::chunk {
        background-color:#F07676; }

    /*  --------------------------------------------------------------------------------
        QPushButton - BOTÓN
        -------------------------------------------------------------------------------- */

    QPushButton {
        background-color: #FFF;
        border: 1px solid teal;
        border-radius: 4%;
        color: teal;
        font-size: 12px;
        font-weight: bold;
        height: 20px;
        line-height: 16px;
        text-align: center;
        text-transform: uppercase;
        width: 90px;
        qproperty-iconSize: 10px; }

    QPushButton:hover {
        background: teal;
        color: #FFF;
        border: 1px solid teal;
    }  

    QPushButton:focus {
        background: teal;
        color: #FFF;
        border: 2px solid teal;
    }

    QPushButton:pressed {
        background: teal;
        color: #474747;
        border: 2px solid teal;
    }   

    QPushButton:disabled {
        background-color: #9E9E9E;
        border: 1px solid #9E9E9E;
        color:  #FFF;
    }

    QPushButton::menu-indicator {
        image: none;
        width: 0px;
    }

    /* BOTÓN ACEPTAR */

    QPushButton#BTN_ACE {
        background-color: #3fb5a1;
        color: #FFF;
        border-color: #3fb5a1;
        /*transition: background-color 2s;*/
    }

    QPushButton:hover#BTN_ACE {
        background-color: #43bfaa;
        color: #474747;
        border: 3px solid #43bfaa;
    }

    QPushButton:focus#BTN_ACE {
        background-color: #43bfaa;
        color: #000000; }

    QPushButton:pressed#BTN_ACE {
        background-color: #43bfaa;
        color: #474747; }

    QPushButton:disabled#BTN_ACE {
        background-color: #9E9E9E;
        border: 1px solid #9E9E9E;
        color:  #F2F3F3; }


    /* BOTÓN CANCELAR */

    QPushButton#BTN_CLR, QPushButton#BTN_CNC {
        background-color: #b84f4f;
        color: #FFF;
        border-color: #b84f4f;
    }

    QPushButton:hover#BTN_CLR, QPushButton:hover#BTN_CNC {
        background-color: #c95555;
        color: #474747;
        border: 3px solid #c95555;
    }

    QPushButton:focus#BTN_CLR, QPushButton:focus#BTN_CNC {
        background-color: #c95555;
        color: #474747;
    }

    QPushButton:pressed#BTN_CLR, QPushButton:pressed#BTN_CNC {
        background-color: #c95555;
        color: #000000; 
    }

    QPushButton:disabled#BTN_CLR, QPushButton:disabled#BTN_CNC {
        background-color: #9E9E9E;
        border: 3px solid #9E9E9E;
        color:  #F2F3F3; }

           /* BOTÓN AMPLIAR, BOTÓN BUSCAR, BOTÓN MENU y BOTÓN REDUCIR */

    QPushButton#BTN_AMP, QPushButton#BTN_BUS, QPushButton#BTN_MEN, QPushButton#BTN_RED {
        background-color: transparent;
        border: none !important;
        qproperty-iconSize: 18px; }

    QPushButton:hover#BTN_AMP, QPushButton:hover#BTN_BUS, QPushButton:hover#BTN_MEN, QPushButton:hover#BTN_RED {
        border-radius: 4px;
        background-color: #E7DDDD;
    }    

    QPushButton:focus#BTN_AMP, QPushButton:focus#BTN_BUS, QPushButton:focus#BTN_MEN, QPushButton:focus#BTN_RED {
        border-radius: 4px;
        background-color: #E7DDDD; 
    }

    QPushButton:pressed#BTN_AMP, QPushButton:pressed#BTN_BUS, QPushButton:pressed#BTN_MEN, QPushButton:pressed#BTN_RED {
        border-radius: 4px;
        background-color: #E7DDDD; 
    }

    QPushButton:disabled#BTN_AMP, QPushButton:disabled#BTN_BUS, QPushButton:disabled#BTN_MEN, QPushButton:disabled#BTN_RED {
        border: 1px solid #9E9E9E; 
    }

    /*  --------------------------------------------------------------------------------
        QScrollBar - BARRA DE SCROLL VERTICAL Y HORIZONTAL
        -------------------------------------------------------------------------------- */

    QScrollBar:vertical {
        background: transparent;
        padding-left: 2px;
        padding-right: 2px;
        width: 12px; }

    QScrollBar:horizontal {
        background: transparent;
        padding-bottom: 2px;
        padding-top: 2px;
        height: 12px;  }

    QScrollBar::handle:vertical {
        background: #474747;
        border: 3px solid #474747;
        border-radius: 4px;
        min-height: 90px; }

    QScrollBar::handle:horizontal {
        background: #474747;
        border: 3px solid #474747;
        border-radius: 4px;
        min-width: 90px; }

    QScrollBar::handle:hover {
        background: #F07676;
        border: 3px solid #F07676; }

    QScrollBar::add-line:vertical {
        border: none;
        background: none; }

    QScrollBar::sub-line:vertical {
        border: none;
        background: none; }

    QScrollBar::add-line:horizontal {
        border: none;
        background: none; }

    QScrollBar::sub-line:horizontal {
        border: none;
        background: none; }

         /*  --------------------------------------------------------------------------------
        QScrollBar - Recuadro de intersección entre las scrollbar horizontal y vertical
        -------------------------------------------------------------------------------- */

    QAbstractScrollArea::corner {
        background: transparent;
        border: transparent; }

    /*  --------------------------------------------------------------------------------
        QSlider - DESLIZADOR
        -------------------------------------------------------------------------------- */

    QSlider {
        background: transparent;
        margin-top: 10%;
    }

    QSlider::handle:horizontal{
        background: #E7DDDD;
        border: 3px solid #F07676;

        border-radius: 7px;
    }

    QSlider::handle:horizontal:hover{
        background: #474747;
        border: 3px solid #F07676;

        border-radius: 7px;
    }

    QSlider::add-page:qlineargradient{
        background: #474747;
        border-top-right-radius: 5px;
        border-bottom-right-radius: 5px;
        border-top-left-radius: 0px;
        border-bottom-left-radius: 0px;
    }

    QSlider::sub-page:qlineargradient{
        background: #E7DDDD;
        border-top-right-radius: 0px;
        border-bottom-right-radius: 0px;
        border-top-left-radius: 5px;
        border-bottom-left-radius: 5px;
    }    

    /*  --------------------------------------------------------------------------------
        QSplitter - SPLITTER
        -------------------------------------------------------------------------------- */

    QSplitter {
        background: #F2F3F3; }

    /*  --------------------------------------------------------------------------------
        QStatusBar - BARRA DE ESTADO
        -------------------------------------------------------------------------------- */

    QStatusBar {
        background-color: #FFF;
        border-top: 1px solid #CECECE;
        qproperty-visible: false; }

    /*  --------------------------------------------------------------------------------
        QTabWidget - SEPARADOR DE FORMULARIOS (Pestañas)
        -------------------------------------------------------------------------------- */

    QTabWidget::pane {
        border-top: 1px solid #CECECE;
        margin:0px;
        top: 2px; }

    QTabWidget::tab-bar {
        left: 10px;
        min-width: 10px;
        right: 10px;
        top: 3px; }

    QTabBar::tab {
        background-color: #FFF;
        border-left: 1px solid #EEEEEE;
        border-top: 1px solid #EEEEEE;
        border-right: 1px solid #EEEEEE;
        color: #212121;
        margin-left: 5px;
        padding-bottom: 1px;
        padding-left: 5px;
        padding-right: 5px;
        padding-top: 1px;
        border-top-left-radius: 12px;
        border-top-right-radius: 12px;
    }

    QTabBar::tab:!selected {
        background-color: #EEEEEE;
        border-bottom: 1px solid #EEEEEE;
        color: #474747; }

    QTabBar::tab:selected {
        background-color: #E7DDDD;
        border-left: 3px solid #E7DDDD;
        border-top: 3px solid #E7DDDD;
        border-right: 3px solid #E7DDDD;
        border-bottom: 1px solid #EEEEEE;
        color: #474747;
    }


    QTabBar::tab:selected:hover {
        background-color: #E7DDDD;
        border-left: 3px solid #F07676;
        border-top: 3px solid #F07676;
        border-right: 3px solid #F07676;
        border-bottom: 1px solid #EEEEEE;
        color: #F07676;
    }

    QTabBar::tab:!selected:hover {
        background-color: #E7DDDD;
        border-left: 3px solid #E7DDDD;
        border-top: 3px solid #E7DDDD;
        border-right: 3px solid #E7DDDD;
        border-bottom: 1px solid #EEEEEE;
        color: #474747;
    }

    QTabBar::close-button {
        image: url();
        margin: 3px
    }

    QTabBar::close-button:hover {
        image: url($sysCacheClientPath/CRR.png);
        margin: 3px;
    }

    QTabBar::scroller {
        padding-top: 10px;
        width:15px;}

    QTabBar QToolButton::left-arrow {
        image: url($sysCacheClientPath/IZQ.png);
        width:15px;}

    QTabBar QToolButton::right-arrow {
        image: url($sysCacheClientPath/DER.png);
        width:15px;}

    /*  --------------------------------------------------------------------------------
        QTableView - REJILLA
        VCFootView - PIE DE REJILLA
        -------------------------------------------------------------------------------- */

    /* CABECERA DE REJILLA */

    QTableView {
        alternate-background-color: transparent;
        background-color: transparent;
        border: none;
        border-bottom: 1px solid #BDBDBD;
        font-weight: normal;
        gridline-color: transparent;
        selection-background-color: #E7DDDD;
        selection-color: #474747;
        qproperty-showGrid: false; }

    QTableView::indicator:unchecked{
        background-color: transparent;
        border: none;
    }

    QTableView::indicator:checked{
        background-color: #F07676;
        color: #474747;
        border: none;
    }

    QTableView QTableCornerButton::section {
