        color: #474747;
        font-style: oblique;
        font-size: 12px; }

    /* TITULOS DE LOS FORMULARIOS Y MENUS */

    QLabel#TXT_TIT {
        color: #F07676;
        font-weight: bold;
        font-style: normal;
        font-size: 14px; }

     QLabel#TXT_TIT_MES{
         color: #F07676;
