    /*  --------------------------------------------------------------------------------
        QPushButton - BOTÓN
        -------------------------------------------------------------------------------- */

    QPushButton {
        background-color: #FFF;
        border: 1px solid teal;
        border-radius: 4%;
        color: teal;
        font-size: 12px;
        font-weight: bold;
        height: 20px;
        line-height: 16px;
        text-align: center;
        text-transform: uppercase;
        width: 90px;
        qproperty-iconSize: 10px; }

    QPushButton:hover {
        background: teal;
