    QTableView::indicator:unchecked{
        background-color: transparent;
        border: none;
    }

    QTableView::indicator:checked{
        background-color: #F07676;
        color: #474747;
        border: none;
    }

    QTableView QTableCornerButton::section {
        background-color: #FFF;
        border: 0px; }

    QHeaderView::section{
        color: #F07676;
        font-weight: bold;
    }

