/*--------------------------------------------------------------------------------
    QCheckBox - BOTÓN CHECK
    QRadioButton - BOTÓN DE RADIO
--------------------------------------------------------------------------------*/

/* - - - - - - - - - - - - - - - - QCheckBox - BOTÓN CHECK - - - - - - - - - - - - - - - -*/

QCheckBox {
    background: $checkbox-fondo-color;
    border: $checkbox-borde-size solid $checkbox-borde-color;
    color: $checkbox-texto-color;
    font-family: $checkbox-fuente-familia;
    font-size: $checkbox-fuente-size;
    height: $checkbox-alto;
    margin: $checkbox-margen;
    spacing: 0;
    text-decoration: none;
    outline: none;
}

QCheckBox:hover {
    color: $checkbox-texto-color-hover;
}

QCheckBox:disabled {
    background: $checkbox-fondo-color;
    color: $checkbox-texto-color-disabled;
}

QCheckBox::indicator {
    margin-right: $checkbox-margen-indicador;
    position: left;
