--------------------------------------------------------------------------------*/

QComboBox {
	background-color: $color-fondo-edicion;
	border: $size-borde solid $color-borde-normal;
	color: $color-texto-edicion;
	font-size: $alto-fuente-m;
	height: $alto-referencia;
	padding-left: $padding-combo-izquierda;
	padding-right: $padding-combo-derecha;
	selection-background-color: $color-elemento-seleccionado-combobox;
	selection-color: $color-texto-seleccionado-combobox;
	width: $ancho-referencia;
}

QComboBox:hover {
	border: $size-borde solid $color-borde-hover;
}

QComboBox:focus {
	border: $size-borde-seleccionado solid $color-borde-focus;
}

QComboBox:disabled {
	background-color: $color-fondo-disabled;
	color: $color-texto-disabled;
}

QComboBox::drop-down {
	border: none;
	image: url($sysCacheClientPath/ABA.png);
	width: $ancho-icono-combobox;
}

QComboBox QAbstractItemView {
	border: $size-borde solid $color-borde-linea;
}

/*--------------------------------------------------------------------------------
    QDockWidget - DOCK
--------------------------------------------------------------------------------*/

QDockWidget {
	color: transparent;
	font-size: 10px;
	min-width: 10px;
	width: 210px;
}

QDockWidget::title {
	background: $color-fondo-general;
	color: $color-texto-estatico;
	margin: 0px;
	padding: 0px;
	text-align: center;
}

QDockWidget::close-button {
	image: none;
	position: absolute;
	subcontrol-origin: margin;
	subcontrol-position: top right;
	top: 0px;
	left: 0px;
	bottom: 0px;
	width: 0px;
}

QDockWidget::float-button {
	image: none;
	position: absolute;
	subcontrol-origin: margin;
	subcontrol-position: top right;
	top: 0px;
	left: 0px;
	bottom: 0px;
	width: 0px;
}

/*--------------------------------------------------------------------------------
    QFrame - MARCO
--------------------------------------------------------------------------------*/

QFrame {
	background: $color-fondo-general;
	border: 0px;
}

/*--------------------------------------------------------------------------------
    QGroupBox - CAJA DE GRUPO
--------------------------------------------------------------------------------*/

QGroupBox {
	border: $size-borde solid $color-borde-normal;
	border-radius: 5px;
	font-size: 12px;
	font-weight: bold;
	margin-top: 20px;
	padding: 5px;
}

QGroupBox::title {
	color: $color-texto-edicion;
	padding-top: -5px;
	padding-left: 5px;
	subcontrol-origin: margin;
}

/*--------------------------------------------------------------------------------
    QHeaderView - CABECERA DE REJILLAS Y ARBOLES
--------------------------------------------------------------------------------*/

QHeaderView::section {
	background: $color-fondo-general;
	border: none;
	border-bottom: $size-borde solid $color-borde-linea;
	color: $color-texto-rejilla-cabecera;
	font-size: $alto-fuente-s;
	font-weight: bold;
	/* margin-right: $space-controls; */
	/* Posible incidencia en detalle con márgenes */
	min-height: $alto-referencia;
	padding-left: 2px;
	/* Posible incidencia en detalle que siempre aplica 2px a izquierda y derecha del texto */
	padding-right: 2px;
}

/*QHeaderView::section:vertical:checked {
	image: url($sysCacheClientPath/ABA.png);
}*/

/*QHeaderView[toggleVertical=true]::section:vertical:checked {
	image: url($sysCacheClientPath/ABA.png);
}*/

QHeaderView::down-arrow {
	image: url($sysCacheClientPath/ABA.png);
}

QHeaderView::up-arrow {
	image: url($sysCacheClientPath/ARR.png);
}

QHeaderView::section:horizontal:uncheked {
	padding-right: 2px;
}

QHeaderView::section:horizontal:checked {
	padding-right: 20px;
}

/* Posible incidencia en detalle que siempre aplica 2px a izquierda y derecha del texto */

/*--------------------------------------------------------------------------------
    QLabel - ETIQUETA DE TEXTO
--------------------------------------------------------------------------------*/

QLabel {
	background-color: $color-fondo-general;
	color: $color-texto-estatico;
}

/* TITULOS DE LOS FORMULARIOS Y MENUS */

QLabel#TXT_TIT {
	color: $color-texto-edicion;
	font-size: $alto-fuente-xl;
}

QLabel#EMP {
	color: $color-texto-estatico;
	font-size: $alto-fuente-m;
	text-transform: uppercase;
}

QLabel#USR {
	color: $color-texto-estatico;
	font-size: $alto-fuente-m;
}

/*--------------------------------------------------------------------------------
    QLineEdit - CAJA DE EDICIÓN DE TEXTO EN UNA LINEA
--------------------------------------------------------------------------------*/

QLineEdit {
	background-color: $color-fondo-edicion;
	border: $size-borde solid $color-borde-normal;
	color: $color-texto-edicion;
	font-size: $alto-fuente-m;
	height: $alto-referencia;
	padding-left: $padding-qline-izquierda;
	selection-background-color: $color-fondo-texto-seleccionado;
	selection-color: $color-texto-seleccionado;
}

QLineEdit:hover {
	border: $size-borde solid $color-borde-hover;
}

QLineEdit:focus {
	border: $size-borde-seleccionado solid $color-borde-focus;
	padding-left: $padding-qline-izquierda-resaltado;
}

QLineEdit:disabled {
	background-color: $color-fondo-disabled;
	color: $color-texto-defecto;
}

/* CONTROL DE EDICIÓN DE TEXTOS A BUSCAR EN MENUS */

QLineEdit#TXT_BUS {
	qproperty-placeholderText: 'Texto a buscar';
}

/* FORMULARIO ALTA CONTACTO > TEXTO EDIT NOMBRE Y NOMBRE COMERCIAL */

QLineEdit#NAME_CON {
	qproperty-placeholderText: 'Nombre contacto';
	qproperty-text: 'Nombre contacto';
}

/*--------------------------------------------------------------------------------
    QTextEdit - CAJA DE EDICIÓN DE TEXTO MULTI-LINEA
--------------------------------------------------------------------------------*/

QTextEdit {
	background-color: $color-fondo-edicion;
	border: $size-borde solid $color-borde-normal;
	color: $color-texto-edicion;
	font-size: $alto-fuente-m;
	padding: $padding-qtextedit;
	selection-background-color: $color-fondo-texto-seleccionado;
	selection-color: $color-texto-seleccionado;
}

QTextEdit:hover {
	border: $size-borde solid $color-borde-hover;
}

QTextEdit:focus {
	border: $size-borde-seleccionado solid $color-borde-focus;
}

QTextEdit:disabled {
	background-color: $color-fondo-disabled;
	color: $color-texto-defecto;
}

/*--------------------------------------------------------------------------------
    QMainWindow - VENTANA PRINCIPAL
--------------------------------------------------------------------------------*/

QMainWindow {
	background: $color-fondo-general;
	border: 0px;
}

QMainWindow::separator {
	background: $color-fondo-general;
	border-left: 0px solid $color-borde-defecto;
	border-right: $size-borde solid $color-borde-defecto;
	height: 5px;
	width: 5px;
}

QMainWindow::separator:hover {
	background: $color-borde-hover;
}

/*--------------------------------------------------------------------------------
    QMenu - MENU CONTEXTUAL
--------------------------------------------------------------------------------*/

QMenu {
	background-color: $color-fondo-general;
	border: $size-borde solid $color-borde-normal;
	color: $color-texto-defecto;
	padding: $ancho-minimo;
}

QMenu::icon {
	background: transparent;
	/*background: $color-fondo-general;*/
	qproperty-iconSize: $size-icono;
}

QMenu::item {
	border: $size-borde solid $color-fondo-general;
	height: $alto-referencia;
	padding-left: $space-controls;
	padding-right: $ancho-boton-dibujo;
}

QMenu::item:disabled {
	color: $color-texto-disabled;
}

QMenu::item:hover {
	color: $color-texto-menu-hover;
}

QMenu::item:selected {
	background-color: $color-fondo-menu-item;
	color: $color-texto-menu-seleccionado;
}

QMenu::separator {
	background-color: $color-borde-normal;
	height: $size-borde;
	margin-top: $ancho-minimo;
	margin-bottom: $ancho-minimo;
}

QMenu::indicator {
	height: $ancho-minimo;
	width: $ancho-minimo;
}

QMenu::right-arrow {
	height: $size-icono;
	image: url($sysCacheClientPath/SIG.png);
	width: $size-icono;
}

QMenu::left-arrow {
	height: $size-icono;
	image: url($sysCacheClientPath/ANT.png);
	width: $size-icono;
}

/*--------------------------------------------------------------------------------
    QMenuBar - BARRA DE MENU (Solo afecta a Windows)
--------------------------------------------------------------------------------*/

QMenuBar {
