	border-bottom: 1px solid #808080;
	min-height: 20px;
	color: #DFDFDF;
}

QMenuBar::item:selected {
	background-color: #D8D8D8;
	color: #3F3F3F;
}

QMenuBar::item:hover {
	background: #D8D8D8;
	color: #3F3F3F;
}

