/*--------------------------------------------------------------------------------
    QScrollBar - BARRA DE SCROLL VERTICAL Y HORIZONTAL
--------------------------------------------------------------------------------*/

/* SCROLL VERTICAL Y HORIZONTAL */

QScrollBar:vertical {
	background: $color-fondo-barra-scroll-normal;
	border: $size-borde solid $color-fondo-barra-scroll-normal;
	border-radius: 0px;
	padding-left: $size-borde-seleccionado;
	padding-right: $size-borde-seleccionado;
	margin: 0px 0px 0px 0px;
	width: $ancho-minimo-scroll;
}

QScrollBar:horizontal {
	background: $color-fondo-barra-scroll-normal;
	border: $size-borde solid $color-fondo-barra-scroll-normal;
	border-radius: 0px;
	padding-bottom: $size-borde-seleccionado;
	padding-top: $size-borde-seleccionado;
	margin: 0px 0px 0px 0px;
	height: $ancho-minimo-scroll;
}

/* MANEJADOR DE LA BARRA DE SCROLL */

QScrollBar::handle:vertical {
	background: $color-barra-scroll-normal;
	border: $size-borde solid $color-barra-scroll-normal;
	border-radius: 0px;
	min-height: $ancho-referencia;
}

QScrollBar::handle:horizontal {
	background: $color-barra-scroll-normal;
	border: $size-borde solid $color-barra-scroll-normal;
	border-radius: 0px;
	min-width: $ancho-minimo;
}

QScrollBar::hover {
	background: $color-fondo-barra-scroll-normal;
	border: $size-borde solid $color-barra-scroll-normal;
}

QScrollBar::handle:hover {
	background: $color-barra-scroll-hover;
	border: $size-borde solid $color-barra-scroll-hover;
}

/* BOTONES EN LOS EXTREMOS DE LA BARRA DE SCROLL */

QScrollBar::add-line:vertical {
	background: $color-fondo-barra-scroll-normal;
	border: $size-borde solid $color-fondo-barra-scroll-normal;
	height: $ancho-borde-sin;
	subcontrol-origin: margin;
	subcontrol-position: bottom;
	width: $ancho-borde-sin;
}

QScrollBar::add-line:horizontal {
	background: $color-fondo-barra-scroll-normal;
	border: $size-borde solid $color-fondo-barra-scroll-normal;
	height: $ancho-minimo;
	subcontrol-origin: margin;
	subcontrol-position: right;
	width: $ancho-minimo;
}

QScrollBar::sub-line:vertical {
	background: $color-fondo-barra-scroll-normal;
	border: $size-borde solid $color-fondo-barra-scroll-normal;
	height: $ancho-borde-sin;
	subcontrol-position: top;
	subcontrol-origin: margin;
	width: $ancho-borde-sin;
}

QScrollBar::sub-line:horizontal {
	background: $color-fondo-barra-scroll-normal;
	border: $size-borde solid $color-fondo-barra-scroll-normal;
	height: $ancho-minimo;
	subcontrol-origin: margin;
	subcontrol-position: left;
	width: $ancho-minimo;
}

/*--------------------------------------------------------------------------------
    QScrollBar - Recuadro de intersección entre las scrollbar horizontal y vertical
--------------------------------------------------------------------------------*/

QAbstractScrollArea::corner {
	background: transparent;
	border: transparent;
}

/*--------------------------------------------------------------------------------
    QSlider - DESLIZADOR
--------------------------------------------------------------------------------*/

QSlider {
	background: transparent;
}

/*--------------------------------------------------------------------------------
    QSplitter - SPLITTER
--------------------------------------------------------------------------------*/

QSplitter {
	background: $color-fondo-general;
}

/*--------------------------------------------------------------------------------
    QStatusBar - BARRA DE ESTADO
--------------------------------------------------------------------------------*/

QStatusBar {
	background-color: $color-fondo-general;
	border-top: 1px solid $color-borde-normal;
	qproperty-visible: false;
}

/*--------------------------------------------------------------------------------
    QTabWidget - SEPARADOR DE FORMULARIOS (Pestañas)
--------------------------------------------------------------------------------*/

QTabWidget::pane {
	border-top: 1px solid $color-borde-linea;
	margin: 0px;
	top: 10px;
}

QTabWidget::tab-bar {
	left: 10px;
	min-width: 10px;
	right: 10px;
	top: 10px;
}

QTabBar {
	font-weight: normal;
	font-size: $alto-fuente-s;
	text-transform: uppercase;
}

QTabBar::tab {
	background-color: $color-fondo-general;
	color: $color-texto-tab;
	/*margin-left: 5px;*/
	margin-right: 10px;
	padding-bottom: 3px;
	padding-left: 5px;
	padding-right: 5px;
	padding-top: 5px;
}

QTabBar::tab:hover {
	border-bottom: $size-borde-seleccionado solid $color-borde-tab-hover;
}

QTabBar::tab:selected {
	color: $color-texto-tab-seleccionado;
	border-bottom: $size-borde-seleccionado solid $color-borde-tab-seleccionada;
}

QTabBar::close-button {
	image: url($sysCacheClientPath/CRR.png);
	margin: 3px;
}

QTabBar::scroller {
	padding-top: 10px;
	width: 64px;
}

QTabBar QToolButton::left-arrow {
	image: url($sysCacheClientPath/IZQ.png);
	width: 32px;
}

QTabBar QToolButton::right-arrow {
	image: url($sysCacheClientPath/DER.png);
	width: 32px;
}

/*--------------------------------------------------------------------------------
    QTableView - REJILLA
    VCFootView - PIE DE REJILLA
--------------------------------------------------------------------------------*/

/* REJILLA */

QTableView {
	alternate-background-color: $color-fondo-general;
	background-color: $color-fondo-general;
	border: none;
	border-bottom: $size-borde solid $color-borde-defecto;
	color: $color-texto-rejilla-detalle;
	font-weight: normal;
	gridline-color: $color-fondo-general;
	margin-right: $space-controls;
	min-height: $alto-referencia;
	/* Posible incidencia, parece que no funciona */
	padding-left: $space-controls;
	/* Posible incidencia, parece que no funciona */
	padding-right: $space-controls;
	/* Posible incidencia, parece que no funciona */
	qproperty-showGrid: $grid-rejilla;
	selection-background-color: $color-fondo-rejilla-fila-focus;
	selection-color: $color-texto-rejilla-fila-focus;
}

QTableView QTableCornerButton::section {
	background-color: $color-fondo-general;
	border: none;
	border-bottom: $size-borde solid $color-borde-linea;
}

/* PIE DE REJILLA */

VCFootView {
	background: $color-fondo-general;
	border: none;
	color: $color-texto-rejilla-pie;
	font-size: $alto-fuente-s;
	font-weight: bold;
	margin-right: $space-controls;
	min-height: $alto-referencia;
	padding-left: $space-controls;
	padding-right: $space-controls;
}

/*--------------------------------------------------------------------------------
    QTitanGrid - REJILLA AVANZADA
--------------------------------------------------------------------------------*/

/* Marco de la rejilla */
Qtitan--Grid {
	border-bottom: $size-borde solid $color-borde-defecto;
	color: $color-texto-rejilla-detalle;
	gridline-color: $color-sistema-gris-medio;
	margin: $space-controls;
	padding: $space-controls;

	/* Color de fondo y fondo par de filas */
	background-color: $color-fondo-general;
	/* Fondo de la rejilla */
	/*alternate-background-color: $color-fondo-disabled;*/
	/* Color de las líneas impares */

	/* Color de la fila seleccionada */
	/* Background del registro seleccionado */
	selection-background-color: $color-sistema-gris-claro;
	/* Color del texto del registro seleccionado */
	selection-color: $color-sistema-gris-oscuro;

	/* Área de los agrupamientos */
	qproperty-headerTextColor: $color-texto-seleccionado;
	/* Texto informativo de la cabecera de agrupamiento */
	qproperty-headerBrush: $color-fondo-general;
	/* Color de la franja de agrupamiento */
	qproperty-headerBorderColor: $color-fondo-general;
	/* Color del borde */
	qproperty-headerBorderSize: $size-borde;
	/* Tamaño del borde */
	qproperty-headerTextFontSize: $alto-fuente-s;
	/* Tamaño de la fuente */

	/* Agrupamientos */
	/* Color de fondo según el nivel de agrupamiento */
	qproperty-groupColorLevel0: $color-texto-rejilla-agrupamiento;
	qproperty-groupColorLevel1: $color-borde-linea;
	qproperty-groupColorLevel2: $color-texto-rejilla-agrupamiento;
	qproperty-groupColorLevel3: $color-borde-linea;
	qproperty-groupColorLevel4: $color-texto-rejilla-agrupamiento;
	qproperty-groupColorLevel5: $color-borde-linea;
	qproperty-groupColorLevel6: $color-texto-rejilla-agrupamiento;
	qproperty-groupColorLevel7: $color-borde-linea;

	/* Nivel de despliegue de agrupamientos */
	qproperty-keepGroupLevelExpanded: 3;

	/* Pies de grupo */
	qproperty-summaryGroupItemTextColor: $color-texto-rejilla-pie;
	/* No está operativo en la versión 26 */
	qproperty-summaryGroupItemBackgroundColor: $color-sistema-producto-oscuro;
	qproperty-summaryGroupItemHeight: $alto-referencia;
	qproperty-summaryGroupItemBorderColor: $color-sistema-producto-oscuro;
	qproperty-summaryGroupItemBorderSize: $ancho-borde-sin;

	/* Pies de rejilla */
	qproperty-summaryItemTextColor: $color-texto-rejilla-pie;
	/* No está operativo en la versión 26 */
	qproperty-summaryItemBackgroundColor: $color-sistema-producto-oscuro;
	qproperty-summaryItemHeight: $alto-referencia;
	qproperty-summaryItemBorderColor: $color-sistema-producto-oscuro;
	qproperty-summaryItemBorderSize: $ancho-borde-sin;

	/* Línea de columnas inmovilizadas */
	qproperty-fixedSeparatorWidth: 3;
	qproperty-fixedSeparatorColor: $color-texto-seleccionado;
}

/* Cabecera y banda */
Qtitan--Grid::section {
	color: $color-texto-rejilla-cabecera;
	background-color: $color-fondo-general;
	border-bottom: $size-borde solid $color-borde-defecto;
}

/* Cabecera con foco */
Qtitan--Grid::section:focus {
	background-color: $color-fondo-general;
}

/* Celdas */
Qtitan--Grid::item {
	/* Se aplica sobre los posibles colores asignados genéricamente en la rejilla al fondo o fondo par */
	/*background-color: $color-fondo-general;*/
	border-bottom: $size-borde solid $color-borde-disabled;
	color: $color-texto-rejilla-detalle;
}

/* Celda con foco */
Qtitan--Grid::item:focus {
	/* Se aplica sobre los posibles colores asignados genéricamente en la rejilla al fondo o fondo par */
	background-color: $color-sistema-blanco;
	border: $size-borde solid $color-borde-disabled;
	color: $color-texto-rejilla-fila-focus;
}

/* Marco del foco y cabecera seleccionada de agrupación */
Qtitan--Grid:focus {
	outline: $size-borde dotted $color-texto-seleccionado;
}

/* Filtro rápido */
Qtitan--Grid>Qtitan--GridColumnFilterPopup,
Qtitan--Grid>Qtitan--GridColumnFilterPopup * {
	background-color: $color-borde-transparente;
	color: $color-texto-rejilla-cabecera;
}

/* Selección de campos */
Qtitan--Grid>Qtitan--GridColumnsQuickCustomization,
Qtitan--Grid>Qtitan--GridColumnsQuickCustomization * {
	background-color: $color-borde-transparente;
	color: $color-texto-rejilla-cabecera;
}

/*--------------------------------------------------------------------------------
    QToolBar - BARRA DE HERRAMIENTAS
--------------------------------------------------------------------------------*/

QToolBar {
	background-color: transparent;
	border: 0px;
	padding: 0px;
	spacing: 10px;
	qproperty-iconSize: $size-icono;
}

QToolButton {
	background-color: $color-boton-icono-fondo;
	border: $size-borde solid $color-boton-icono-borde;
	color: $color-texto-estatico;
	margin: $margen-sin;
	min-height: $alto-referencia;
	min-width: $alto-referencia;
	padding: 0px;
	qproperty-iconSize: $size-icono;
}

QToolButton:hover {
	background-color: $color-boton-icono-fondo-hover;
	border: $size-borde solid $color-boton-icono-borde-hover;
}

QToolButton:focus {
	background-color: $color-boton-icono-fondo-focus;
	border: $size-borde-seleccionado solid $color-boton-icono-borde-focus;
}

QToolButton:pressed {
	background-color: $color-boton-icono-fondo-pressed;
	border: $size-borde-seleccionado solid $color-boton-icono-borde-pressed;
}

QToolButton:disabled {
	/* background-color: $color-fondo-boton-normal;
    border: $size-borde solid $color-borde-disabled;
    color: $color-texto-boton-disabled; */
}

QToolButton::menu-indicator {
	background-color: transparent;
	color: transparent;
}

QToolBar QToolButton {
	background-color: $color-boton-toolbar-fondo;
	border: $size-borde solid $color-boton-toolbar-borde;
	color: $color-texto-estatico;
	margin: $margen-sin;
	min-height: 25;
	min-width: 25;
	padding: 0px;
	qproperty-iconSize: $size-icono;
}

QToolBar QToolButton:hover {
	background-color: $color-boton-toolbar-fondo-hover;
	border: $size-borde solid $color-boton-toolbar-borde-hover;
}

QToolBar QToolButton:focus {
	background-color: $color-boton-toolbar-fondo-focus;
	border: $size-borde-seleccionado solid $color-boton-toolbar-borde-focus;
}

QToolBar QToolButton:pressed {
	background-color: $color-boton-toolbar-fondo-pressed;
	border: $size-borde-seleccionado solid $color-boton-toolbar-borde-pressed;
}

/*--------------------------------------------------------------------------------
    QTooltip - TOOLTIP
--------------------------------------------------------------------------------*/

QToolTip {
	align: center;
	background-color: $color-sistema-fondo;
	border: $size-borde solid $color-borde-tooltip;
	color: $color-texto-edicion;
	vertical-align: center;
}

/*--------------------------------------------------------------------------------
    QTreeView - ARBOL VISOR Y MENU ARBOLADO
--------------------------------------------------------------------------------*/

QTreeView {
	outline: 0;
	background-color: $color-fondo-general;
	font-size: $alto-fuente-m;
	selection-background-color: transparent;
	selection-color: $color-texto-treewidget-seleccionado;
	show-decoration-selected: 1;
}

QTreeView::item {
	border-right: 0;
	color: $color-texto-treewidget;
	margin-bottom: 2px;
	margin-right: 10px;
	margin-top: 1px;
	padding-bottom: 2px;
	padding-top: 1px;
}

QTreeView::item:hover {
	border-color: transparent;
	color: $color-texto-treewidget-hover;
}

QTreeView::item:selected {
	color: $color-texto-treewidget-seleccionado;
}

QTreeView::branch:closed:has-children:!has-siblings,
QTreeView::branch:closed:has-children:has-siblings {
	image: url($sysCacheClientPath/DER.png);
}

QTreeView::branch:open:has-children:!has-siblings,
QTreeView::branch:open:has-children:has-siblings {
	image: url($sysCacheClientPath/ABA.png);
}

/*--------------------------------------------------------------------------------
    QCalendarWidget - CALENDARIO
    QWidget#qt_calendar - CALENDARIO
--------------------------------------------------------------------------------*/

QWidget#qt_calendar_navigationbar {
	background-color: $color-fondo-general;
	qproperty-gridVisible: false;
}

QWidget#qt_calendar_nextmonth {
	background: $color-fondo-general;
	border: $size-borde solid $color-fondo-general;
	qproperty-icon: url($sysCacheClientPath/DER.png);
}

QWidget#qt_calendar_prevmonth {
	background: $color-fondo-general;
	border: $size-borde solid $color-fondo-general;
	qproperty-icon: url($sysCacheClientPath/IZQ.png);
}

QWidget#qt_calendar_monthbutton,
QWidget#qt_calendar_yearbutton {
	background: $color-fondo-general;
	border: $size-borde solid $color-fondo-general;
	color: $color-texto-defecto;
}

QCalendarWidget {
	min-width: 300px;
	min-height: 260px;
}

QCalendarWidget QSpinBox {
	width: 35px;
	font-size: 12px;
	color: $color-texto-edicion;
	background-color: $color-fondo-general;
}

QCalendarWidget QSpinBox::up-button {
	subcontrol-origin: border;
	subcontrol-position: top right;
	width: 30px;
}

QCalendarWidget QSpinBox::down-button {
	subcontrol-origin: border;
	subcontrol-position: bottom right;
	width: 30px;
}

/*  --------------------------------------------------------------------------------
    VCOverlayShadow - OVERLAY 
    -------------------------------------------------------------------------------- */

VCOverlayShadow {
background-color: rgba(255, 255, 255, 5%); }

VCOverlayShadowGui {
background-color: rgba(255, 255, 255, 5%); }

