}

QDockWidget::title {
    background: $dock-fondo-color;
    color: $dock-texto-color;
    margin: 0px;
    padding: 0px;
    text-align: left;
}

QDockWidget::close-button {
    image: none;
    position: absolute;
    subcontrol-origin: margin;
    subcontrol-position: top right;
    top: 0px;
    left: 0px;
    bottom: 0px;
    width: 0px;
}

QDockWidget::float-button {
    image: none;
    position: absolute;
    subcontrol-origin: margin;
    subcontrol-position: top right;
    top: 0px;
    left: 0px;
    bottom: 0px;
    width: 0px;
}

