}

QTextEdit:focus {
    border: $qtextedit-borde-seleccionado-size solid $qtextedit-borde-color-focus;
}

QTextEdit:disabled {
    background-color: $qtextedit-fondo-color-disabled;
    color: $qtextedit-texto-color-disabled;
}

/*--------------------------------------------------------------------------------
    QMainWindow - VENTANA PRINCIPAL
--------------------------------------------------------------------------------*/

QMainWindow {
    background: $ventana-fondo-color;
    border: 0px;
    font-family: $ventana-fuente-familia;
    font-size: $ventana-fuente-size;
}

QMainWindow::separator {
    background: $ventana-separador-color;
    border-left: 0px solid $ventana-borde-color;
    border-right: $ventana-borde-size solid $ventana-borde-color;
    height: 5px;
    width: 5px;
}

QMainWindow::separator:hover {
    background: $ventana-separador-fondo-color;
}

/*--------------------------------------------------------------------------------
    QMenu - MENU CONTEXTUAL
--------------------------------------------------------------------------------*/

QMenu {
    background-color: $menu-fondo-color;
    border: $menu-borde-size solid $menu-borde-color;
    color: $menu-texto-color;
    font-family: $menu-fuente-familia;
    font-size: $menu-fuente-size;
    padding: $menu-padding;
}

QMenu::icon {
    background: transparent;
    qproperty-iconSize: $menu-size-icono;
}

QMenu::item {
    border: $menu-borde-size solid $menu-item-borde-color;
    height: $menu-item-alto;
    padding-left: $menu-padding;
    padding-right: $menu-item-padding;
}

QMenu::item:disabled {
    color: $menu-texto-color-disabled;
}

QMenu::item:hover {
    background-color: $menu-fondo-color-item;
    color: $menu-texto-color-hover;
}

QMenu::item:selected {
    background-color: $menu-fondo-color-item;
    color: $menu-texto-color-seleccionado;
}

QMenu::separator {
    background-color: $menu-separador-color;
    height: $menu-separador-alto;
    margin-top: $menu-margen;
    margin-bottom: $menu-margen;
}

QMenu::indicator {
    height: $menu-icono-size;
    width: $menu-icono-size;
}

QMenu::right-arrow {
    height: $menu-size-icono-flecha;
    image: url($sysCacheClientPath/SIG.png);
    width: $menu-size-icono-flecha;
}

QMenu::left-arrow {
    height: $menu-size-icono-flecha;
    image: url($sysCacheClientPath/ANT.png);
    width: $menu-size-icono-flecha;
}

/*--------------------------------------------------------------------------------
    QMenuBar - BARRA DE MENU (Solo afecta a Windows)
--------------------------------------------------------------------------------*/

QMenuBar {
    background-color: #3F3F3F;
    border-bottom: 1px solid #808080;
    min-height: 20px;
    color: #DFDFDF;
}

QMenuBar::item:selected {
    background-color: #D8D8D8;
    color: #3F3F3F;
}

QMenuBar::item:hover {
    background: #D8D8D8;
    color: #3F3F3F;
}

/*--------------------------------------------------------------------------------
    QMessageBox - VENTANA DE MENSAJE
    QDialog - VENTANA EN CUADRO DE DIÁLOGO
--------------------------------------------------------------------------------*/

QMessageBox,
QDialog {
    background-color: $dialogo-fondo-color;
}

QMessageBox QLabel {
    font-size: $label-fuente-mensaje-size;
    font-weight: $label-fuente-mensaje-peso;
    text-transform: $label-texto-mensaje-transformacion;
}

QMessageBox QPushButton {
    min-width: 90px;
}

/*--------------------------------------------------------------------------------
    QProgressBar - BARRA DE PROGRESO
--------------------------------------------------------------------------------*/

QProgressBar {
    background-color: $progressbar-fondo-color;
    border: $progressbar-borde-size solid $progressbar-borde-color;
    border-radius: 2px;
    color: $progressbar-texto-color;
    font-family: $progressbar-fuente-familia;
    font-size: $progressbar-fuente-size;
    height: 30px;
    text-align: center;
}

QProgressBar::chunk {
    background-color: #C5CAE9;
}

/*--------------------------------------------------------------------------------
    QPushButton - BOTÓN
--------------------------------------------------------------------------------*/

QPushButton {
    background-color: $boton-fondo-color-defecto;
    border: $boton-borde-size solid $boton-borde-color-defecto;
    border-radius: $boton-borde-radius;
    color: $boton-texto-color-defecto;
    font-family: $boton-fuente-familia;
    font-size: $boton-fuente-size;
    font-weight: $boton-fuente-peso;
    height: $boton-alto;
    outline: 0;
    text-align: $boton-texto-alineacion;
    text-transform: $boton-texto-transformacion;
    width: $boton-ancho;
    qproperty-iconSize: $boton-icono-size;
}

QPushButton:hover {
    background-color: $boton-fondo-color-hover;
    border: $boton-borde-size solid $boton-borde-color-hover;
    color: $boton-texto-color-hover;
}

QPushButton:focus {
    background-color: $boton-fondo-color-focus;
    border: $boton-borde-seleccionado-size solid $boton-borde-color-focus;
    color: $boton-texto-color-focus;
}

QPushButton:pressed {
    background-color: $boton-fondo-color-pressed;
    border: $boton-borde-seleccionado-size solid $boton-borde-color-pressed;
    color: $boton-texto-color-pressed;
}

QPushButton:disabled {
    background-color: $boton-fondo-color-disabled;
    border: $boton-borde-size solid $boton-borde-color-disabled;
    color: $boton-texto-color-disabled;
}

QPushButton::menu-indicator {
    image: none;
    width: 0px;
}

/* BOTÓN ACEPTAR */

QPushButton#BTN_ACE {
    background-color: $boton-cta-fondo-color-defecto;
    border: $boton-borde-size solid $boton-cta-borde-color-defecto;
    color: $boton-cta-texto-color-defecto;
}

QPushButton:hover#BTN_ACE {
    background-color: $boton-cta-fondo-color-hover;
    border: 0;
    color: $boton-cta-texto-color-hover;
}

QPushButton:focus#BTN_ACE {
    background-color: $boton-cta-fondo-color-focus;
    border: $boton-borde-seleccionado-size solid $boton-cta-borde-color-focus;
    color: $boton-cta-texto-color-focus;
}

QPushButton:pressed#BTN_ACE {
    background-color: $boton-cta-fondo-color-pressed;
    border: $boton-borde-size solid $boton-cta-borde-color-pressed;
    color: $boton-cta-texto-color-pressed;
}

QPushButton:disabled#BTN_ACE {
    background-color: $boton-cta-fondo-color-disabled;
    border: $boton-borde-size solid $boton-cta-borde-color-disabled;
    color: $boton-cta-texto-color-disabled;
}

/* BOTÓN AMPLIAR, BOTÓN BUSCAR, BOTÓN MENU y BOTÓN REDUCIR */

QPushButton#BTN_AMP,
QPushButton#BTN_AYU,
QPushButton#BTN_BUS,
QPushButton#BTN_MEN,
QPushButton#BTN_RED {
    border-radius: $boton-borde-radius;
    min-width: $boton-icono-size;
    qproperty-iconSize: $boton-icono-size;
}

/* BOTÓN OCULTO AVANZA CONTROL */

QPushButton#BTN_AVA_CTL {
    height: 0px;
    width: 0px;
}

/*--------------------------------------------------------------------------------
    QScrollBar - BARRA DE SCROLL VERTICAL Y HORIZONTAL
--------------------------------------------------------------------------------*/

/* SCROLL VERTICAL Y HORIZONTAL */

QScrollBar:vertical {
    background: $scrollbar-fondo-barra-color;
    border: $scrollbar-borde-size solid $scrollbar-fondo-barra-color;
    border-radius: 0px;
    padding-left: $scrollbar-padding;
    padding-right: $scrollbar-padding;
    margin: 0px 0px 0px 0px;
    width: $scrollbar-ancho;
}

QScrollBar:horizontal {
    background: $scrollbar-fondo-barra-color;
    border: $scrollbar-borde-size solid $scrollbar-fondo-barra-color;
    border-radius: 0px;
    padding-bottom: $scrollbar-padding;
    padding-top: $scrollbar-padding;
    margin: 0px 0px 0px 0px;
    height: $scrollbar-alto;
}

/* MANEJADOR DE LA BARRA DE SCROLL */

QScrollBar::handle:vertical {
    background: $scrollbar-color-normal;
    border: $scrollbar-borde-size solid $scrollbar-color-normal;
    border-radius: 0px;
    min-height: $scrollbar-handle-alto-minimo;
}

QScrollBar::handle:horizontal {
    background: $scrollbar-color-normal;
    border: $scrollbar-borde-size solid $scrollbar-color-normal;
    border-radius: 0px;
    min-width: $scrollbar-handle-ancho-minimo;
}

QScrollBar::hover {
    background: $scrollbar-fondo-barra-color;
    border: $scrollbar-borde-size solid $scrollbar-color-normal;
}

QScrollBar::handle:hover {
    background: $scrollbar-color-hover;
    border: $scrollbar-borde-size solid $scrollbar-color-hover;
}

/* BOTONES EN LOS EXTREMOS DE LA BARRA DE SCROLL */

QScrollBar::add-line:vertical {
    background: $scrollbar-fondo-barra-color;
    border: $scrollbar-borde-size solid $scrollbar-fondo-barra-color;
    height: $scrollbar-boton-vertical-size;
    subcontrol-origin: margin;
    subcontrol-position: bottom;
    width: $scrollbar-boton-vertical-size;
}

QScrollBar::add-line:horizontal {
    background: $scrollbar-fondo-barra-color;
    border: $scrollbar-borde-size solid $scrollbar-fondo-barra-color;
    height: $scrollbar-boton-horizontal-size;
    subcontrol-origin: margin;
    subcontrol-position: right;
    width: $scrollbar-boton-horizontal-size;
}

QScrollBar::sub-line:vertical {
    background: $scrollbar-fondo-barra-color;
    border: $scrollbar-borde-size solid $scrollbar-fondo-barra-color;
    height: $scrollbar-boton-vertical-size;
    subcontrol-position: top;
    subcontrol-origin: margin;
    width: $scrollbar-boton-vertical-size;
}

QScrollBar::sub-line:horizontal {
    background: $scrollbar-fondo-barra-color;
    border: $scrollbar-borde-size solid $scrollbar-fondo-barra-color;
    height: $scrollbar-boton-horizontal-size;
    subcontrol-origin: margin;
    subcontrol-position: left;
    width: $scrollbar-boton-horizontal-size;
}

/*--------------------------------------------------------------------------------
    QScrollBar - Recuadro de intersección entre las scrollbar horizontal y vertical
--------------------------------------------------------------------------------*/

QAbstractScrollArea::corner {
    background: transparent;
    border: transparent;
}

/*--------------------------------------------------------------------------------
    QSlider - DESLIZADOR
--------------------------------------------------------------------------------*/

QSlider {
    background: transparent;
}

/*--------------------------------------------------------------------------------
    QSplitter - SPLITTER
--------------------------------------------------------------------------------*/

QSplitter {
    background: $splitter-fondo-color;
}

/*--------------------------------------------------------------------------------
    QStatusBar - BARRA DE ESTADO
--------------------------------------------------------------------------------*/

QStatusBar {
    background-color: $statusbar-fondo-color;
    border-top: 1px solid $statusbar-borde-color;
    font-family: $statusbar-fuente-familia;
    font-size: $statusbar-fuente-size;
    qproperty-visible: false;
}

/*--------------------------------------------------------------------------------
    QTabWidget - SEPARADOR DE FORMULARIOS (Pestañas)
--------------------------------------------------------------------------------*/

QTabWidget::pane {
    border-top: 1px solid $tab-borde-color-linea;
    margin: 0px;
    top: 10px;
}

QTabWidget::tab-bar {
    left: 10px;
    min-width: 10px;
    right: 10px;
    top: 10px;
}

QTabBar {
    font-family: $tab-fuente-familia;
    font-size: $tab-fuente-size;
    font-weight: normal;
    text-transform: uppercase;
}

QTabBar::tab {
    background-color: $tab-fondo-color;
    color: $tab-texto-color;
    margin-left: 5px;
    margin-right: 10px;
    padding-bottom: 3px;
    padding-left: 5px;
    padding-right: 5px;
    padding-top: 5px;
}

QTabBar::tab:hover {
    border-bottom: $tab-borde-seleccionado-size solid $tab-borde-color-hover;
}

QTabBar::tab:selected {
    color: $tab-texto-color-seleccionado;
    border-bottom: $tab-borde-seleccionado-size solid $tab-borde-color-focus;
}

QTabBar::close-button {
    image: url($sysCacheClientPath/CRR.png);
    margin: 3px;
}

QTabBar::scroller {
    padding-top: 10px;
    width: 64px;
}

QTabBar QToolButton::left-arrow {
    image: url($sysCacheClientPath/IZQ.png);
    width: 32px;
}

QTabBar QToolButton::right-arrow {
    image: url($sysCacheClientPath/DER.png);
    width: 32px;
}

/*--------------------------------------------------------------------------------
    QTableView - REJILLA
    VCFootView - PIE DE REJILLA
--------------------------------------------------------------------------------*/

/* REJILLA */

QTableView {
    alternate-background-color: $rejilla-fila-fondo-color-alternativo;
    background-color: $rejilla-fila-fondo-color;
    border: none;
    border-bottom: $rejilla-borde-size solid $rejilla-borde-color;
    color: $rejilla-fila-texto-color;
    font-family: $rejilla-fuente-familia;
    font-size: $rejilla-fuente-size;
    font-weight: normal;
    gridline-color: $rejilla-grid-color;
    margin-right: $rejilla-margen;
    min-height: $rejilla-alto-minimo;
    /* Posible incidencia, parece que no funciona */
    padding-left: $rejilla-padding;
    /* Posible incidencia, parece que no funciona */
    padding-right: $rejilla-padding;
    /* Posible incidencia, parece que no funciona */
    qproperty-showGrid: $rejilla-grid;
    selection-background-color: $rejilla-fila-fondo-color-focus;
    selection-color: $rejilla-fila-texto-color-focus;
}

QTableView QTableCornerButton::section {
    background-color: $rejilla-esquinas-fondo-color;
    border: none;
    border-bottom: $rejilla-borde-size solid $rejilla-borde-color-linea;
}

/* PIE DE REJILLA */

VCFootView {
    background: $rejilla-pie-fondo-color;
    border: none;
    border-right: 5px solid transparent;
    color: $rejilla-pie-texto-color;
    font-family: $rejilla-pie-fuente-familia;
    font-size: $rejilla-pie-fuente-size;
    font-weight: bold;
    margin-right: $rejilla-pie-margen;
    min-height: $rejilla-pie-alto-minimo;
    padding-left: $rejilla-pie-padding;
    padding-right: $rejilla-pie-padding;
    min-height: $rejilla-pie-alto-minimo; }

VCFootView::section:active {
    background: $rejilla-pie-fondo-color;
    border: none;
    border-right: 5px solid transparent; }

VCFootView::section:!active {
    background: $rejilla-pie-fondo-color;
    border: none;
    border-right: 5px solid transparent; }

/*--------------------------------------------------------------------------------
    QTitanGrid - REJILLA AVANZADA
--------------------------------------------------------------------------------*/

/* Marco de la rejilla */
Qtitan--Grid {
    border-bottom: $rejilla-avanzada-borde-size solid $rejilla-avanzada-borde-color;
    color: $rejilla-avanzada-texto-color-detalle;
    font-family: $rejilla-avanzada-fuente-familia;
    font-size: $rejilla-avanzada-fuente-size;
    gridline-color: $rejilla-avanzada-grid-color;
    margin: $rejilla-avanzada-margen;
    padding: $rejilla-avanzada-padding;

    /* Color de fondo y fondo par de filas */
    background-color: $rejilla-avanzada-fondo-color;
    /* Fondo de la rejilla */
    alternate-background-color: $rejilla-avanzada-fondo-color-fila;
    /* Color de las líneas impares */

    /* Color de la fila seleccionada */
    /* Background del registro seleccionado */
    selection-background-color: $rejilla-avanzada-fondo-color-fila-sel;
    /* Color del texto del registro seleccionado */
    selection-color: $rejilla-avanzada-texto-color-fila-sel;

    /* Área de los agrupamientos */
    qproperty-headerTextColor: $rejilla-avanzada-texto-color-agrupamiento;
    /* Texto informativo de la cabecera de agrupamiento */
    qproperty-headerBrush: $rejilla-avanzada-info-agrupamiento-color;
    /* Color de la franja de agrupamiento */
    qproperty-headerBorderColor: $rejilla-avanzada-banda-agrupamiento-color;
    /* Color del borde */
    qproperty-headerBorderSize: $rejilla-avanzada-borde-size;
    /* Tamaño del borde */
    qproperty-headerTextFontSize: $rejilla-avanzada-cabecera-fuente-size;
    /* Tamaño de la fuente */

    /* Agrupamientos */
    /* Color de fondo según el nivel de agrupamiento */
    qproperty-groupColorLevel0: $rejilla-avanzada-fondo-color-agrupamiento-0;
    qproperty-groupColorLevel1: $rejilla-avanzada-fondo-color-agrupamiento-1;
    qproperty-groupColorLevel2: $rejilla-avanzada-fondo-color-agrupamiento-2;
    qproperty-groupColorLevel3: $rejilla-avanzada-fondo-color-agrupamiento-3;
    qproperty-groupColorLevel4: $rejilla-avanzada-fondo-color-agrupamiento-4;
    qproperty-groupColorLevel5: $rejilla-avanzada-fondo-color-agrupamiento-5;
    qproperty-groupColorLevel6: $rejilla-avanzada-fondo-color-agrupamiento-6;
    qproperty-groupColorLevel7: $rejilla-avanzada-fondo-color-agrupamiento-7;

    /* Nivel de despliegue de agrupamientos */
    qproperty-keepGroupLevelExpanded: 3;

    /* Pies de grupo */
    qproperty-summaryGroupItemTextColor: $rejilla-avanzada-texto-color-pie;
    /* No está operativo en la versión 26 */
    qproperty-summaryGroupItemBackgroundColor: $rejilla-avanzada-fondo-color-item-grupo;
    qproperty-summaryGroupItemHeight: $rejilla-avanzada-alto-item-grupo;
    qproperty-summaryGroupItemBorderColor: $rejilla-avanzada-borde-color-item-grupo;
    qproperty-summaryGroupItemBorderSize: $rejilla-avanzada-borde-size-item-grupo;

    /* Pies de rejilla */
    qproperty-summaryItemTextColor: $rejilla-avanzada-texto-color-pie;
    /* No está operativo en la versión 26 */
    qproperty-summaryItemBackgroundColor: $rejilla-avanzada-fondo-color-item-pie;
    qproperty-summaryItemHeight: $rejilla-avanzada-alto-item-pie;
    qproperty-summaryItemBorderColor: $rejilla-avanzada-borde-color-item-pie;
    qproperty-summaryItemBorderSize: $rejilla-avanzada-borde-size-item-pie;

    /* Línea de columnas inmovilizadas */
    qproperty-fixedSeparatorWidth: 3;
    qproperty-fixedSeparatorColor: $rejilla-avanzada-borde-color-inmovilizadas;
}

/* Cabecera y banda */
Qtitan--Grid::section {
    color: $rejilla-avanzada-texto-color-cabecera;
    background-color: $rejilla-avanzada-fondo-color-seccion;
    border-bottom: $rejilla-avanzada-borde-size solid $rejilla-avanzada-borde-color-seccion;
}

/* Cabecera con foco */
Qtitan--Grid::section:focus {
    background-color: $rejilla-avanzada-fondo-color-seccion-focus;
}

/* Celdas */
Qtitan--Grid::item {
    /* Se aplica sobre los posibles colores asignados genéricamente en la rejilla al fondo o fondo par */
    /*background-color: $rejilla-avanzada-fondo-color-item;*/
    border-bottom: $rejilla-avanzada-borde-size solid $rejilla-avanzada-borde-color-item-disabled;
    color: $rejilla-avanzada-texto-color-detalle;
}

/* Celda con foco */
Qtitan--Grid::item:focus {
    /* Se aplica sobre los posibles colores asignados genéricamente en la rejilla al fondo o fondo par */
    background-color: $rejilla-avanzada-fondo-color-fila-focus;
    border: $rejilla-avanzada-borde-size solid $rejilla-avanzada-borde-color-item-disabled;
    color: $rejilla-avanzada-texto-color-fila-focus;
}

/* Marco del foco y cabecera seleccionada de agrupación */
Qtitan--Grid:focus {
    outline: $rejilla-avanzada-borde-size dotted $rejilla-avanzada-borde-color-agrupacion-sel;
}

/* Filtro rápido */
Qtitan--Grid>Qtitan--GridColumnFilterPopup,
Qtitan--Grid>Qtitan--GridColumnFilterPopup * {
    background-color: $rejilla-avanzada-borde-color-filtro-popup;
    color: $rejilla-avanzada-texto-color-cabecera;
}

/* Selección de campos */
Qtitan--Grid>Qtitan--GridColumnsQuickCustomization,
Qtitan--Grid>Qtitan--GridColumnsQuickCustomization * {
    background-color: $rejilla-avanzada-borde-color-sel-campos;
    color: $rejilla-avanzada-texto-color-cabecera;
}

/*--------------------------------------------------------------------------------
    QToolBar - BARRA DE HERRAMIENTAS
--------------------------------------------------------------------------------*/

QToolBar {
    background-color: $toolbar-fondo-color;
    border: 0px;
    padding: 0px;
    spacing: 10px;
    qproperty-iconSize: $toolbar-icono-size;
}

QToolButton {
    background-color: $toolbar-boton-fondo-color;
    border: $toolbar-boton-borde-size solid $toolbar-boton-borde-color;
    border-radius: $boton-borde-radius;
    color: $toolbar-texto-color;
    font-family: $toolbar-fuente-familia;
    font-size: $toolbar-fuente-size;
    margin: $toolbar-margen;
    min-height: $toolbar-boton-size;
    min-width: $toolbar-boton-size;
    padding: 0px;
    qproperty-iconSize: $toolbar-icono-size;
}

QToolButton:hover {
    background-color: $toolbar-boton-fondo-color-hover;
    border: $toolbar-boton-borde-size solid $toolbar-boton-borde-color-hover;
}

QToolButton:focus {
    background-color: $toolbar-boton-fondo-color-focus;
    border: $toolbar-boton-borde-seleccionado-size solid $toolbar-boton-borde-color-focus;
}

QToolButton:pressed {
    background-color: $toolbar-boton-fondo-color-pressed;
    border: $toolbar-boton-borde-seleccionado-size solid $toolbar-boton-borde-color-pressed;
}

QToolButton:disabled {
    background-color: $toolbar-boton-fondo-color-disabled;
    border: $toolbar-boton-borde-size solid $toolbar-boton-borde-color-disabled;
    color: $toolbar-texto-color-disabled;
}

QToolButton::menu-indicator {
    background-color: transparent;
    color: transparent;
}

QToolBar QToolButton {
    background-color: $toolbar-boton-fondo-color;
    border: $toolbar-boton-borde-size solid $toolbar-boton-borde-color;
    border-radius: $boton-borde-radius;
    color: $toolbar-texto-color;
    margin: $toolbar-margen;
    min-height: 25;
    min-width: 25;
    padding: 0px;
    qproperty-iconSize: $toolbar-icono-size;
}

QToolBar QToolButton:hover {
    background-color: $toolbar-boton-fondo-color-hover;
    border: $toolbar-borde-size solid $toolbar-boton-borde-color-hover;
}

QToolBar QToolButton:focus {
    background-color: $toolbar-boton-fondo-color-focus;
    border: $toolbar-boton-borde-seleccionado-size solid $toolbar-boton-borde-color-focus;
}

QToolBar QToolButton:pressed {
    background-color: $toolbar-boton-fondo-color-pressed;
    border: $toolbar-boton-borde-seleccionado-size solid $toolbar-boton-borde-color-pressed;
}

/*--------------------------------------------------------------------------------
    QTooltip - TOOLTIP
--------------------------------------------------------------------------------*/

QToolTip {
    align: center;
    background-color: $tooltip-fondo-color;
    border: $tooltip-borde-size solid $tooltip-borde-color;
    color: $tooltip-texto-color;
    font-family: $tooltip-fuente-familia;
    font-size: $tooltip-fuente-size;
    vertical-align: center;
}

/*--------------------------------------------------------------------------------
    QTreeView - ARBOL VISOR Y MENU ARBOLADO
--------------------------------------------------------------------------------*/

QTreeView {
    outline: 0;
    background-color: $treeview-fondo-color;
    font-family: $treeview-fuente-familia;
    font-size: $treeview-fuente-size;
    selection-background-color: transparent;
    selection-color: $treeview-texto-seleccionado-color;
    show-decoration-selected: 1;
}

QTreeView::item {
    border-right: 0;
    color: $treeview-texto-color;
    margin-bottom: 2px;
    margin-right: 10px;
    margin-top: 1px;
    padding-bottom: 2px;
    padding-top: 1px;
}

QTreeView::item:hover {
    border-color: transparent;
    color: $treeview-texto-color-hover;
}

QTreeView::item:selected {
    color: $treeview-texto-seleccionado-color;
}

QTreeView::branch:closed:has-children:!has-siblings,
QTreeView::branch:closed:has-children:has-siblings {
    image: url($sysCacheClientPath/DER.png);
}

QTreeView::branch:open:has-children:!has-siblings,
QTreeView::branch:open:has-children:has-siblings {
    image: url($sysCacheClientPath/ABA.png);
}

/*--------------------------------------------------------------------------------
    QCalendarWidget - CALENDARIO
    QWidget#qt_calendar - CALENDARIO
--------------------------------------------------------------------------------*/

QWidget#qt_calendar_navigationbar {
    background-color: $calendario-fondo-color-barra-navegacion;
    qproperty-gridVisible: false;
}

QWidget#qt_calendar_nextmonth {
    background: $calendario-fondo-color-mes-siguiente;
    border: $calendario-borde-size solid $calendario-borde-color-mes-siguiente;
    qproperty-icon: url($sysCacheClientPath/DER.png);
}

QWidget#qt_calendar_prevmonth {
    background: $calendario-fondo-color-mes-anterior;
    border: $calendario-borde-size solid $calendario-borde-color-mes-anterior;
    qproperty-icon: url($sysCacheClientPath/IZQ.png);
}

QWidget#qt_calendar_monthbutton,
QWidget#qt_calendar_yearbutton {
    background: $calendario-fondo-color-boton-ano-mes;
    border: $calendario-borde-size solid $calendario-borde-color-boton-ano-mes;
    color: $calendario-texto-color-boton-ano-mes;
}

QCalendarWidget {
    min-width: 300px;
    min-height: 260px;
}

QCalendarWidget QSpinBox {
    width: 35px;
    font-family: $calendario-spinbox-fuente-familia;
    font-size: $calendario-spinbox-fuente-size;
    color: $calendario-texto-color-spinbox;
    background-color: $calendario-fondo-color-spinbox;
}

QCalendarWidget QSpinBox::up-button {
    subcontrol-origin: border;
    subcontrol-position: top right;
    width: 30px;
}

QCalendarWidget QSpinBox::down-button {
    subcontrol-origin: border;
    subcontrol-position: bottom right;
    width: 30px;
}

