/*--------------------------------------------------------------------------------
    QComboBox - COMBOBOX
--------------------------------------------------------------------------------*/

QComboBox {
    background-color: $combobox-fondo-color;
    border: $combobox-borde-size solid $combobox-borde-color;
    border-radius: $combobox-borde-radius;
    color: $combobox-texto-color;
    font-family: $combobox-fuente-familia;
    font-size: $combobox-fuente-size;
    height: $control-edicion-alto;
    padding-left: $combobox-padding-izquierdo;
    padding-right: $combobox-padding-derecho;
    selection-background-color: $combobox-fondo-color-seleccionado;
    selection-color: $combobox-texto-color-seleccionado;
    width: $control-edicion-ancho;
}

QComboBox:hover {
    border: $combobox-borde-size solid $combobox-borde-color-hover;
}

QComboBox:focus {
    border: $combobox-borde-seleccionado-size solid $combobox-borde-color-focus;
}

QComboBox:disabled {
    background-color: $combobox-fondo-color-disabled;
    color: $combobox-texto-color-disabled;
}

QComboBox::drop-down {
    border: none;
    image: url($sysCacheClientPath/ABA.png);
    width: $combobox-icono-size;
}

QComboBox QAbstractItemView {
    border:$combobox-borde-size solid $combobox-borde-color;
}

/*--------------------------------------------------------------------------------
    QDockWidget - DOCK
--------------------------------------------------------------------------------*/

QDockWidget {
    color: transparent;
    font-family: $dock-fuente-familia;
    font-size: $dock-fuente-size;
    min-width: 10px;
    width: 210px;
}

QDockWidget::title {
    background: $dock-fondo-color;
    color: $dock-texto-color;
    margin: 0px;
    padding: 0px;
    text-align: center;
}

QDockWidget::close-button {
    image: none;
    position: absolute;
    subcontrol-origin: margin;
    subcontrol-position: top right;
    top: 0px;
    left: 0px;
    bottom: 0px;
    width: 0px;
}

QDockWidget::float-button {
    image: none;
    position: absolute;
    subcontrol-origin: margin;
    subcontrol-position: top right;
    top: 0px;
    left: 0px;
    bottom: 0px;
    width: 0px;
}

/*--------------------------------------------------------------------------------
    QFrame - MARCO
--------------------------------------------------------------------------------*/

QFrame {
    background: $frame-fondo-color;
    border: 0px;
}

/*--------------------------------------------------------------------------------
    QGroupBox - CAJA DE GRUPO
--------------------------------------------------------------------------------*/

QGroupBox {
    border: $caja-grupo-borde-size solid $caja-grupo-borde-color;
    border-radius: 5px;
    font-family: $caja-grupo-fuente-familia;
    font-size: $caja-grupo-fuente-size;
    font-weight: bold;
    margin-top: 20px;
    padding: 5px;
}

QGroupBox::title {
    color: $caja-grupo-texto-color;
    padding-top: -5px;
    padding-left: 5px;
    subcontrol-origin: margin;
}

/*--------------------------------------------------------------------------------
    QHeaderView - CABECERA DE REJILLAS Y ARBOLES
--------------------------------------------------------------------------------*/

QHeaderView::section {
    background: $rejilla-cabecera-fondo-color;
    border: none;
    border-bottom: $rejilla-cabecera-borde-size solid $rejilla-borde-color-linea-cabecera;
    color: $rejilla-cabecera-texto-color;
    font-family: $rejilla-cabecera-fuente-familia;
    font-size: $rejilla-cabecera-fuente-size;
    font-weight: bold;
    /* margin-right: $rejilla-cabecera-margen; */
    /* Posible incidencia en detalle con márgenes */
    min-height: $rejilla-cabecera-alto;
    padding-left: 2px;
    /* Posible incidencia en detalle que siempre aplica 2px a izquierda y derecha del texto */
    padding-right: 2px;
}

/*QHeaderView::section:vertical:checked {
    image: url($sysCacheClientPath/ABA.png);
}*/

/*QHeaderView[toggleVertical=true]::section:vertical:checked {
    image: url($sysCacheClientPath/ABA.png);
}*/

QHeaderView::down-arrow {
    image: url($sysCacheClientPath/ABA.png);
}

QHeaderView::up-arrow {
    image: url($sysCacheClientPath/ARR.png);
}

QHeaderView::section:horizontal:uncheked {
    padding-right: 2px;
}

QHeaderView::section:horizontal:checked {
    padding-right: 20px;
}

/* Posible incidencia en detalle que siempre aplica 2px a izquierda y derecha del texto */

/*--------------------------------------------------------------------------------
    QLabel - ETIQUETA DE TEXTO
--------------------------------------------------------------------------------*/

QLabel {
    background-color: $label-fondo-color;
    color: $label-texto-color;
    font-family: $label-fuente-familia;
    font-size: $label-fuente-size;
}

/* TITULOS DE LOS FORMULARIOS Y MENUS */

QLabel#TXT_TIT {
    color: $label-texto-color;
    font-size: $label-fuente-titulo-size;
}

QLabel#EMP {
    color: $label-texto-color;
    font-size: $label-fuente-size;
    text-transform: uppercase;
}

QLabel#USR {
    color: $label-texto-color;
    font-size: $label-fuente-size;
}

/*--------------------------------------------------------------------------------
    QLineEdit - CAJA DE EDICIÓN DE TEXTO EN UNA LINEA
--------------------------------------------------------------------------------*/

QLineEdit {
    background-color: $qlineedit-fondo-color;
    border: $qlineedit-borde-size solid $qlineedit-borde-color;
    border-radius: $qlineedit-borde-radius;
    color: $qlineedit-texto-color;
    font-family: $qlineedit-fuente-familia;
    font-size: $qlineedit-fuente-size;
    height: $control-edicion-alto;
    padding-left: $qlineedit-padding-izquierdo;
    selection-background-color: $qlineedit-fondo-color-seleccionado;
    selection-color: $qlineedit-texto-color-seleccionado;
}

QLineEdit:hover {
    border: $qlineedit-borde-size solid $qlineedit-borde-color-hover;
}

QLineEdit:focus {
    border: $qlineedit-borde-seleccionado-size solid $qlineedit-borde-color-focus;
    padding-left: $qlineedit-padding-izquierdo-resaltado;
}

QLineEdit:disabled {
    background-color: $qlineedit-fondo-color-disabled;
    color: $qlineedit-texto-color-disabled;
}

/* CONTROL DE EDICIÓN DE TEXTOS A BUSCAR EN MENUS */

QLineEdit#TXT_BUS {
    qproperty-placeholderText: 'Texto a buscar';
}

/* FORMULARIO ALTA CONTACTO > TEXTO EDIT NOMBRE Y NOMBRE COMERCIAL */

QLineEdit#NAME_CON {
    qproperty-placeholderText: 'Nombre contacto';
    qproperty-text: 'Nombre contacto';
}

/*--------------------------------------------------------------------------------
    QTextEdit - CAJA DE EDICIÓN DE TEXTO MULTI-LINEA
--------------------------------------------------------------------------------*/

QTextEdit {
    background-color: $qtextedit-fondo-color;
    border: $qtextedit-borde-size solid $qtextedit-borde-color;
    border-radius: $qtextedit-borde-radius;
    color: $qtextedit-texto-color;
    font-family: $qtextedit-fuente-familia;
    font-size: $qtextedit-fuente-size;
    padding: $qtextedit-padding;
    selection-background-color: $qtextedit-fondo-color-seleccionado;
    selection-color: $qtextedit-texto-color-seleccionado;
}

QTextEdit:hover {
    border: $qtextedit-borde-size solid $qtextedit-borde-color-hover;
}

QTextEdit:focus {
    border: $qtextedit-borde-seleccionado-size solid $qtextedit-borde-color-focus;
}

QTextEdit:disabled {
    background-color: $qtextedit-fondo-color-disabled;
    color: $qtextedit-texto-color-disabled;
}

/*--------------------------------------------------------------------------------
    QMainWindow - VENTANA PRINCIPAL
--------------------------------------------------------------------------------*/

QMainWindow {
    background: $ventana-fondo-color;
    border: 0px;
    font-family: $ventana-fuente-familia;
    font-size: $ventana-fuente-size;
}

QMainWindow::separator {
    background: $ventana-separador-color;
    border-left: 0px solid $ventana-borde-color;
    border-right: $ventana-borde-size solid $ventana-borde-color;
    height: 5px;
    width: 5px;
}

QMainWindow::separator:hover {
    background: $ventana-separador-fondo-color;
}

/*--------------------------------------------------------------------------------
    QMenu - MENU CONTEXTUAL
--------------------------------------------------------------------------------*/

QMenu {
    background-color: $menu-fondo-color;
    border: $menu-borde-size solid $menu-borde-color;
    color: $menu-texto-color;
    font-family: $menu-fuente-familia;
    font-size: $menu-fuente-size;
    padding: $menu-padding;
}

QMenu::icon {
    background: transparent;
    qproperty-iconSize: $menu-size-icono;
}

QMenu::item {
    border: $menu-borde-size solid $menu-fondo-color-item;
    height: $menu-item-alto;
    padding-left: $menu-padding;
    padding-right: $menu-item-padding;
}

QMenu::item:disabled {
    color: $menu-texto-color-disabled;
}

QMenu::item:hover {
    color: $menu-texto-color-hover;
}

QMenu::item:selected {
    background-color: $menu-fondo-color-item;
    color: $menu-texto-color-seleccionado;
}

QMenu::separator {
    background-color: $menu-separador-color;
    height: $menu-separador-alto;
    margin-top: $menu-margen;
    margin-bottom: $menu-margen;
}

QMenu::indicator {
    height: $menu-icono-size;
    width: $menu-icono-size;
}

QMenu::right-arrow {
    height: $menu-size-icono-flecha;
    image: url($sysCacheClientPath/SIG.png);
    width: $menu-size-icono-flecha;
}

QMenu::left-arrow {
    height: $menu-size-icono-flecha;
    image: url($sysCacheClientPath/ANT.png);
    width: $menu-size-icono-flecha;
}

/*--------------------------------------------------------------------------------
    QMenuBar - BARRA DE MENU (Solo afecta a Windows)
--------------------------------------------------------------------------------*/

QMenuBar {
    background-color: #3F3F3F;
    border-bottom: 1px solid #808080;
    min-height: 20px;
    color: #DFDFDF;
}

QMenuBar::item:selected {
    background-color: #D8D8D8;
    color: #3F3F3F;
}

QMenuBar::item:hover {
    background: #D8D8D8;
    color: #3F3F3F;
}

/*--------------------------------------------------------------------------------
    QMessageBox - VENTANA DE MENSAJE
    QDialog - VENTANA EN CUADRO DE DIÁLOGO
--------------------------------------------------------------------------------*/

QMessageBox,
QDialog {
    background-color: $dialogo-fondo-color;
}

QMessageBox QPushButton {
    min-width: 90px;
}

/*--------------------------------------------------------------------------------
    QProgressBar - BARRA DE PROGRESO
--------------------------------------------------------------------------------*/

QProgressBar {
    background-color: $progressbar-fondo-color;
    border: $progressbar-borde-size solid $progressbar-borde-color;
    border-radius: 2px;
    color: $progressbar-texto-color;
    font-family: $progressbar-fuente-familia;
    font-size: $progressbar-fuente-size;
    height: 30px;
    text-align: center;
}

QProgressBar::chunk {
    background-color: #C5CAE9;
}

/*--------------------------------------------------------------------------------
    QPushButton - BOTÓN
--------------------------------------------------------------------------------*/

QPushButton {
    background-color: $boton-fondo-color-defecto;
    border: $boton-borde-size solid $boton-borde-color-defecto;
    border-radius: $boton-borde-radius;
    color: $boton-texto-color-defecto;
    font-family: $boton-fuente-familia;
    font-size: $boton-fuente-size;
    font-weight: $boton-fuente-peso;
    height: $boton-alto;
    outline: 0;
    text-align: $boton-texto-alineacion;
    text-transform: $boton-texto-transformacion;
    width: $boton-ancho;
    qproperty-iconSize: $boton-icono-size;
}

QPushButton:hover {
    background-color: $boton-fondo-color-hover;
    border: $boton-borde-size solid $boton-borde-color-hover;
    color: $boton-texto-color-hover;
}

QPushButton:focus {
    background-color: $boton-fondo-color-focus;
    border: $boton-borde-seleccionado-size solid $boton-borde-color-focus;
    color: $boton-texto-color-focus;
}

QPushButton:pressed {
    background-color: $boton-fondo-color-pressed;
    border: $boton-borde-seleccionado-size solid $boton-borde-color-pressed;
    color: $boton-texto-color-pressed;
}

QPushButton:disabled {
    background-color: $boton-fondo-color-disabled;
    border: $boton-borde-size solid $boton-borde-color-disabled;
    color: $boton-texto-color-disabled;
}

QPushButton::menu-indicator {
    image: none;
    width: 0px;
}

/* BOTÓN ACEPTAR */

QPushButton#BTN_ACE {
    background-color: $boton-cta-fondo-color-defecto;
    border: $boton-borde-size solid $boton-cta-borde-color-defecto;
    color: $boton-cta-texto-color-defecto;
}

QPushButton:hover#BTN_ACE {
    background-color: $boton-cta-fondo-color-hover;
    border: 0;
    color: $boton-cta-texto-color-hover;
}

QPushButton:focus#BTN_ACE {
    background-color: $boton-cta-fondo-color-focus;
    border: $boton-borde-seleccionado-size solid $boton-cta-borde-color-focus;
    color: $boton-cta-texto-color-focus;
}

QPushButton:pressed#BTN_ACE {
    background-color: $boton-cta-fondo-color-pressed;
    border: $boton-borde-size solid $boton-cta-borde-color-pressed;
    color: $boton-cta-texto-color-pressed;
}

QPushButton:disabled#BTN_ACE {
    background-color: $boton-cta-fondo-color-disabled;
    border: $boton-borde-size solid $boton-cta-borde-color-disabled;
    color: $boton-cta-texto-color-disabled;
}

/* BOTÓN AMPLIAR, BOTÓN BUSCAR, BOTÓN MENU y BOTÓN REDUCIR */

QPushButton#BTN_AMP,
QPushButton#BTN_AYU,
QPushButton#BTN_BUS,
QPushButton#BTN_MEN,
QPushButton#BTN_RED {
    border-radius: $borde-radius;
    min-width: $boton-icono-size;
    qproperty-iconSize: $boton-icono-size;
}

/* BOTÓN OCULTO AVANZA CONTROL */

QPushButton#BTN_AVA_CTL {
    height: 0px;
    width: 0px;
}

/*--------------------------------------------------------------------------------
    QScrollBar - BARRA DE SCROLL VERTICAL Y HORIZONTAL
--------------------------------------------------------------------------------*/

/* SCROLL VERTICAL Y HORIZONTAL */

QScrollBar:vertical {
    background: $scrollbar-fondo-barra-color;
    border: $scrollbar-borde-size solid $scrollbar-fondo-barra-color;
    border-radius: 0px;
    padding-left: $scrollbar-padding;
    padding-right: $scrollbar-padding;
    margin: 0px 0px 0px 0px;
    width: $scrollbar-ancho;
}

QScrollBar:horizontal {
    background: $scrollbar-fondo-barra-color;
    border: $scrollbar-borde-size solid $scrollbar-fondo-barra-color;
    border-radius: 0px;
    padding-bottom: $scrollbar-padding;
    padding-top: $scrollbar-padding;
    margin: 0px 0px 0px 0px;
    height: $scrollbar-alto;
}

/* MANEJADOR DE LA BARRA DE SCROLL */

QScrollBar::handle:vertical {
    background: $scrollbar-color-normal;
    border: $scrollbar-borde-size solid $scrollbar-color-normal;
    border-radius: 0px;
    min-height: $scrollbar-handle-alto-minimo;
}

QScrollBar::handle:horizontal {
    background: $scrollbar-color-normal;
    border: $scrollbar-borde-size solid $scrollbar-color-normal;
    border-radius: 0px;
    min-width: $scrollbar-handle-ancho-minimo;
}

QScrollBar::hover {
    background: $scrollbar-fondo-barra-color;
    border: $scrollbar-borde-size solid $scrollbar-color-normal;
}

QScrollBar::handle:hover {
    background: $scrollbar-color-hover;
    border: $scrollbar-borde-size solid $scrollbar-color-hover;
}

/* BOTONES EN LOS EXTREMOS DE LA BARRA DE SCROLL */

QScrollBar::add-line:vertical {
    background: $scrollbar-fondo-barra-color;
    border: $scrollbar-borde-size solid $scrollbar-fondo-barra-color;
    height: $scrollbar-boton-vertical-size;
    subcontrol-origin: margin;
    subcontrol-position: bottom;
    width: $scrollbar-boton-vertical-size;
}

QScrollBar::add-line:horizontal {
    background: $scrollbar-fondo-barra-color;
    border: $scrollbar-borde-size solid $scrollbar-fondo-barra-color;
    height: $scrollbar-boton-horizontal-size;
    subcontrol-origin: margin;
    subcontrol-position: right;
    width: $scrollbar-boton-horizontal-size;
}

QScrollBar::sub-line:vertical {
    background: $scrollbar-fondo-barra-color;
    border: $scrollbar-borde-size solid $scrollbar-fondo-barra-color;
    height: $scrollbar-boton-vertical-size;
    subcontrol-position: top;
    subcontrol-origin: margin;
    width: $scrollbar-boton-vertical-size;
}

QScrollBar::sub-line:horizontal {
    background: $scrollbar-fondo-barra-color;
    border: $scrollbar-borde-size solid $scrollbar-fondo-barra-color;
    height: $scrollbar-boton-horizontal-size;
    subcontrol-origin: margin;
    subcontrol-position: left;
    width: $scrollbar-boton-horizontal-size;
}

/*--------------------------------------------------------------------------------
    QScrollBar - Recuadro de intersección entre las scrollbar horizontal y vertical
--------------------------------------------------------------------------------*/

QAbstractScrollArea::corner {
    background: transparent;
    border: transparent;
}

/*--------------------------------------------------------------------------------
    QSlider - DESLIZADOR
--------------------------------------------------------------------------------*/

QSlider {
    background: transparent;
}

/*--------------------------------------------------------------------------------
    QSplitter - SPLITTER
--------------------------------------------------------------------------------*/

QSplitter {
    background: $splitter-fondo-color;
}

/*--------------------------------------------------------------------------------
    QStatusBar - BARRA DE ESTADO
--------------------------------------------------------------------------------*/

QStatusBar {
