}

QTabBar QToolButton::right-arrow {
    image: url($sysCacheClientPath/DER.png);
    width: 32px;
}

/*--------------------------------------------------------------------------------
    QTableView - REJILLA
    VCFootView - PIE DE REJILLA
--------------------------------------------------------------------------------*/

/* REJILLA */

QTableView {
    alternate-background-color: $rejilla-fila-fondo-color-alternativo;
    background-color: $rejilla-fila-fondo-color;
    border: none;
    border-bottom: $rejilla-borde-size solid $rejilla-borde-color;
    color: $rejilla-fila-texto-color;
    font-family: $rejilla-fuente-familia;
    font-size: $rejilla-fuente-size;
    font-weight: normal;
    gridline-color: $rejilla-grid-color;
    margin-right: $rejilla-margen;
    min-height: $rejilla-alto-minimo;
    /* Posible incidencia, parece que no funciona */
    padding-left: $rejilla-padding;
    /* Posible incidencia, parece que no funciona */
    padding-right: $rejilla-padding;
    /* Posible incidencia, parece que no funciona */
    qproperty-showGrid: $rejilla-grid;
