}

/*--------------------------------------------------------------------------------
    QTooltip - TOOLTIP
--------------------------------------------------------------------------------*/

QToolTip {
    align: center;
    background-color: $tooltip-fondo-color;
    border: $tooltip-borde-size solid $tooltip-borde-color;
    color: $tooltip-texto-color;
    font-family: $tooltip-fuente-familia;
    font-size: $tooltip-fuente-size;
    vertical-align: center;
}

/*--------------------------------------------------------------------------------
