    color: #727272; }

QToolButton:pressed {
    border: 2px solid #3F51B5;
    border-radius: 5px; }

QToolButton::menu-indicator {
    background-color: transparent;
    color: transparent; }

/*  --------------------------------------------------------------------------------
    QTooltip - TOOLTIP
    -------------------------------------------------------------------------------- */

QToolTip {
    align: center;
    background-color: #FFFFFF;
    border: 1px solid #727272;
    color: #333333;
    vertical-align:center; }

/*	--------------------------------------------------------------------------------
    QTreeView - ARBOL VISOR Y MENU ARBOLADO
    -------------------------------------------------------------------------------- */

QTreeView {
    outline: 0;
    background-color: #FFF;
    selection-background-color: transparent;
    show-decoration-selected: 1; }

QTreeView::item {
    border-right: 0;
    color: #727272;
    margin-bottom:2px;
    margin-right:10px;
    margin-top: 1px;
    padding-bottom:2px;
    padding-top:1px; }

QTreeView::item:hover {
    border-color: transparent;
    color: #212121; }

QTreeView::item:selected {
    background-color: #3F51B5;
    color: #FFF; }

/* MEN_APP - MENU GENERAL ARBOLADO */

QTreeView::item:selected#MEN_APP {
    background-color: transparent;
    border-color: transparent;
    color: #212121; }

/*	--------------------------------------------------------------------------------
    QCalendarWidget - CALENDARIO
    QWidget#qt_calendar - CALENDARIO
    -------------------------------------------------------------------------------- */

QWidget#qt_calendar_navigationbar {
    background-color: #CECECE;
    qproperty-gridVisible: false; }

QWidget#qt_calendar_nextmonth {
    background: #CECECE;
    border: 1px solid #CECECE;
    qproperty-icon: url(SENDA_ICONOS_DER.png); }

QWidget#qt_calendar_prevmonth {
    background: #CECECE;
    border: 1px solid #CECECE;
    qproperty-icon: url(SENDA_ICONOS_IZQ.png); }

QWidget#qt_calendar_monthbutton, QWidget#qt_calendar_yearbutton {
    background: #CECECE;
    border: 1px solid #CECECE; }

QCalendarWidget  {
    min-width: 300px;
    min-height: 260px;}

QCalendarWidget QSpinBox {
    width: 35px;
    font-size:14px;
    color: #212121;
    background-color: #CECECE; }

QCalendarWidget QSpinBox::up-button {
    subcontrol-origin: border;  subcontrol-position: top right;  width:30px; }

QCalendarWidget QSpinBox::down-button {
    subcontrol-origin: border; subcontrol-position: bottom right;  width:30px; }

